import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import api from '../config/axios';

const Diagnostic = () => {
  const { user, isAuthenticated, loading } = useAuth();
  const [apiStatus, setApiStatus] = useState('En cours...');
  const [errors, setErrors] = useState([]);

  useEffect(() => {
    const runDiagnostics = async () => {
      const newErrors = [];
      
      try {
        // Test de l'API
        const response = await api.get('/api/products');
        setApiStatus('‚úÖ API fonctionne');
      } catch (error) {
        setApiStatus('‚ùå API ne fonctionne pas');
        newErrors.push(`API Error: ${error.message}`);
      }

      // Test des contextes
      if (loading) {
        newErrors.push('AuthContext est en cours de chargement');
      }

      if (!user && isAuthenticated) {
        newErrors.push('Utilisateur non d√©fini mais authentifi√©');
      }

      setErrors(newErrors);
    };

    runDiagnostics();
  }, [user, isAuthenticated, loading]);

  return (
    <div className="fixed top-4 right-4 bg-white p-4 border border-gray-300 rounded-lg shadow-lg z-50 max-w-sm">
      <h3 className="font-bold text-lg mb-2">üîç Diagnostic</h3>
      
      <div className="space-y-2 text-sm">
        <div>
          <strong>AuthContext:</strong>
          <div className="ml-2">
            <div>Loading: {loading ? '‚úÖ' : '‚ùå'}</div>
            <div>Authenticated: {isAuthenticated ? '‚úÖ' : '‚ùå'}</div>
            <div>User: {user ? `‚úÖ ${user.email}` : '‚ùå'}</div>
          </div>
        </div>
        
        <div>
          <strong>API Status:</strong> {apiStatus}
        </div>
        
        {errors.length > 0 && (
          <div>
            <strong>Erreurs:</strong>
            <ul className="ml-2">
              {errors.map((error, index) => (
                <li key={index} className="text-red-600">‚Ä¢ {error}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Diagnostic;
