import { useState } from "react";
import { removeBackground, isApiKeyConfigured } from "../config/removeBg";

export default function HoodieCustomizer() {
  const [color, setColor] = useState("#ff0000"); // couleur par défaut (rouge)
  const [logo, setLogo] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [logoPosition, setLogoPosition] = useState("chest"); // position par défaut
  const [logoSize, setLogoSize] = useState(80); // taille par défaut

  // Positions disponibles pour le logo
  const logoPositions = [
    { id: "chest", name: "Poitrine", icon: "👕" },
    { id: "back", name: "Dos", icon: "🔙" },
    { id: "sleeve", name: "Manche", icon: "👋" },
    { id: "hood", name: "Capuche", icon: "🎩" },
    { id: "pocket", name: "Poche", icon: "👝" }
  ];

  // Styles de positionnement pour chaque emplacement
  const getLogoStyle = (position) => {
    const baseStyle = {
      position: "absolute",
      width: `${logoSize}px`,
      transform: "translate(-50%, -50%)",
      pointerEvents: "none",
      filter: "drop-shadow(0 2px 4px rgba(0,0,0,0.3))",
    };

    // Tailles recommandées selon la position
    const getRecommendedSize = (pos) => {
      switch (pos) {
        case "sleeve": return 60;
        case "hood": return 70;
        case "pocket": return 60;
        default: return 80;
      }
    };

    const recommendedSize = getRecommendedSize(position);
    if (logoSize !== recommendedSize) {
      setLogoSize(recommendedSize);
    }

    switch (position) {
      case "chest":
        return { ...baseStyle, top: "40%", left: "50%" };
      case "back":
        return { ...baseStyle, top: "35%", left: "50%" };
      case "sleeve":
        return { ...baseStyle, top: "60%", left: "25%" };
      case "hood":
        return { ...baseStyle, top: "15%", left: "50%" };
      case "pocket":
        return { ...baseStyle, top: "50%", left: "50%" };
      default:
        return { ...baseStyle, top: "40%", left: "50%" };
    }
  };

  // Upload du logo avec suppression d'arrière-plan
  const handleLogoUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setIsProcessing(true);
      
      try {
        if (isApiKeyConfigured()) {
          // Avec suppression d'arrière-plan
          const logoUrl = await removeBackground(file);
          setLogo(logoUrl);
        } else {
          // Sans suppression d'arrière-plan (fallback)
          const reader = new FileReader();
          reader.onload = (event) => {
            setLogo(event.target.result);
          };
          reader.readAsDataURL(file);
        }
      } catch (error) {
        console.error('Erreur lors du traitement du logo:', error);
        // Fallback: utiliser l'image originale
        const reader = new FileReader();
        reader.onload = (event) => {
          setLogo(event.target.result);
        };
        reader.readAsDataURL(file);
      } finally {
        setIsProcessing(false);
      }
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Personnalise ton hoodie</h2>

      <div style={{ position: "relative", display: "inline-block" }}>
        {/* Hoodie de base */}
        <img
          src="/hoodie-blanc.jpg"
          alt="Hoodie"
          style={{ maxWidth: "400px", display: "block" }}
        />

        {/* Calque couleur */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: color,
            mixBlendMode: color === "#000000" ? "overlay" : "multiply",
            opacity: color === "#000000" ? 0.8 : 1, // ajuste pour le noir
            pointerEvents: "none",
          }}
        ></div>

        {/* Logo */}
        {logo && (
          <img
            src={logo}
            alt="Logo"
            style={getLogoStyle(logoPosition)}
          />
        )}

        {/* Indicateur de position (quand pas de logo) */}
        {!logo && (
          <div
            style={{
              ...getLogoStyle(logoPosition),
              border: "2px dashed #007bff",
              borderRadius: "50%",
              background: "rgba(0, 123, 255, 0.1)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: "20px",
              color: "#007bff",
            }}
          >
            📍
          </div>
        )}

        {/* Indicateur de traitement */}
        {isProcessing && (
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              background: "rgba(0,0,0,0.8)",
              color: "white",
              padding: "10px 20px",
              borderRadius: "5px",
              fontSize: "14px",
            }}
          >
            🔄 Traitement du logo...
          </div>
        )}
      </div>

      {/* Contrôles */}
      <div style={{ marginTop: "20px" }}>
        <label>
          Choisir couleur :
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            style={{ marginLeft: "10px" }}
          />
        </label>

        <br /><br />

        <label>
          Ajouter logo :
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            style={{ marginLeft: "10px" }}
            disabled={isProcessing}
          />
        </label>

        {logo && (
          <div style={{ marginTop: "15px" }}>
            <label style={{ display: "block", marginBottom: "10px", fontWeight: "bold" }}>
              📍 Position du logo :
            </label>
            <div style={{ 
              display: "flex", 
              flexWrap: "wrap", 
              gap: "8px", 
              justifyContent: "center",
              maxWidth: "400px",
              margin: "0 auto"
            }}>
              {logoPositions.map((pos) => (
                <button
                  key={pos.id}
                  onClick={() => setLogoPosition(pos.id)}
                  style={{
                    padding: "8px 12px",
                    border: logoPosition === pos.id ? "2px solid #007bff" : "1px solid #ddd",
                    borderRadius: "20px",
                    background: logoPosition === pos.id ? "#e3f2fd" : "white",
                    cursor: "pointer",
                    fontSize: "12px",
                    display: "flex",
                    alignItems: "center",
                    gap: "5px",
                    transition: "all 0.2s ease",
                  }}
                >
                  <span>{pos.icon}</span>
                  <span>{pos.name}</span>
                </button>
              ))}
            </div>
          </div>
        )}

        {logo && (
          <div style={{ marginTop: "15px" }}>
            <label style={{ display: "block", marginBottom: "10px", fontWeight: "bold" }}>
              📏 Taille du logo :
            </label>
            <div style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: "10px" }}>
              <span style={{ fontSize: "12px" }}>Petit</span>
              <input
                type="range"
                min="40"
                max="120"
                value={logoSize}
                onChange={(e) => setLogoSize(parseInt(e.target.value))}
                style={{ width: "150px" }}
              />
              <span style={{ fontSize: "12px" }}>Grand</span>
              <span style={{ 
                fontSize: "12px", 
                background: "#f0f0f0", 
                padding: "2px 8px", 
                borderRadius: "10px",
                minWidth: "40px",
                textAlign: "center"
              }}>
                {logoSize}px
              </span>
            </div>
          </div>
        )}
        
        {!isApiKeyConfigured() && (
          <div style={{ 
            marginTop: "10px", 
            padding: "10px", 
            background: "#fff3cd", 
            border: "1px solid #ffeaa7",
            borderRadius: "5px",
            fontSize: "12px",
            color: "#856404"
          }}>
            ⚠️ <strong>Conseil :</strong> Pour supprimer automatiquement l'arrière-plan, 
            utilisez des logos PNG transparents ou configurez l'API Remove.bg.
          </div>
        )}
        
        {logo && (
          <div style={{ marginTop: "10px" }}>
            <button
              onClick={() => setLogo(null)}
              style={{
                background: "#dc3545",
                color: "white",
                border: "none",
                padding: "5px 10px",
                borderRadius: "3px",
                cursor: "pointer",
                fontSize: "12px",
              }}
            >
              Supprimer le logo
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
