import React, { useState, useRef } from 'react';
import { FaTimes } from 'react-icons/fa';
import '../styles/SimpleHoodieDesigner.css';

const SimpleHoodieDesigner = ({ onClose }) => {
  const [selectedColor, setSelectedColor] = useState('#ffffff');
  const [uploadedLogo, setUploadedLogo] = useState(null);
  const [logoSize, setLogoSize] = useState(100);
  const fileInputRef = useRef(null);

  const handleColorChange = (e) => {
    setSelectedColor(e.target.value);
  };

  const handleLogoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setUploadedLogo(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeLogo = () => {
    setUploadedLogo(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const getHoodieStyle = () => {
    // Convertir la couleur hex en valeurs RGB
    const hex = selectedColor.replace('#', '');
    const r = parseInt(hex.substr(0, 2), 16);
    const g = parseInt(hex.substr(2, 2), 16);
    const b = parseInt(hex.substr(4, 2), 16);
    
    // Calculer la teinte pour hue-rotate
    const max = Math.max(r, g, b);
    const min = Math.min(r, g, b);
    let hue = 0;
    
    if (max !== min) {
      const d = max - min;
      if (max === r) hue = ((g - b) / d) % 6;
      else if (max === g) hue = (b - r) / d + 2;
      else hue = (r - g) / d + 4;
    }
    
    const hueDegrees = Math.round(hue * 60);
    
    return {
      filter: `hue-rotate(${hueDegrees}deg) saturate(1.2) brightness(1.1)`
    };
  };

  return (
    <div className="simple-hoodie-designer">
      <div className="designer-header">
        <h1>Personnalise ta capuche</h1>
        <button onClick={onClose} className="close-btn">
          <FaTimes />
        </button>
      </div>
      
      <div className="hoodie-container">
        <img 
          id="hoodie" 
          src="/hoodie-white.jpg" 
          alt="Hoodie" 
          className="hoodie"
          style={getHoodieStyle()}
        />
        {uploadedLogo && (
          <img 
            id="logo" 
            src={uploadedLogo} 
            alt="Logo" 
            className="logo"
            style={{
              width: `${logoSize}px`,
              height: `${logoSize}px`
            }}
          />
        )}
      </div>

      <div className="controls">
        <div className="control-group">
          <label htmlFor="colorPicker">Choisir couleur :</label>
          <input 
            type="color" 
            id="colorPicker" 
            value={selectedColor}
            onChange={handleColorChange}
          />
        </div>
        
        <div className="control-group">
          <label htmlFor="logoUploader">Ajouter logo :</label>
          <input 
            type="file" 
            id="logoUploader" 
            accept="image/*"
            onChange={handleLogoUpload}
            ref={fileInputRef}
          />
          {uploadedLogo && (
            <button onClick={removeLogo} className="remove-logo-btn">
              Supprimer le logo
            </button>
          )}
        </div>

        {uploadedLogo && (
          <div className="control-group">
            <label htmlFor="logoSize">Taille du logo : {logoSize}px</label>
            <input 
              type="range" 
              id="logoSize"
              min="30" 
              max="200" 
              value={logoSize}
              onChange={(e) => setLogoSize(parseInt(e.target.value))}
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default SimpleHoodieDesigner;
