import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../config/axios';

const SettingsContext = createContext();

export const useSettings = () => {
  const context = useContext(SettingsContext);
  if (!context) {
    throw new Error('useSettings must be used within a SettingsProvider');
  }
  return context;
};

export const SettingsProvider = ({ children }) => {
  const [settings, setSettings] = useState({
    general: {
      nomBoutique: 'AYNEXT',
      email: '',
      telephone: '',
      adresse: {
        rue: '',
        ville: '',
        codePostal: '',
        pays: 'Tunisie'
      },
      description: '',
      logo: ''
    },
    delivery: {
      fraisStandard: 15,
      fraisExpress: 25,
      delaiStandard: '3-5 jours',
      delaiExpress: '1-2 jours',
      zonesLivraison: ['Tunis', 'Ariana', 'Ben Arous', 'Manouba'],
      livraisonGratuite: 100
    },
    payment: {
      especes: true,
      carteBancaire: true,
      virement: false,
      paypal: false,
      stripe: false
    }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Charger les param√®tres au d√©marrage
  useEffect(() => {
    fetchSettings();
  }, []);

  // Recharger les param√®tres toutes les 30 secondes pour synchroniser avec les modifications admin
  useEffect(() => {
    const interval = setInterval(() => {
      fetchSettings(false); // Pas de loading pour les rechargements automatiques
    }, 30000); // 30 secondes

    return () => clearInterval(interval);
  }, []);

  // √âcouter les √©v√©nements de mise √† jour des param√®tres depuis l'admin
  useEffect(() => {
    const handleSettingsUpdate = (event) => {
      console.log('üîÑ Param√®tres mis √† jour par l\'admin, rechargement...', event.detail);
      fetchSettings(false); // Recharger imm√©diatement sans loading
    };

    window.addEventListener('settingsUpdated', handleSettingsUpdate);
    
    return () => {
      window.removeEventListener('settingsUpdated', handleSettingsUpdate);
    };
  }, []);

  const fetchSettings = async (showLoading = true) => {
    try {
      if (showLoading) {
        setLoading(true);
      }
      setError(null);
      const response = await api.get('/api/settings/public');
      setSettings(response.data);
    } catch (error) {
      console.error('Erreur lors du chargement des param√®tres:', error);
      setError('Erreur lors du chargement des param√®tres');
    } finally {
      if (showLoading) {
        setLoading(false);
      }
    }
  };

  const value = {
    settings,
    loading,
    error,
    fetchSettings
  };

  return (
    <SettingsContext.Provider value={value}>
      {children}
    </SettingsContext.Provider>
  );
};
