import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaTrash, FaArrowLeft, FaLock, FaTruck, FaCreditCard, FaShieldAlt } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import { formatBrand } from '../utils/formatUtils';
import '../styles/Cart.css';

const Cart = () => {
  const [loading, setLoading] = useState(true);
  const [updating, setUpdating] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [forceUpdate, setForceUpdate] = useState(0);

  const { 
    cart, 
    updateQuantity, 
    removeFromCart, 
    clearCart, 
    getTotal, 
    getItemCount 
  } = useCart();
  
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (user) {
      fetchCart();
    } else {
      setLoading(false);
    }
  }, [user]);

  // Effet pour forcer la mise Ã  jour quand le panier change
  useEffect(() => {
    console.log('ðŸ”„ Panier mis Ã  jour:', cart);
  }, [cart]);

  const fetchCart = async () => {
    try {
      setLoading(true);
      // Cart is already loaded by the context, no need to fetch
    } catch (error) {
      toast.error('Erreur lors du chargement du panier');
    } finally {
      setLoading(false);
    }
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      setUpdating(true);
      await updateQuantity(productId, newQuantity);
      // Le panier est automatiquement mis Ã  jour par le contexte
      toast.success('QuantitÃ© mise Ã  jour');
    } catch (error) {
      toast.error('Erreur lors de la mise Ã  jour de la quantitÃ©');
    } finally {
      setUpdating(false);
    }
  };

  const handleRemoveItem = async (productId) => {
    console.log('ðŸ—‘ï¸ Suppression du produit:', productId);
    console.log('Panier avant suppression:', cart);
    
    try {
      setUpdating(true);
      const result = await removeFromCart(productId);
      console.log('RÃ©sultat de la suppression:', result);
      
      if (result.success) {
        toast.success('Produit retirÃ© du panier');
      } else {
        toast.error('Erreur lors de la suppression du produit');
      }
    } catch (error) {
      console.error('Erreur lors de la suppression:', error);
      toast.error('Erreur lors de la suppression du produit');
    } finally {
      setUpdating(false);
    }
  };

  const handleClearCart = async () => {
    if (window.confirm('ÃŠtes-vous sÃ»r de vouloir vider votre panier ?')) {
      try {
        setUpdating(true);
        await clearCart();
        toast.success('Panier vidÃ©');
      } catch (error) {
        toast.error('Erreur lors du vidage du panier');
      } finally {
        setUpdating(false);
      }
    }
  };

  const handleCheckout = () => {
    if (!user) {
      toast.error('Veuillez vous connecter pour continuer');
      navigate('/login', { state: { from: '/cart' } });
      return;
    }
    setShowCheckout(true);
  };

  const handleContinueShopping = () => {
    navigate('/products');
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement du panier...</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="cart-page">
        <div className="cart-container">
          <div className="cart-header">
            <h1>Mon Panier</h1>
          </div>
          <div className="login-required">
            <h2>Connexion requise</h2>
            <p>Veuillez vous connecter pour accÃ©der Ã  votre panier</p>
            <div className="auth-buttons">
              <Link to="/login" className="btn-primary">
                Se connecter
              </Link>
              <Link to="/register" className="btn-secondary">
                CrÃ©er un compte
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (cart.articles && cart.articles.length === 0) {
    return (
      <div className="cart-page">
        <div className="cart-container">
          <div className="cart-header">
            <h1>Mon Panier</h1>
          </div>
          <div className="empty-cart">
            <div className="empty-cart-icon">ðŸ›’</div>
            <h2>Votre panier est vide</h2>
            <p>DÃ©couvrez nos produits et commencez vos achats</p>
            <button onClick={handleContinueShopping} className="btn-primary">
              Continuer mes achats
            </button>
          </div>
        </div>
      </div>
    );
  }

  const subtotal = getTotal();
  const shipping = subtotal >= 50 ? 0 : 5.99;
  const total = subtotal + shipping;
  const itemCount = getItemCount();

  return (
    <div className="cart-page">
      <div className="cart-container">
        <div className="cart-header">
          <h1>Mon Panier</h1>
          <p>{itemCount} article(s) dans votre panier</p>
        </div>

        <div className="cart-content">
          {/* Liste des produits */}
          <div className="cart-items">
            <div className="cart-items-header">
              <h3>Produits</h3>
              <button 
                onClick={handleClearCart}
                className="clear-cart-btn"
                disabled={updating}
              >
                <FaTrash />
                Vider le panier
              </button>
            </div>

            {cart.articles && cart.articles.map((item) => (
              <CartItem
                key={item._id}
                item={item}
                onQuantityChange={handleQuantityChange}
                onRemove={handleRemoveItem}
                updating={updating}
              />
            ))}
          </div>

          {/* RÃ©sumÃ© et commande */}
          <div className="cart-summary">
            <div className="summary-card">
              <h3>RÃ©sumÃ© de la commande</h3>
              
              <div className="summary-details">
                <div className="summary-row">
                  <span>Sous-total ({itemCount} article(s))</span>
                  <span>{subtotal.toFixed(2)}â‚¬</span>
                </div>
                
                <div className="summary-row">
                  <span>Frais de livraison</span>
                  <span>
                    {shipping === 0 ? (
                      <span className="free-shipping">Gratuit</span>
                    ) : (
                      `${shipping.toFixed(2)}â‚¬`
                    )}
                  </span>
                </div>
                
                {shipping > 0 && (
                  <div className="shipping-info">
                    <small>
                      Livraison gratuite Ã  partir de 50â‚¬ d'achat
                    </small>
                  </div>
                )}
                
                <div className="summary-row total">
                  <span>Total</span>
                  <span>{total.toFixed(2)}â‚¬</span>
                </div>
              </div>

              <div className="summary-actions">
                <button
                  onClick={handleCheckout}
                  className="checkout-btn"
                  disabled={updating}
                >
                  <FaLock />
                  Passer la commande
                </button>
                
                <button
                  onClick={handleContinueShopping}
                  className="continue-shopping-btn"
                >
                  <FaArrowLeft />
                  Continuer mes achats
                </button>
              </div>

              <div className="security-info">
                <div className="security-item">
                  <FaShieldAlt />
                  <span>Paiement sÃ©curisÃ©</span>
                </div>
                <div className="security-item">
                  <FaTruck />
                  <span>Livraison rapide</span>
                </div>
                <div className="security-item">
                  <FaCreditCard />
                  <span>Retours gratuits</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Section de passage de commande */}
        {showCheckout && (
          <CheckoutSection
            cart={cart}
            total={total}
            shipping={shipping}
            onClose={() => setShowCheckout(false)}
            onSuccess={() => {
              setShowCheckout(false);
              navigate('/orders');
            }}
          />
        )}
      </div>
    </div>
  );
};

// Composant article du panier
const CartItem = ({ item, onQuantityChange, onRemove, updating }) => {
  const [quantity, setQuantity] = useState(item.quantite);

  const handleQuantityChange = (newQuantity) => {
    if (newQuantity >= 1 && newQuantity <= 10) {
      setQuantity(newQuantity);
      onQuantityChange(item.produit._id, newQuantity);
    }
  };

  const handleRemove = () => {
    onRemove(item.produit._id);
  };

  const price = item.prixUnitaire;
  const totalPrice = price * item.quantite;

  return (
    <div className="cart-item">
      <div className="item-image">
        <img 
          src={item.produit.images?.[0]?.url || item.produit.images?.[0] || '/placeholder-product.jpg'} 
          alt={item.produit.images?.[0]?.alt || item.produit.nom}
          onError={(e) => {
            e.target.src = '/placeholder-product.jpg';
          }}
        />
      </div>

      <div className="item-details">
        <div className="item-info">
          <h4 className="item-name">
            <Link to={`/product/${item.produit._id}`}>
              {item.produit.nom}
            </Link>
          </h4>
          <p className="item-brand">{formatBrand(item.produit.marque)}</p>
          <div className="item-options">
            <span className="option">Taille: {item.taille}</span>
            <span className="option">Couleur: {item.couleur}</span>
          </div>
        </div>

        <div className="item-price">
          <span className="unit-price">{price.toFixed(2)}â‚¬</span>
          <span className="total-price">{totalPrice.toFixed(2)}â‚¬</span>
        </div>

        <div className="item-quantity">
          <div className="quantity-controls">
            <button
              onClick={() => handleQuantityChange(quantity - 1)}
              disabled={quantity <= 1 || updating}
              className="quantity-btn"
            >
              -
            </button>
            <span className="quantity-display">{item.quantite}</span>
            <button
              onClick={() => handleQuantityChange(quantity + 1)}
              disabled={quantity >= 10 || updating}
              className="quantity-btn"
            >
              +
            </button>
          </div>
        </div>

        <button
          onClick={handleRemove}
          disabled={updating}
          className="remove-item-btn"
          title="Retirer du panier"
        >
          <FaTrash />
        </button>
      </div>
    </div>
  );
};

// Composant de passage de commande
const CheckoutSection = ({ cart, total, shipping, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    adresseLivraison: {
      rue: '',
      ville: '',
      codePostal: '',
      pays: 'France'
    },
    methodePaiement: 'card',
    notes: ''
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    
    // Effacer l'erreur du champ modifiÃ©
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.adresseLivraison.rue.trim()) {
      newErrors['adresseLivraison.rue'] = 'La rue est requise';
    }

    if (!formData.adresseLivraison.ville.trim()) {
      newErrors['adresseLivraison.ville'] = 'La ville est requise';
    }

    if (!formData.adresseLivraison.codePostal) {
      newErrors['adresseLivraison.codePostal'] = 'Le code postal est requis';
    } else if (!/^\d{5}$/.test(formData.adresseLivraison.codePostal)) {
      newErrors['adresseLivraison.codePostal'] = 'Le code postal doit contenir 5 chiffres';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    
    try {
      const orderData = {
        adresseLivraison: formData.adresseLivraison,
        methodePaiement: formData.methodePaiement,
        notes: formData.notes
      };

      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify(orderData)
      });

      if (response.ok) {
        toast.success('Commande passÃ©e avec succÃ¨s !');
        onSuccess();
      } else {
        const error = await response.json();
        toast.error(error.message || 'Erreur lors de la commande');
      }
    } catch (error) {
      toast.error('Erreur de connexion');
    } finally {
      setLoading(false);
    }
  };

  const getFieldError = (fieldName) => {
    return errors[fieldName] || '';
  };

  return (
    <div className="checkout-overlay">
      <div className="checkout-modal">
        <div className="checkout-header">
          <h2>Finaliser la commande</h2>
          <button onClick={onClose} className="close-btn">Ã—</button>
        </div>

        <form onSubmit={handleSubmit} className="checkout-form">
          <div className="checkout-sections">
            {/* Adresse de livraison */}
            <div className="checkout-section">
              <h3>Adresse de livraison</h3>
              
              <div className="form-group">
                <label htmlFor="adresse.rue">Rue *</label>
                <input
                  type="text"
                  id="adresse.rue"
                  name="adresseLivraison.rue"
                  value={formData.adresseLivraison.rue}
                  onChange={handleChange}
                  placeholder="123 Rue de la Paix"
                  className={getFieldError('adresseLivraison.rue') ? 'error' : ''}
                />
                {getFieldError('adresseLivraison.rue') && (
                  <span className="error-message">{getFieldError('adresseLivraison.rue')}</span>
                )}
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="adresse.codePostal">Code postal *</label>
                  <input
                    type="text"
                    id="adresse.codePostal"
                    name="adresseLivraison.codePostal"
                    value={formData.adresseLivraison.codePostal}
                    onChange={handleChange}
                    placeholder="75001"
                    className={getFieldError('adresseLivraison.codePostal') ? 'error' : ''}
                  />
                  {getFieldError('adresseLivraison.codePostal') && (
                    <span className="error-message">{getFieldError('adresseLivraison.codePostal')}</span>
                  )}
                </div>

                <div className="form-group">
                  <label htmlFor="adresse.ville">Ville *</label>
                  <input
                    type="text"
                    id="adresse.ville"
                    name="adresseLivraison.ville"
                    value={formData.adresseLivraison.ville}
                    onChange={handleChange}
                    placeholder="Paris"
                    className={getFieldError('adresseLivraison.ville') ? 'error' : ''}
                  />
                  {getFieldError('adresseLivraison.ville') && (
                    <span className="error-message">{getFieldError('adresseLivraison.ville')}</span>
                  )}
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="adresse.pays">Pays</label>
                <select
                  id="adresse.pays"
                  name="adresseLivraison.pays"
                  value={formData.adresseLivraison.pays}
                  onChange={handleChange}
                >
                  <option value="France">France</option>
                  <option value="Belgique">Belgique</option>
                  <option value="Suisse">Suisse</option>
                  <option value="Canada">Canada</option>
                </select>
              </div>
            </div>

            {/* MÃ©thode de paiement */}
            <div className="checkout-section">
              <h3>MÃ©thode de paiement</h3>
              
              <div className="payment-methods">
                <label className="payment-method">
                  <input
                    type="radio"
                    name="methodePaiement"
                    value="card"
                    checked={formData.methodePaiement === 'card'}
                    onChange={handleChange}
                  />
                  <span className="checkmark"></span>
                  <div className="method-info">
                    <span className="method-name">Carte bancaire</span>
                    <span className="method-description">Visa, Mastercard, American Express</span>
                  </div>
                </label>

                <label className="payment-method">
                  <input
                    type="radio"
                    name="methodePaiement"
                    value="paypal"
                    checked={formData.methodePaiement === 'paypal'}
                    onChange={handleChange}
                  />
                  <span className="checkmark"></span>
                  <div className="method-info">
                    <span className="method-name">PayPal</span>
                    <span className="method-description">Paiement sÃ©curisÃ© en ligne</span>
                  </div>
                </label>
              </div>
            </div>

            {/* Notes */}
            <div className="checkout-section">
              <h3>Notes de commande (optionnel)</h3>
              <textarea
                name="notes"
                value={formData.notes}
                onChange={handleChange}
                placeholder="Instructions spÃ©ciales, prÃ©fÃ©rences de livraison..."
                rows="3"
              />
            </div>
          </div>

          {/* RÃ©sumÃ© final */}
          <div className="checkout-summary">
            <h3>RÃ©sumÃ© de la commande</h3>
            <div className="summary-row">
              <span>Sous-total</span>
              <span>{(total - shipping).toFixed(2)}â‚¬</span>
            </div>
            <div className="summary-row">
              <span>Livraison</span>
              <span>{shipping === 0 ? 'Gratuit' : `${shipping.toFixed(2)}â‚¬`}</span>
            </div>
            <div className="summary-row total">
              <span>Total</span>
              <span>{total.toFixed(2)}â‚¬</span>
            </div>
          </div>

          <div className="checkout-actions">
            <button
              type="button"
              onClick={onClose}
              className="btn-secondary"
              disabled={loading}
            >
              Annuler
            </button>
            <button
              type="submit"
              className="btn-primary"
              disabled={loading}
            >
              {loading ? 'Traitement...' : 'Confirmer la commande'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Cart;
