import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaPlus, FaEdit, FaTrash, FaEye, FaUsers, FaBox, FaShoppingCart, FaChartLine, FaCog } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({});
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [usersPagination, setUsersPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    pages: 0
  });
  const [usersSearch, setUsersSearch] = useState('');
  const [usersRoleFilter, setUsersRoleFilter] = useState('');

  const { user } = useAuth();

  useEffect(() => {
    if (user && user.role === 'admin') {
      fetchDashboardData();
    }
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Charger les statistiques
      const statsResponse = await fetch('/api/admin/stats', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setStats(statsData);
      }

      // Charger les produits
      const productsResponse = await fetch('/api/products?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (productsResponse.ok) {
        const productsData = await productsResponse.json();
        setProducts(productsData.products);
      }

      // Charger les commandes récentes
      const ordersResponse = await fetch('/api/orders?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (ordersResponse.ok) {
        const ordersData = await ordersResponse.json();
        setOrders(ordersData.orders);
      }

      // Charger les utilisateurs récents pour le dashboard
      await fetchUsers(1, 10);

    } catch (error) {
      toast.error('Erreur lors du chargement des données');
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async (page = 1, limit = 20, search = '', role = '') => {
    try {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: limit.toString()
      });
      
      if (search) params.append('recherche', search);
      if (role) params.append('role', role);

      const response = await fetch(`/api/users/admin/tous?${params}`, {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setUsers(data.utilisateurs);
        setUsersPagination(data.pagination);
      } else {
        toast.error('Erreur lors du chargement des utilisateurs');
      }
    } catch (error) {
      toast.error('Erreur lors du chargement des utilisateurs');
    }
  };

  const handleDeleteProduct = async (productId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce produit ?')) {
      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: 'DELETE',
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        });

        if (response.ok) {
          toast.success('Produit supprimé avec succès');
          fetchDashboardData(); // Recharger les données
        } else {
          toast.error('Erreur lors de la suppression');
        }
      } catch (error) {
        toast.error('Erreur de connexion');
      }
    }
  };

  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    try {
      const response = await fetch(`/api/orders/${orderId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ statut: newStatus })
      });

      if (response.ok) {
        toast.success('Statut de la commande mis à jour');
        fetchDashboardData(); // Recharger les données
      } else {
        toast.error('Erreur lors de la mise à jour');
      }
    } catch (error) {
      toast.error('Erreur de connexion');
    }
  };

  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-page">
        <div className="admin-container">
          <div className="access-denied">
            <h1>Accès refusé</h1>
            <p>Vous n'avez pas les permissions nécessaires pour accéder à cette page.</p>
            <Link to="/" className="btn-primary">
              Retour à l'accueil
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement du tableau de bord...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-layout">
        {/* Sidebar */}
        <div className="admin-sidebar">
          <div className="sidebar-header">
            <h2>Administration</h2>
            <p>SHADOWHOOD</p>
          </div>
          
          <nav className="sidebar-nav">
            <button
              className={`sidebar-item ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={() => setActiveTab('dashboard')}
            >
              <FaChartLine className="sidebar-icon" />
              <span>Tableau de bord</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'products' ? 'active' : ''}`}
              onClick={() => setActiveTab('products')}
            >
              <FaBox className="sidebar-icon" />
              <span>Produits</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'orders' ? 'active' : ''}`}
              onClick={() => setActiveTab('orders')}
            >
              <FaShoppingCart className="sidebar-icon" />
              <span>Commandes</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'users' ? 'active' : ''}`}
              onClick={() => setActiveTab('users')}
            >
              <FaUsers className="sidebar-icon" />
              <span>Utilisateurs</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'settings' ? 'active' : ''}`}
              onClick={() => setActiveTab('settings')}
            >
              <FaCog className="sidebar-icon" />
              <span>Paramètres</span>
            </button>
          </nav>
        </div>

        {/* Contenu principal */}
        <div className="admin-main">
          <div className="admin-header">
            <h1>Tableau de bord administrateur</h1>
            <p>Gérez votre boutique en ligne</p>
          </div>

          <div className="admin-content">
          {activeTab === 'dashboard' && (
            <DashboardTab 
              stats={stats} 
              products={products} 
              orders={orders} 
              users={users}
              onDeleteProduct={handleDeleteProduct}
              onUpdateOrderStatus={handleUpdateOrderStatus}
            />
          )}
          
          {activeTab === 'products' && (
            <ProductsTab 
              products={products}
              onDeleteProduct={handleDeleteProduct}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'orders' && (
            <OrdersTab 
              orders={orders}
              onUpdateOrderStatus={handleUpdateOrderStatus}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'users' && (
            <UsersTab 
              users={users}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'settings' && (
            <SettingsTab />
          )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Tableau de bord
const DashboardTab = ({ stats, products, orders, users, onDeleteProduct, onUpdateOrderStatus }) => {
  return (
    <div className="dashboard-tab">
      {/* Statistiques */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">
            <FaUsers />
          </div>
          <div className="stat-content">
            <h3>{stats.totalUsers || 0}</h3>
            <p>Utilisateurs</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <FaBox />
          </div>
          <div className="stat-content">
            <h3>{stats.totalProducts || 0}</h3>
            <p>Produits</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <FaShoppingCart />
          </div>
          <div className="stat-content">
            <h3>{stats.totalOrders || 0}</h3>
            <p>Commandes</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <FaChartLine />
          </div>
          <div className="stat-content">
            <h3>{stats.totalRevenue || 0}€</h3>
            <p>Chiffre d'affaires</p>
          </div>
        </div>
      </div>

      <div className="dashboard-sections">
        {/* Produits récents */}
        <div className="dashboard-section">
          <div className="section-header">
            <h3>Produits récents</h3>
            <Link to="/admin/products" className="btn-secondary">
              Voir tous
            </Link>
          </div>
          <div className="recent-items">
            {(products || []).slice(0, 5).map(product => (
              <div key={product._id} className="recent-item">
                <img 
                  src={product.images[0] || '/placeholder-product.jpg'} 
                  alt={product.nom}
                  onError={(e) => {
                    e.target.src = '/placeholder-product.jpg';
                  }}
                />
                <div className="item-info">
                  <h4>{product.nom}</h4>
                  <p>{product.marque} - {product.prix}€</p>
                </div>
                <div className="item-actions">
                  <Link to={`/admin/products/${product._id}`} className="btn-icon">
                    <FaEdit />
                  </Link>
                  <button 
                    onClick={() => onDeleteProduct(product._id)}
                    className="btn-icon delete"
                  >
                    <FaTrash />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Commandes récentes */}
        <div className="dashboard-section">
          <div className="section-header">
            <h3>Commandes récentes</h3>
            <Link to="/admin/orders" className="btn-secondary">
              Voir toutes
            </Link>
          </div>
          <div className="recent-items">
            {(orders || []).slice(0, 5).map(order => (
              <div key={order._id} className="recent-item">
                <div className="item-info">
                  <h4>Commande #{order._id?.slice(-6) || 'N/A'}</h4>
                  <p>{order.utilisateur?.nom} {order.utilisateur?.prenom}</p>
                  <p className="order-status">{order.statut}</p>
                </div>
                <div className="item-actions">
                  <select
                    value={order.statut}
                    onChange={(e) => onUpdateOrderStatus(order._id, e.target.value)}
                    className="status-select"
                  >
                    <option value="en_attente">En attente</option>
                    <option value="confirmee">Confirmée</option>
                    <option value="en_preparation">En préparation</option>
                    <option value="expediee">Expédiée</option>
                    <option value="livree">Livrée</option>
                    <option value="annulee">Annulée</option>
                  </select>
                  <Link to={`/admin/orders/${order._id}`} className="btn-icon">
                    <FaEye />
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Produits
const ProductsTab = ({ products, onDeleteProduct, onRefresh }) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  return (
    <div className="products-tab">
      <div className="tab-header">
        <h2>Gestion des produits</h2>
        <button 
          onClick={() => setShowAddForm(true)}
          className="btn-primary"
        >
          <FaPlus />
          Ajouter un produit
        </button>
      </div>

      <div className="products-table">
        <table>
          <thead>
            <tr>
              <th>Image</th>
              <th>Nom</th>
              <th>Marque</th>
              <th>Catégorie</th>
              <th>Prix</th>
              <th>Stock</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {(products || []).map(product => (
              <tr key={product._id}>
                <td>
                  <img 
                    src={product.images[0] || '/placeholder-product.jpg'} 
                    alt={product.nom}
                    className="product-thumbnail"
                    onError={(e) => {
                      e.target.src = '/placeholder-product.jpg';
                    }}
                  />
                </td>
                <td>{product.nom}</td>
                <td>{product.marque}</td>
                <td>{product.categorie}</td>
                <td>{product.prix}€</td>
                <td>
                  {Object.values(product.tailles || {}).reduce((sum, stock) => sum + stock, 0)}
                </td>
                <td>
                  <div className="action-buttons">
                    <button 
                      onClick={() => setEditingProduct(product)}
                      className="btn-icon edit"
                      title="Modifier"
                    >
                      <FaEdit />
                    </button>
                    <button 
                      onClick={() => onDeleteProduct(product._id)}
                      className="btn-icon delete"
                      title="Supprimer"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Formulaire d'ajout/modification */}
      {(showAddForm || editingProduct) && (
        <ProductForm
          product={editingProduct}
          onClose={() => {
            setShowAddForm(false);
            setEditingProduct(null);
          }}
          onSuccess={() => {
            setShowAddForm(false);
            setEditingProduct(null);
            onRefresh();
          }}
        />
      )}
    </div>
  );
};

