import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaSearch, FaFilter, FaSort, FaHeart, FaShoppingCart } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import { formatBrand, debugObjects } from '../utils/formatUtils';
import DebugWrapper from '../components/DebugWrapper';
import api from '../config/axios';
import '../styles/Products.css';

// Composant carte produit
const ProductCard = ({ product, onAddToCart }) => {
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [showQuickView, setShowQuickView] = useState(false);

  // Débogage des données du produit
  React.useEffect(() => {
    debugObjects(product, 'ProductCard - product');
    if (product?.marque) {
      debugObjects(product.marque, 'ProductCard - product.marque');
    }
  }, [product]);

  const availableSizes = product?.tailles ? product.tailles.filter(taille => 
    taille.stock > 0
  ).map(taille => taille.nom) : ['S', 'M', 'L', 'XL']; // Tailles par défaut

  const availableColors = product?.couleurs ? product.couleurs.map(couleur => couleur.nom) : ['Noir', 'Blanc', 'Rouge', 'Bleu']; // Couleurs par défaut

  const handleAddToCart = () => {
    // Si aucune taille/couleur sélectionnée, utiliser des valeurs par défaut
    const size = selectedSize || 'M';
    const color = selectedColor || 'Noir';
    
    console.log('handleAddToCart appelé avec:', { 
      product: product?.nom, 
      size, 
      color, 
      selectedSize, 
      selectedColor 
    });
    
    onAddToCart(product, size, color);
  };

  return (
    <div className="product-card">
      <div className="product-image">
        <img 
          src={product?.images?.[0]?.url || '/placeholder-product.jpg'} 
          alt={product?.images?.[0]?.alt || product?.nom || 'Produit'}
          onError={(e) => {
            e.target.src = '/placeholder-product.jpg';
          }}
        />
        <div className="product-actions">
          <button 
            className="action-btn favorite"
            title="Ajouter aux favoris"
          >
            <FaHeart />
          </button>
          <button 
            className="action-btn quick-view"
            onClick={() => setShowQuickView(true)}
            title="Voir rapidement"
          >
            <FaSearch />
          </button>
        </div>
      </div>

      <div className="product-info">
        <h3 className="product-name">
          <Link to={`/product/${product?._id}`}>
            {product?.nom || 'Produit'}
          </Link>
        </h3>
        
        <p className="product-brand">{formatBrand(product?.marque)}</p>
        <p className="product-description">{product?.description || ''}</p>
        
        <div className="product-price">
          {product?.prixPromo ? (
            <>
              <span className="original-price">{product?.prix || 0}€</span>
              <span className="promo-price">{product?.prixPromo || 0}€</span>
            </>
          ) : (
            <span className="price">{product?.prix || 0}€</span>
          )}
        </div>

        <div className="product-options">
          <select
            value={selectedSize}
            onChange={(e) => setSelectedSize(e.target.value)}
            className="size-select"
          >
            <option value="">Taille</option>
            {availableSizes.map(size => (
              <option key={size} value={size}>{size}</option>
            ))}
          </select>

          <select
            value={selectedColor}
            onChange={(e) => setSelectedColor(e.target.value)}
            className="color-select"
          >
            <option value="">Couleur</option>
            {availableColors.map(color => (
              <option key={color} value={color}>{color}</option>
            ))}
          </select>
        </div>

        <button
          className="add-to-cart-btn"
          onClick={handleAddToCart}
          disabled={!selectedSize || !selectedColor}
        >
          <FaShoppingCart />
          Ajouter au panier
        </button>
      </div>

      {/* Quick View Modal */}
      {showQuickView && (
        <div className="quick-view-modal" onClick={() => setShowQuickView(false)}>
          <div className="quick-view-content" onClick={e => e.stopPropagation()}>
            <button 
              className="close-btn"
              onClick={() => setShowQuickView(false)}
            >
              ×
            </button>
            
            <div className="quick-view-grid">
              <div className="quick-view-image">
                <img src={product?.images?.[0]?.url || '/placeholder-product.jpg'} alt={product?.images?.[0]?.alt || product?.nom || 'Produit'} />
              </div>
              
              <div className="quick-view-details">
                <h2>{product?.nom || 'Produit'}</h2>
                <p className="brand">{formatBrand(product?.marque)}</p>
                <p className="description">{product?.description || ''}</p>
                
                <div className="price">
                  {product?.prixPromo ? (
                    <>
                      <span className="original">{product?.prix || 0}€</span>
                      <span className="promo">{product?.prixPromo || 0}€</span>
                    </>
                  ) : (
                    <span>{product?.prix || 0}€</span>
                  )}
                </div>

                <div className="options">
                  <select
                    value={selectedSize}
                    onChange={(e) => setSelectedSize(e.target.value)}
                  >
                    <option value="">Taille</option>
                    {availableSizes.map(size => (
                      <option key={size} value={size}>{size}</option>
                    ))}
                  </select>

                  <select
                    value={selectedColor}
                    onChange={(e) => setSelectedColor(e.target.value)}
                  >
                    <option value="">Couleur</option>
                    {availableColors.map(color => (
                      <option key={color} value={color}>{color}</option>
                    ))}
                  </select>
                </div>

                <button
                  className="add-to-cart-btn"
                  onClick={handleAddToCart}
                  disabled={!selectedSize || !selectedColor}
                >
                  <FaShoppingCart />
                  Ajouter au panier
                </button>

                <Link 
                  to={`/product/${product?._id}`}
                  className="view-details-btn"
                >
                  Voir les détails
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [filters, setFilters] = useState({
    category: '',
    brand: '',
    minPrice: '',
    maxPrice: '',
    size: '',
    color: ''
  });
  const [sortBy, setSortBy] = useState('name');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showFilters, setShowFilters] = useState(false);
  const [timeoutError, setTimeoutError] = useState(false);

  const { addToCart } = useCart();
  const { user, isAuthenticated } = useAuth();
  
  // Log de débogage pour vérifier l'état de connexion
  React.useEffect(() => {
    console.log('🔍 État de connexion dans Products:', { 
      user: !!user, 
      isAuthenticated, 
      userId: user?._id 
    });
  }, [user, isAuthenticated]);


  useEffect(() => {
    // Charger les produits en priorité
    fetchProducts();
    
    // Charger les catégories et marques seulement si pas encore chargées
    if (categories.length === 0) {
      fetchCategories();
    }
    if (brands.length === 0) {
      fetchBrands();
    }
  }, [filters, sortBy, searchTerm, currentPage]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = {
        page: currentPage,
        limit: 12,
        tri: sortBy
      };
      
      // Mapper les filtres vers les noms attendus par le backend
      if (filters.category) params.categorie = filters.category;
      if (filters.brand) params.marque = filters.brand;
      if (filters.minPrice) params.prixMin = filters.minPrice;
      if (filters.maxPrice) params.prixMax = filters.maxPrice;
      if (filters.size) params.taille = filters.size;
      if (filters.color) params.couleur = filters.color;
      
      if (searchTerm) params.recherche = searchTerm;

      // Ajouter un timeout pour éviter les attentes trop longues
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 2000); // 2 secondes max

      const response = await api.get('/api/products', { 
        params,
        signal: controller.signal,
        timeout: 2000
      });
      
      clearTimeout(timeoutId);
      const data = response.data;
      
      const produits = data.produits || [];
      debugObjects(produits, 'Products - fetchProducts');
      
      // Filtrer les produits pour ne garder que ceux qui sont disponibles (en stock)
      const produitsDisponibles = produits.filter(product => {
        // Vérifier si le produit a des tailles avec du stock
        if (product.tailles && Array.isArray(product.tailles)) {
          return product.tailles.some(taille => taille.stock > 0);
        }
        // Si pas de tailles définies, considérer comme disponible
        return true;
      });
      
      setProducts(produitsDisponibles);
      setTotalPages(data.totalPages || 1);
    } catch (error) {
      if (error.name === 'AbortError') {
        console.log('Timeout: Le serveur met trop de temps à répondre');
        setTimeoutError(true);
        setProducts([]);
        setTotalPages(1);
      } else {
        console.log('Aucun produit trouvé ou base de données vide');
        setTimeoutError(false);
        setProducts([]);
        setTotalPages(1);
      }
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await api.get('/api/products/categories/liste', {
        timeout: 2000 // 2 secondes max pour les catégories
      });
      const data = response.data || [];
      // S'assurer que les données sont bien formatées
      const formattedCategories = data.map(cat => {
        if (typeof cat === 'string') return cat;
        if (typeof cat === 'object' && cat !== null) {
          return {
            _id: cat._id || '',
            nom: cat.nom || cat.name || '',
            code: cat.code || ''
          };
        }
        return String(cat);
      });
      debugObjects(formattedCategories, 'Products - fetchCategories');
      setCategories(formattedCategories);
    } catch (error) {
      console.log('Aucune catégorie trouvée ou base de données vide');
      setCategories([]);
    }
  };

  const fetchBrands = async () => {
    try {
      const response = await api.get('/api/products/marques/liste', {
        timeout: 2000 // 2 secondes max pour les marques
      });
      const data = response.data || [];
      // S'assurer que les données sont bien formatées
      const formattedBrands = data.map(brand => {
        if (typeof brand === 'string') return brand;
        if (typeof brand === 'object' && brand !== null) {
          return {
            _id: brand._id || '',
            nom: brand.nom || brand.name || '',
            code: brand.code || ''
          };
        }
        return String(brand);
      });
      debugObjects(formattedBrands, 'Products - fetchBrands');
      setBrands(formattedBrands);
    } catch (error) {
      console.log('Aucune marque trouvée ou base de données vide');
      setBrands([]);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
    setCurrentPage(1);
  };

  const handleSortChange = (value) => {
    setSortBy(value);
    setCurrentPage(1);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(1);
  };

  const clearFilters = () => {
    setFilters({
      category: '',
      brand: '',
      minPrice: '',
      maxPrice: '',
      size: '',
      color: ''
    });
    setSearchTerm('');
    setCurrentPage(1);
  };

  const handleRetry = () => {
    setTimeoutError(false);
    setLoading(true);
    fetchProducts();
  };

  const handleAddToCart = async (product, selectedSize, selectedColor) => {
    console.log('handleAddToCart appelé avec:', { 
      product: product?.nom, 
      productId: product?._id, 
      selectedSize, 
      selectedColor, 
      user: !!user 
    });

    if (!user) {
      console.log('Utilisateur non connecté');
      toast.error('Veuillez vous connecter pour ajouter au panier');
      return;
    }

    // Utiliser des valeurs par défaut si aucune taille/couleur sélectionnée
    const size = selectedSize || 'M';
    const color = selectedColor || 'Noir';

    console.log('Tentative d\'ajout au panier...');
    const result = await addToCart(product._id, 1, size, color);
    console.log('Résultat de l\'ajout au panier:', result);
    if (!result.success) {
      // Le toast d'erreur est déjà affiché dans CartContext
      console.error('Erreur ajout au panier:', result.message);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement des produits...</p>
      </div>
    );
  }

  return (
    <DebugWrapper name="Products">
      <div className="products-page">
        <div className="products-header">
          <h1>Nos Produits</h1>
          <p>Découvrez notre collection de vêtements tendance</p>
        </div>

        <div className="products-container">
          {/* Filtres et recherche */}
          <div className={`filters-sidebar ${showFilters ? 'show' : ''}`}>
            <div className="filters-header">
              <h3>Filtres</h3>
              <button 
                className="clear-filters"
                onClick={clearFilters}
              >
                Effacer
              </button>
            </div>

            <form onSubmit={handleSearch} className="search-form">
              <div className="search-input">
                <FaSearch />
                <input
                  type="text"
                  placeholder="Rechercher un produit..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </form>

            {categories && categories.length > 0 && (
              <div className="filter-section">
                <h4>Catégorie</h4>
                <select
                  value={filters.category}
                  onChange={(e) => handleFilterChange('category', e.target.value)}
                >
                  <option value="">Toutes les catégories</option>
                  {categories.map(cat => {
                    const key = typeof cat === 'object' ? (cat._id || cat.code || cat.nom || 'unknown') : cat;
                    const value = typeof cat === 'object' ? (cat.nom || cat.code || cat._id || '') : cat;
                    const display = typeof cat === 'object' ? (cat.nom || cat.code || cat._id || '') : cat;
                    return (
                      <option key={key} value={value}>
                        {display}
                      </option>
                    );
                  })}
                </select>
              </div>
            )}

            {brands && brands.length > 0 && (
              <div className="filter-section">
                <h4>Marque</h4>
                <select
                  value={filters.brand}
                  onChange={(e) => handleFilterChange('brand', e.target.value)}
                >
                  <option value="">Toutes les marques</option>
                  {brands.map(brand => {
                    const key = typeof brand === 'object' ? (brand._id || brand.code || brand.nom || 'unknown') : brand;
                    const value = typeof brand === 'object' ? (brand.nom || brand.code || brand._id || '') : brand;
                    const display = typeof brand === 'object' ? (brand.nom || brand.code || brand._id || '') : brand;
                    return (
                      <option key={key} value={value}>
                        {display}
                      </option>
                    );
                  })}
                </select>
              </div>
            )}

            <div className="filter-section">
              <h4>Prix</h4>
              <div className="price-inputs">
                <input
                  type="number"
                  placeholder="Min"
                  value={filters.minPrice}
                  onChange={(e) => handleFilterChange('minPrice', e.target.value)}
                />
                <span>-</span>
                <input
                  type="number"
                  placeholder="Max"
                  value={filters.maxPrice}
                  onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
                />
              </div>
            </div>

            <div className="filter-section">
              <h4>Taille</h4>
              <select
                value={filters.size}
                onChange={(e) => handleFilterChange('size', e.target.value)}
              >
                <option value="">Toutes les tailles</option>
                <option value="XS">XS</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
                <option value="XXL">XXL</option>
              </select>
            </div>

            <div className="filter-section">
              <h4>Couleur</h4>
              <select
                value={filters.color}
                onChange={(e) => handleFilterChange('color', e.target.value)}
              >
                <option value="">Toutes les couleurs</option>
                <option value="noir">Noir</option>
                <option value="blanc">Blanc</option>
                <option value="rouge">Rouge</option>
                <option value="bleu">Bleu</option>
                <option value="vert">Vert</option>
                <option value="jaune">Jaune</option>
                <option value="rose">Rose</option>
                <option value="gris">Gris</option>
              </select>
            </div>
          </div>

          {/* Produits */}
          <div className="products-main">
            <div className="products-toolbar">
              <button
                className="filters-toggle"
                onClick={() => setShowFilters(!showFilters)}
              >
                <FaFilter />
                Filtres
              </button>

              <div className="sort-controls">
                <FaSort />
                <select
                  value={sortBy}
                  onChange={(e) => handleSortChange(e.target.value)}
                >
                  <option value="name">Nom A-Z</option>
                  <option value="-name">Nom Z-A</option>
                  <option value="prix">Prix croissant</option>
                  <option value="-prix">Prix décroissant</option>
                  <option value="createdAt">Plus récents</option>
                  <option value="-createdAt">Plus anciens</option>
                </select>
              </div>

              <div className="results-info">
                {products?.length || 0} produit(s) disponible(s)
                <small style={{ display: 'block', color: '#666', fontSize: '0.9em' }}>
                  Seuls les produits en stock sont affichés
                </small>
              </div>
            </div>

            {!products || products.length === 0 ? (
              <div className="no-products">
                {timeoutError ? (
                  <>
                    <h3>⏱️ Délai d'attente dépassé</h3>
                    <p>Le serveur met trop de temps à répondre. Voulez-vous réessayer ?</p>
                    <button onClick={handleRetry} className="retry-button">
                      🔄 Réessayer
                    </button>
                  </>
                ) : (
                  <>
                    <h3>Aucun produit disponible</h3>
                    <p>
                      {searchTerm || Object.values(filters).some(f => f) 
                        ? "Aucun produit en stock ne correspond à vos critères. Essayez de modifier vos filtres ou votre recherche." 
                        : "Aucun produit en stock pour le moment. La boutique est en cours de préparation. Revenez bientôt !"
                      }
                    </p>
                  </>
                )}
              </div>
            ) : (
              <>
                <div className="products-grid">
                  {products?.map(product => (
                    <ProductCard
                      key={product._id}
                      product={product}
                      onAddToCart={handleAddToCart}
                    />
                  ))}
                </div>

                {/* Pagination */}
                {totalPages > 1 && (
                  <div className="pagination">
                    <button
                      onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                      disabled={currentPage === 1}
                    >
                      Précédent
                    </button>
                    
                    {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                      <button
                        key={page}
                        onClick={() => setCurrentPage(page)}
                        className={currentPage === page ? 'active' : ''}
                      >
                        {page}
                      </button>
                    ))}
                    
                    <button
                      onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                      disabled={currentPage === totalPages}
                    >
                      Suivant
                    </button>
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </DebugWrapper>
  );
};

export default Products;
