import { useState } from "react";

export default function HoodieCustomizer() {
  const [color, setColor] = useState("#ff0000"); // couleur par défaut (rouge)
  const [logo, setLogo] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);

  // Suppression de l'arrière-plan avec Remove.bg
  const removeBackground = async (file) => {
    const formData = new FormData();
    formData.append("image_file", file);
    
    try {
      const res = await fetch("https://api.remove.bg/v1.0/removebg", {
        method: "POST",
        headers: {
          "X-Api-Key": "YOUR_REMOVE_BG_API_KEY", // Remplacez par votre clé API
        },
        body: formData,
      });

      if (!res.ok) {
        throw new Error('Erreur lors de la suppression de l\'arrière-plan');
      }

      const blob = await res.blob();
      return URL.createObjectURL(blob);
    } catch (error) {
      console.error('Erreur Remove.bg:', error);
      // Fallback: retourner l'image originale
      return URL.createObjectURL(file);
    }
  };

  // Upload du logo avec suppression d'arrière-plan
  const handleLogoUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setIsProcessing(true);
      
      try {
        // Option 1: Avec suppression d'arrière-plan (nécessite une clé API)
        // const logoUrl = await removeBackground(file);
        // setLogo(logoUrl);
        
        // Option 2: Sans suppression d'arrière-plan (pour test)
        const reader = new FileReader();
        reader.onload = (event) => {
          setLogo(event.target.result);
          setIsProcessing(false);
        };
        reader.readAsDataURL(file);
      } catch (error) {
        console.error('Erreur lors du traitement du logo:', error);
        setIsProcessing(false);
      }
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Personnalise ton hoodie</h2>

      <div style={{ position: "relative", display: "inline-block" }}>
        {/* Hoodie de base */}
        <img
          src="/hoodie-blanc.jpg"
          alt="Hoodie"
          style={{ maxWidth: "400px", display: "block" }}
        />

        {/* Calque couleur */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: color,
            mixBlendMode: color === "#000000" ? "overlay" : "multiply",
            opacity: color === "#000000" ? 0.8 : 1, // ajuste pour le noir
            pointerEvents: "none",
          }}
        ></div>

        {/* Logo */}
        {logo && (
          <img
            src={logo}
            alt="Logo"
            style={{
              position: "absolute",
              top: "40%",
              left: "50%",
              width: "100px",
              transform: "translate(-50%, -50%)",
              pointerEvents: "none",
              filter: "drop-shadow(0 2px 4px rgba(0,0,0,0.3))",
            }}
          />
        )}

        {/* Indicateur de traitement */}
        {isProcessing && (
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              background: "rgba(0,0,0,0.8)",
              color: "white",
              padding: "10px 20px",
              borderRadius: "5px",
              fontSize: "14px",
            }}
          >
            🔄 Traitement du logo...
          </div>
        )}
      </div>

      {/* Contrôles */}
      <div style={{ marginTop: "20px" }}>
        <label>
          Choisir couleur :
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            style={{ marginLeft: "10px" }}
          />
        </label>

        <br /><br />

        <label>
          Ajouter logo :
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            style={{ marginLeft: "10px" }}
          />
        </label>
      </div>
    </div>
  );
}
