import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaSearch, FaFilter, FaSort, FaHeart, FaShoppingCart } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Products.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [filters, setFilters] = useState({
    category: '',
    brand: '',
    minPrice: '',
    maxPrice: '',
    size: '',
    color: ''
  });
  const [sortBy, setSortBy] = useState('name');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showFilters, setShowFilters] = useState(false);

  const { addToCart } = useCart();
  const { user } = useAuth();

  useEffect(() => {
    fetchProducts();
    fetchCategories();
    fetchBrands();
  }, [filters, sortBy, searchTerm, currentPage]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams({
        page: currentPage,
        limit: 12,
        sort: sortBy,
        ...filters
      });
      
      if (searchTerm) params.append('search', searchTerm);

      const response = await fetch(`/api/products?${params}`);
      const data = await response.json();
      
      if (response.ok) {
        setProducts(data.products);
        setTotalPages(data.totalPages);
      } else {
        toast.error('Erreur lors du chargement des produits');
      }
    } catch (error) {
      toast.error('Erreur de connexion');
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await fetch('/api/products/categories');
      const data = await response.json();
      if (response.ok) {
        setCategories(data);
      }
    } catch (error) {
      console.error('Erreur lors du chargement des catégories');
    }
  };

  const fetchBrands = async () => {
    try {
      const response = await fetch('/api/products/brands');
      const data = await response.json();
      if (response.ok) {
        setBrands(data);
      }
    } catch (error) {
      console.error('Erreur lors du chargement des marques');
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
    setCurrentPage(1);
  };

  const handleSortChange = (value) => {
    setSortBy(value);
    setCurrentPage(1);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(1);
  };

  const clearFilters = () => {
    setFilters({
      category: '',
      brand: '',
      minPrice: '',
      maxPrice: '',
      size: '',
      color: ''
    });
    setSearchTerm('');
    setCurrentPage(1);
  };

  const handleAddToCart = async (product, selectedSize, selectedColor) => {
    if (!user) {
      toast.error('Veuillez vous connecter pour ajouter au panier');
      return;
    }

    if (!selectedSize || !selectedColor) {
      toast.error('Veuillez sélectionner une taille et une couleur');
      return;
    }

    try {
      await addToCart(product._id, 1, selectedSize, selectedColor);
      toast.success('Produit ajouté au panier !');
    } catch (error) {
      toast.error('Erreur lors de l\'ajout au panier');
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement des produits...</p>
      </div>
    );
  }

  return (
    <div className="products-page">
      <div className="products-header">
        <h1>Nos Produits</h1>
        <p>Découvrez notre collection de vêtements tendance</p>
      </div>

      <div className="products-container">
        {/* Filtres et recherche */}
        <div className={`filters-sidebar ${showFilters ? 'show' : ''}`}>
          <div className="filters-header">
            <h3>Filtres</h3>
            <button 
              className="clear-filters"
              onClick={clearFilters}
            >
              Effacer
            </button>
          </div>

          <form onSubmit={handleSearch} className="search-form">
            <div className="search-input">
              <FaSearch />
              <input
                type="text"
                placeholder="Rechercher un produit..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </form>

          <div className="filter-section">
            <h4>Catégorie</h4>
            <select
              value={filters.category}
              onChange={(e) => handleFilterChange('category', e.target.value)}
            >
              <option value="">Toutes les catégories</option>
              {categories?.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>

          <div className="filter-section">
            <h4>Marque</h4>
            <select
              value={filters.brand}
              onChange={(e) => handleFilterChange('brand', e.target.value)}
            >
              <option value="">Toutes les marques</option>
              {brands?.map(brand => (
                <option key={brand} value={brand}>{brand}</option>
              ))}
            </select>
          </div>

          <div className="filter-section">
            <h4>Prix</h4>
            <div className="price-inputs">
              <input
                type="number"
                placeholder="Min"
                value={filters.minPrice}
                onChange={(e) => handleFilterChange('minPrice', e.target.value)}
              />
              <span>-</span>
              <input
                type="number"
                placeholder="Max"
                value={filters.maxPrice}
                onChange={(e) => handleFilterChange('maxPrice', e.target.value)}
              />
            </div>
          </div>

          <div className="filter-section">
            <h4>Taille</h4>
            <select
              value={filters.size}
              onChange={(e) => handleFilterChange('size', e.target.value)}
            >
              <option value="">Toutes les tailles</option>
              <option value="XS">XS</option>
              <option value="S">S</option>
              <option value="M">M</option>
              <option value="L">L</option>
              <option value="XL">XL</option>
              <option value="XXL">XXL</option>
            </select>
          </div>

          <div className="filter-section">
            <h4>Couleur</h4>
            <select
              value={filters.color}
              onChange={(e) => handleFilterChange('color', e.target.value)}
            >
              <option value="">Toutes les couleurs</option>
              <option value="noir">Noir</option>
              <option value="blanc">Blanc</option>
              <option value="rouge">Rouge</option>
              <option value="bleu">Bleu</option>
              <option value="vert">Vert</option>
              <option value="jaune">Jaune</option>
              <option value="rose">Rose</option>
              <option value="gris">Gris</option>
            </select>
          </div>
        </div>

        {/* Produits */}
        <div className="products-main">
          <div className="products-toolbar">
            <button
              className="filters-toggle"
              onClick={() => setShowFilters(!showFilters)}
            >
              <FaFilter />
              Filtres
            </button>

            <div className="sort-controls">
              <FaSort />
              <select
                value={sortBy}
                onChange={(e) => handleSortChange(e.target.value)}
              >
                <option value="name">Nom A-Z</option>
                <option value="-name">Nom Z-A</option>
                <option value="prix">Prix croissant</option>
                <option value="-prix">Prix décroissant</option>
                <option value="createdAt">Plus récents</option>
                <option value="-createdAt">Plus anciens</option>
              </select>
            </div>

            <div className="results-info">
              {products?.length || 0} produit(s) trouvé(s)
            </div>
          </div>

          {!products || products.length === 0 ? (
            <div className="no-products">
              <h3>Aucun produit trouvé</h3>
              <p>Essayez de modifier vos filtres ou votre recherche</p>
            </div>
          ) : (
            <>
              <div className="products-grid">
                {products?.map(product => (
                  <ProductCard
                    key={product._id}
                    product={product}
                    onAddToCart={handleAddToCart}
                  />
                ))}
              </div>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="pagination">
                  <button
                    onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                    disabled={currentPage === 1}
                  >
                    Précédent
                  </button>
                  
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                    <button
                      key={page}
                      onClick={() => setCurrentPage(page)}
                      className={currentPage === page ? 'active' : ''}
                    >
                      {page}
                    </button>
                  ))}
                  
                  <button
                    onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                    disabled={currentPage === totalPages}
                  >
                    Suivant
                  </button>
                </div>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

// Composant carte produit
const ProductCard = ({ product, onAddToCart }) => {
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [showQuickView, setShowQuickView] = useState(false);

  const availableSizes = product?.tailles ? Object.keys(product.tailles).filter(size => 
    product.tailles[size] > 0
  ) : [];

  const availableColors = product?.couleurs || [];

  const handleAddToCart = () => {
    if (!selectedSize || !selectedColor) {
      toast.error('Veuillez sélectionner une taille et une couleur');
      return;
    }
    onAddToCart(product, selectedSize, selectedColor);
  };

  return (
    <div className="product-card">
      <div className="product-image">
        <img 
          src={product?.images?.[0] || '/placeholder-product.jpg'} 
          alt={product?.nom || 'Produit'}
          onError={(e) => {
            e.target.src = '/placeholder-product.jpg';
          }}
        />
        <div className="product-actions">
          <button 
            className="action-btn favorite"
            title="Ajouter aux favoris"
          >
            <FaHeart />
          </button>
          <button 
            className="action-btn quick-view"
            onClick={() => setShowQuickView(true)}
            title="Voir rapidement"
          >
            <FaSearch />
          </button>
        </div>
      </div>

      <div className="product-info">
        <h3 className="product-name">
          <Link to={`/product/${product?._id}`}>
            {product?.nom || 'Produit'}
          </Link>
        </h3>
        
        <p className="product-brand">{product?.marque || ''}</p>
        <p className="product-description">{product?.description || ''}</p>
        
        <div className="product-price">
          {product?.prixPromo ? (
            <>
              <span className="original-price">{product?.prix || 0}€</span>
              <span className="promo-price">{product?.prixPromo || 0}€</span>
            </>
          ) : (
            <span className="price">{product?.prix || 0}€</span>
          )}
        </div>

        <div className="product-options">
          <select
            value={selectedSize}
            onChange={(e) => setSelectedSize(e.target.value)}
            className="size-select"
          >
            <option value="">Taille</option>
            {availableSizes.map(size => (
              <option key={size} value={size}>{size}</option>
            ))}
          </select>

          <select
            value={selectedColor}
            onChange={(e) => setSelectedColor(e.target.value)}
            className="color-select"
          >
            <option value="">Couleur</option>
            {availableColors.map(color => (
              <option key={color} value={color}>{color}</option>
            ))}
          </select>
        </div>

        <button
          className="add-to-cart-btn"
          onClick={handleAddToCart}
          disabled={!selectedSize || !selectedColor}
        >
          <FaShoppingCart />
          Ajouter au panier
        </button>
      </div>

      {/* Quick View Modal */}
      {showQuickView && (
        <div className="quick-view-modal" onClick={() => setShowQuickView(false)}>
          <div className="quick-view-content" onClick={e => e.stopPropagation()}>
            <button 
              className="close-btn"
              onClick={() => setShowQuickView(false)}
            >
              ×
            </button>
            
            <div className="quick-view-grid">
              <div className="quick-view-image">
                <img src={product?.images?.[0] || '/placeholder-product.jpg'} alt={product?.nom || 'Produit'} />
              </div>
              
              <div className="quick-view-details">
                <h2>{product?.nom || 'Produit'}</h2>
                <p className="brand">{product?.marque || ''}</p>
                <p className="description">{product?.description || ''}</p>
                
                <div className="price">
                  {product?.prixPromo ? (
                    <>
                      <span className="original">{product?.prix || 0}€</span>
                      <span className="promo">{product?.prixPromo || 0}€</span>
                    </>
                  ) : (
                    <span>{product?.prix || 0}€</span>
                  )}
                </div>

                <div className="options">
                  <select
                    value={selectedSize}
                    onChange={(e) => setSelectedSize(e.target.value)}
                  >
                    <option value="">Taille</option>
                    {availableSizes.map(size => (
                      <option key={size} value={size}>{size}</option>
                    ))}
                  </select>

                  <select
                    value={selectedColor}
                    onChange={(e) => setSelectedColor(e.target.value)}
                  >
                    <option value="">Couleur</option>
                    {availableColors.map(color => (
                      <option key={color} value={color}>{color}</option>
                    ))}
                  </select>
                </div>

                <button
                  className="add-to-cart-btn"
                  onClick={handleAddToCart}
                  disabled={!selectedSize || !selectedColor}
                >
                  <FaShoppingCart />
                  Ajouter au panier
                </button>

                <Link 
                  to={`/product/${product?._id}`}
                  className="view-details-btn"
                >
                  Voir les détails
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Products;
