import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../config/axios';
import { toast } from 'react-toastify';
import { useAuth } from './AuthContext';

const CartContext = createContext();

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState({ articles: [] });
  const [loading, setLoading] = useState(false);
  const { isAuthenticated } = useAuth();

  // Charger le panier depuis l'API
  const loadCart = async () => {
    if (!isAuthenticated) return;
    
    try {
      setLoading(true);
      const res = await axios.get('/api/cart');
      setCart(res.data);
    } catch (err) {
      console.error('Erreur lors du chargement du panier:', err);
    } finally {
      setLoading(false);
    }
  };

  // Charger le panier quand l'utilisateur se connecte
  useEffect(() => {
    if (isAuthenticated) {
      loadCart();
    } else {
      setCart({ articles: [] });
    }
  }, [isAuthenticated]);

  // Ajouter un article au panier
  const addToCart = async (productId, quantity, size, color) => {
    if (!isAuthenticated) {
      toast.error('Veuillez vous connecter pour ajouter des articles au panier');
      return { success: false };
    }

    try {
      setLoading(true);
      const res = await axios.post('/api/cart/ajouter', {
        produitId: productId,
        quantite: quantity,
        taille: size,
        couleur: color
      });
      
      setCart(res.data);
      toast.success('Article ajouté au panier !');
      return { success: true };
    } catch (err) {
      const message = err.response?.data?.message || 'Erreur lors de l\'ajout au panier';
      toast.error(message);
      return { success: false, message };
    } finally {
      setLoading(false);
    }
  };

  // Modifier la quantité d'un article
  const updateQuantity = async (articleId, quantity) => {
    try {
      setLoading(true);
      const res = await axios.put(`/api/cart/modifier/${articleId}`, {
        quantite: quantity
      });
      
      setCart(res.data);
      toast.success('Quantité mise à jour !');
      return { success: true };
    } catch (err) {
      const message = err.response?.data?.message || 'Erreur lors de la mise à jour';
      toast.error(message);
      return { success: false, message };
    } finally {
      setLoading(false);
    }
  };

  // Supprimer un article du panier
  const removeFromCart = async (articleId) => {
    try {
      setLoading(true);
      const res = await axios.delete(`/api/cart/supprimer/${articleId}`);
      
      setCart(res.data);
      toast.success('Article supprimé du panier !');
      return { success: true };
    } catch (err) {
      const message = err.response?.data?.message || 'Erreur lors de la suppression';
      toast.error(message);
      return { success: false, message };
    } finally {
      setLoading(false);
    }
  };

  // Vider le panier
  const clearCart = async () => {
    try {
      setLoading(true);
      await axios.delete('/api/cart/vider');
      
      setCart({ articles: [] });
      toast.success('Panier vidé !');
      return { success: true };
    } catch (err) {
      const message = err.response?.data?.message || 'Erreur lors du vidage du panier';
      toast.error(message);
      return { success: false, message };
    } finally {
      setLoading(false);
    }
  };

  // Calculer le total du panier
  const getTotal = () => {
    return cart.articles.reduce((total, article) => {
      return total + (article.prixUnitaire * article.quantite);
    }, 0);
  };

  // Obtenir le nombre total d'articles
  const getItemCount = () => {
    return cart.articles.reduce((count, article) => count + article.quantite, 0);
  };

  // Vérifier si le panier est vide
  const isEmpty = () => {
    return cart.articles.length === 0;
  };

  const value = {
    cart,
    loading,
    addToCart,
    updateQuantity,
    removeFromCart,
    clearCart,
    getTotal,
    getItemCount,
    isEmpty,
    loadCart
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
