import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../config/axios';

const OrdersContext = createContext();

export const useOrders = () => {
  const context = useContext(OrdersContext);
  if (!context) {
    throw new Error('useOrders must be used within an OrdersProvider');
  }
  return context;
};

export const OrdersProvider = ({ children }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fonction pour récupérer les commandes de l'utilisateur
  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const token = localStorage.getItem('token');
      if (!token) {
        console.log('🔑 Pas de token d\'authentification');
        setOrders([]);
        return;
      }

      console.log('📤 Récupération des commandes...');
      
      // Essayer d'abord avec axios
      let response;
      try {
        response = await api.get('/api/orders', {
          headers: {
            'x-auth-token': token
          }
        });
      } catch (axiosError) {
        console.log('⚠️ Erreur axios, essai avec fetch...', axiosError.message);
        
        // Fallback avec fetch
        const fetchResponse = await fetch('http://localhost:5000/api/orders', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'x-auth-token': token
          }
        });
        
        if (!fetchResponse.ok) {
          throw new Error(`HTTP ${fetchResponse.status}: ${fetchResponse.statusText}`);
        }
        
        const fetchData = await fetchResponse.json();
        response = { data: fetchData };
        console.log('✅ Récupération réussie avec fetch');
      }

      console.log('📥 Réponse des commandes:', response.data);
      console.log('📊 Type de réponse:', typeof response.data);
      console.log('📊 Est un tableau:', Array.isArray(response.data));
      console.log('📊 Longueur:', response.data?.length);
      
      // Le serveur retourne directement le tableau des commandes
      let orders = [];
      
      if (Array.isArray(response.data)) {
        orders = response.data;
      } else if (response.data && Array.isArray(response.data.commandes)) {
        orders = response.data.commandes;
      } else if (response.data && Array.isArray(response.data.orders)) {
        orders = response.data.orders;
      } else if (response.data && response.data.data && Array.isArray(response.data.data)) {
        orders = response.data.data;
      }
      
      console.log('📋 Commandes finales:', orders);
      setOrders(orders);
      
      console.log(`✅ ${orders.length} commande(s) chargée(s)`);
    } catch (error) {
      console.error('❌ Erreur lors du chargement des commandes:', error);
      
      let errorMessage = 'Erreur lors du chargement des commandes';
      
      if (error.code === 'ECONNABORTED') {
        errorMessage = 'Timeout: Le serveur met trop de temps à répondre';
      } else if (error.response?.status === 404) {
        errorMessage = 'Route non trouvée';
      } else if (error.response?.status === 401) {
        errorMessage = 'Non autorisé - Veuillez vous reconnecter';
      } else if (error.response?.status >= 500) {
        errorMessage = 'Erreur serveur - Veuillez réessayer plus tard';
      } else if (!error.response) {
        errorMessage = 'Impossible de contacter le serveur';
      }
      
      setError(errorMessage);
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour ajouter une nouvelle commande
  const addOrder = (newOrder) => {
    setOrders(prevOrders => [newOrder, ...prevOrders]);
  };

  // Fonction pour mettre à jour le statut d'une commande
  const updateOrderStatus = (orderId, newStatus) => {
    setOrders(prevOrders => 
      prevOrders.map(order => 
        order._id === orderId 
          ? { ...order, statut: newStatus }
          : order
      )
    );
  };

  // Fonction pour obtenir le nombre de commandes
  const getOrdersCount = () => {
    return orders.length;
  };

  // Fonction pour obtenir les commandes par statut
  const getOrdersByStatus = (status) => {
    return orders.filter(order => order.statut === status);
  };

  // Charger les commandes au montage du composant
  useEffect(() => {
    fetchOrders();
  }, []);

  const value = {
    orders,
    loading,
    error,
    fetchOrders,
    addOrder,
    updateOrderStatus,
    getOrdersCount,
    getOrdersByStatus
  };

  return (
    <OrdersContext.Provider value={value}>
      {children}
    </OrdersContext.Provider>
  );
};

export default OrdersContext;
