  };

  return (
    <div className="orders-tab">
      <div className="tab-header">
        <h2>Gestion des commandes</h2>
        <p>Total: {orders?.length || 0} commandes</p>
      </div>

      <div className="orders-table">
        <table>
          <thead>
            <tr>
              <th>Type</th>
              <th>N¬∞ Commande</th>
              <th>Client</th>
              <th>Articles</th>
              <th>Total</th>
              <th>Statut</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {(orders || []).map(order => (
              <tr key={order._id}>
                <td>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                    <span style={{ fontSize: '16px' }}>{getOrderTypeIcon(order)}</span>
                    <span style={{ fontSize: '12px', color: '#6c757d' }}>
                      {getOrderTypeLabel(order)}
                    </span>
                  </div>
                </td>
                <td>#{order._id?.slice(-6) || 'N/A'}</td>
                <td>{order.utilisateur?.nom} {order.utilisateur?.prenom}</td>
                <td>
                  <div style={{ fontSize: '12px' }}>
                    {order.articles?.map((article, index) => (
                      <div key={index} style={{ marginBottom: '8px', display: 'flex', alignItems: 'center', gap: '8px' }}>
                        {article.type === 'custom_hoodie' ? (
                          <>
                            <div style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
                              <span style={{ color: '#28a745', fontWeight: 'bold' }}>
