import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaPlus, FaEdit, FaTrash, FaEye, FaUsers, FaBox, FaShoppingCart, FaChartLine, FaCog } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({});
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [usersPagination, setUsersPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    pages: 0
  });
  const [usersSearch, setUsersSearch] = useState('');
  const [usersRoleFilter, setUsersRoleFilter] = useState('');

  const { user } = useAuth();

  useEffect(() => {
    if (user && user.role === 'admin') {
      fetchDashboardData();
    }
  }, [user]);

  // Charger les utilisateurs quand on clique sur l'onglet Utilisateurs
  useEffect(() => {
    if (activeTab === 'users' && user && user.role === 'admin') {
      fetchUsers(1, 20, '', '');
    }
  }, [activeTab, user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Charger les statistiques
      const statsResponse = await fetch('/api/admin/stats', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setStats(statsData);
      }

      // Charger les produits
      const productsResponse = await fetch('/api/products?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (productsResponse.ok) {
        const productsData = await productsResponse.json();
        setProducts(productsData.products);
      }

      // Charger les commandes récentes
      const ordersResponse = await fetch('/api/orders?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (ordersResponse.ok) {
        const ordersData = await ordersResponse.json();
        setOrders(ordersData.orders);
      }

      // Charger les utilisateurs récents pour le dashboard
      await fetchUsers(1, 10);

    } catch (error) {
      toast.error('Erreur lors du chargement des données');
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async (page = 1, limit = 20, search = '', role = '') => {
    try {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: limit.toString()
      });
      
      if (search) params.append('recherche', search);
      if (role) params.append('role', role);

      const response = await fetch(`/api/users/admin/tous?${params}`, {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setUsers(data.utilisateurs);
        setUsersPagination(data.pagination);
      } else {
        toast.error('Erreur lors du chargement des utilisateurs');
      }
    } catch (error) {
      toast.error('Erreur lors du chargement des utilisateurs');
    }
  };

  const handleDeleteProduct = async (productId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce produit ?')) {
      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: 'DELETE',
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        });

        if (response.ok) {
          toast.success('Produit supprimé avec succès');
          fetchDashboardData(); // Recharger les données
        } else {
          toast.error('Erreur lors de la suppression');
        }
      } catch (error) {
        toast.error('Erreur de connexion');
      }
    }
  };

  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    try {
      const response = await fetch(`/api/orders/${orderId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ statut: newStatus })
      });

      if (response.ok) {
        toast.success('Statut de la commande mis à jour');
        fetchDashboardData(); // Recharger les données
      } else {
        toast.error('Erreur lors de la mise à jour');
      }
    } catch (error) {
      toast.error('Erreur de connexion');
    }
  };

  const handleUsersSearch = (search) => {
    setUsersSearch(search);
    fetchUsers(1, usersPagination.limit, search, usersRoleFilter);
  };

  const handleUsersRoleFilter = (role) => {
    setUsersRoleFilter(role);
    fetchUsers(1, usersPagination.limit, usersSearch, role);
  };

  const handleUsersPageChange = (page) => {
    fetchUsers(page, usersPagination.limit, usersSearch, usersRoleFilter);
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer cet utilisateur ?')) {
      try {
        const response = await fetch(`/api/users/admin/${userId}`, {
          method: 'DELETE',
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        });

        if (response.ok) {
          toast.success('Utilisateur supprimé avec succès');
          fetchUsers(usersPagination.page, usersPagination.limit, usersSearch, usersRoleFilter);
        } else {
          const errorData = await response.json();
          toast.error(errorData.message || 'Erreur lors de la suppression');
        }
      } catch (error) {
        toast.error('Erreur de connexion');
      }
    }
  };

  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-page">
        <div className="admin-container">
          <div className="access-denied">
            <h1>Accès refusé</h1>
            <p>Vous n'avez pas les permissions nécessaires pour accéder à cette page.</p>
            <Link to="/" className="btn-primary">
              Retour à l'accueil
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement du tableau de bord...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-layout">
        {/* Sidebar */}
        <div className="admin-sidebar">
          <div className="sidebar-header">
            <h2>Administration</h2>
            <p>SHADOWHOOD</p>
          </div>
          
          <nav className="sidebar-nav">
            <button
              className={`sidebar-item ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={() => setActiveTab('dashboard')}
            >
              <FaChartLine className="sidebar-icon" />
              <span>Tableau de bord</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'products' ? 'active' : ''}`}
              onClick={() => setActiveTab('products')}
            >
              <FaBox className="sidebar-icon" />
              <span>Produits</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'orders' ? 'active' : ''}`}
              onClick={() => setActiveTab('orders')}
            >
              <FaShoppingCart className="sidebar-icon" />
              <span>Commandes</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'users' ? 'active' : ''}`}
              onClick={() => setActiveTab('users')}
            >
              <FaUsers className="sidebar-icon" />
              <span>Utilisateurs</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'settings' ? 'active' : ''}`}
              onClick={() => setActiveTab('settings')}
            >
              <FaCog className="sidebar-icon" />
              <span>Paramètres</span>
            </button>
          </nav>
        </div>

        {/* Contenu principal */}
        <div className="admin-main">
          <div className="admin-header">
            <h1>Tableau de bord administrateur</h1>
            <p>Gérez votre boutique en ligne</p>
          </div>

          <div className="admin-content">
          {activeTab === 'dashboard' && (
            <DashboardTab 
              stats={stats} 
              products={products} 
              orders={orders} 
              users={users}
              onDeleteProduct={handleDeleteProduct}
              onUpdateOrderStatus={handleUpdateOrderStatus}
            />
          )}
          
          {activeTab === 'products' && (
            <ProductsTab 
              products={products}
              onDeleteProduct={handleDeleteProduct}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'orders' && (
            <OrdersTab 
              orders={orders}
              onUpdateOrderStatus={handleUpdateOrderStatus}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'users' && (
            <UsersTab 
              users={users}
              pagination={usersPagination}
              search={usersSearch}
              roleFilter={usersRoleFilter}
              onSearch={handleUsersSearch}
              onRoleFilter={handleUsersRoleFilter}
              onPageChange={handleUsersPageChange}
              onDeleteUser={handleDeleteUser}
              onRefresh={() => fetchUsers(usersPagination.page, usersPagination.limit, usersSearch, usersRoleFilter)}
            />
          )}
          
          {activeTab === 'settings' && (
            <SettingsTab />
          )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Tableau de bord
const DashboardTab = ({ stats }) => {
  return (
    <div className="dashboard-tab">
      <div className="simple-stats">
        <h2>Statistiques de la boutique</h2>
        
        <div className="stats-grid">
          <div className="stat-card">
            <FaUsers className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalUsers || 0}</h3>
              <p>Utilisateurs</p>
            </div>
          </div>

          <div className="stat-card">
            <FaBox className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalProducts || 0}</h3>
              <p>Produits</p>
            </div>
          </div>

          <div className="stat-card">
            <FaShoppingCart className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalOrders || 0}</h3>
              <p>Commandes</p>
            </div>
          </div>

          <div className="stat-card">
            <FaChartLine className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.chiffreAffaires || 0}€</h3>
              <p>Chiffre d'affaires</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Produits
const ProductsTab = ({ products }) => {
  return (
    <div className="products-tab">
      <div className="tab-header">
        <h2>Gestion des produits</h2>
        <p>Total: {products?.length || 0} produits</p>
      </div>

      <div className="products-table">
        <table>
          <thead>
            <tr>
              <th>Nom</th>
              <th>Marque</th>
              <th>Prix</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {(products || []).map(product => (
              <tr key={product._id}>
                <td>{product.nom}</td>
                <td>{product.marque}</td>
                <td>{product.prix}€</td>
                <td>
                  <div className="action-buttons">
                    <button className="btn-icon edit" title="Modifier">
                      <FaEdit />
                    </button>
                    <button className="btn-icon delete" title="Supprimer">
                      <FaTrash />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Onglet Commandes
const OrdersTab = ({ orders, onUpdateOrderStatus, onRefresh }) => {
  return (
    <div className="orders-tab">
      <div className="tab-header">
        <h2>Gestion des commandes</h2>
      </div>

      <div className="orders-table">
        <table>
          <thead>
            <tr>
              <th>N° Commande</th>
              <th>Client</th>
              <th>Date</th>
              <th>Total</th>
              <th>Statut</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {(orders || []).map(order => (
              <tr key={order._id}>
                <td>#{order._id?.slice(-6) || 'N/A'}</td>
                <td>{order.utilisateur?.nom} {order.utilisateur?.prenom}</td>
                <td>{new Date(order.createdAt).toLocaleDateString('fr-FR')}</td>
                <td>{order.total}€</td>
                <td>
                  <select
                    value={order.statut}
                    onChange={(e) => onUpdateOrderStatus(order._id, e.target.value)}
                    className="status-select"
                  >
                    <option value="en_attente">En attente</option>
                    <option value="confirmee">Confirmée</option>
                    <option value="en_preparation">En préparation</option>
                    <option value="expediee">Expédiée</option>
                    <option value="livree">Livrée</option>
                    <option value="annulee">Annulée</option>
                  </select>
                </td>
                <td>
                  <Link to={`/admin/orders/${order._id}`} className="btn-icon">
                    <FaEye />
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Onglet Utilisateurs
const UsersTab = ({ 
  users, 
  pagination, 
  search, 
  roleFilter, 
  onSearch, 
  onRoleFilter, 
  onPageChange, 
  onDeleteUser, 
  onRefresh 
}) => {
  return (
    <div className="users-tab">
      <div className="tab-header">
        <h2>Gestion des utilisateurs</h2>
        <div className="users-stats">
          <span>Total: {pagination.total} utilisateurs</span>
        </div>
      </div>

      {/* Barre de recherche et filtres */}
      <div className="users-filters">
        <div className="search-box">
          <input
            type="text"
            placeholder="Rechercher par nom, prénom ou email..."
            value={search}
            onChange={(e) => onSearch(e.target.value)}
            className="search-input"
          />
        </div>
        
        <div className="filter-box">
          <select
            value={roleFilter}
            onChange={(e) => onRoleFilter(e.target.value)}
            className="filter-select"
          >
            <option value="">Tous les rôles</option>
            <option value="client">Clients</option>
            <option value="admin">Administrateurs</option>
          </select>
        </div>
      </div>

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>Nom</th>
              <th>Email</th>
              <th>Téléphone</th>
              <th>Rôle</th>
              <th>Date d'inscription</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {(users || []).map(user => (
              <tr key={user._id}>
                <td>{user.nom} {user.prenom}</td>
                <td>{user.email}</td>
                <td>{user.telephone || 'N/A'}</td>
                <td>
                  <span className={`role-badge ${user.role}`}>
                    {user.role === 'admin' ? 'Administrateur' : 'Client'}
                  </span>
                </td>
                <td>{new Date(user.createdAt).toLocaleDateString('fr-FR')}</td>
                <td>
                  <div className="action-buttons">
                    <Link to={`/admin/users/${user._id}`} className="btn-icon" title="Voir détails">
                      <FaEye />
                    </Link>
                    <button 
                      onClick={() => onDeleteUser(user._id)}
                      className="btn-icon delete" 
                      title="Supprimer"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      {pagination.pages > 1 && (
        <div className="pagination">
          <button
            onClick={() => onPageChange(pagination.page - 1)}
            disabled={pagination.page === 1}
            className="pagination-btn"
          >
            Précédent
          </button>
          
          <div className="pagination-info">
            Page {pagination.page} sur {pagination.pages}
          </div>
          
          <button
            onClick={() => onPageChange(pagination.page + 1)}
            disabled={pagination.page === pagination.pages}
            className="pagination-btn"
          >
            Suivant
          </button>
        </div>
      )}
    </div>
  );
};

