import { useState } from "react";

export default function HoodieCustomizer() {
  const [color, setColor] = useState("#ff0000"); // couleur par défaut (rouge)
  const [logo, setLogo] = useState(null);

  // Upload du logo
  const handleLogoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => setLogo(event.target.result);
      reader.readAsDataURL(file);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Personnalise ton hoodie</h2>

      <div style={{ position: "relative", display: "inline-block" }}>
        {/* Hoodie de base */}
        <img
          src="/hoodie-blanc.jpg"
          alt="Hoodie"
          style={{ maxWidth: "400px", display: "block" }}
        />

        {/* Calque couleur */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: color,
            mixBlendMode: color === "#000000" ? "overlay" : "multiply",
            opacity: color === "#000000" ? 0.8 : 1, // ajuste pour le noir
            pointerEvents: "none",
          }}
        ></div>

        {/* Logo */}
        {logo && (
          <img
            src={logo}
            alt="Logo"
            style={{
              position: "absolute",
              top: "40%",
              left: "50%",
              width: "100px",
              transform: "translate(-50%, -50%)",
              pointerEvents: "none",
            }}
          />
        )}
      </div>

      {/* Contrôles */}
      <div style={{ marginTop: "20px" }}>
        <label>
          Choisir couleur :
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            style={{ marginLeft: "10px" }}
          />
        </label>

        <br /><br />

        <label>
          Ajouter logo :
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            style={{ marginLeft: "10px" }}
          />
        </label>
      </div>
    </div>
  );
}
