import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { FaSearch, FaFilter, FaSort, FaHeart, FaShoppingCart, FaPalette, FaArrowLeft, FaTag, FaStar, FaEye } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import { formatBrand, debugObjects } from '../utils/formatUtils';
import DebugWrapper from '../components/DebugWrapper';
import SimpleHoodieDesigner from '../components/SimpleHoodieDesigner';
import api from '../config/axios';
import '../styles/Products.css';

// Composant carte produit standard
const ProductCard = ({ product, onAddToCart, onCustomize }) => {
  // Débogage des données du produit
  useEffect(() => {
    debugObjects(product, 'ProductCard - product');
    if (product?.marque) {
      debugObjects(product.marque, 'ProductCard - product.marque');
    }
  }, [product]);

  const handleCardClick = () => {
    // Navigation vers la page de détail du produit
    window.location.href = `/product/${product._id}`;
  };

  return (
    <div className="product-card" onClick={handleCardClick}>
      <div className="product-image">
        {product?.images && product.images.length > 0 ? (
          <img 
            src={product.images[0]?.url || product.images[0]} 
            alt={product.images[0]?.alt || product.nom}
            onError={(e) => {
              e.target.src = '/hoodie-base.jpg';
            }}
          />
        ) : (
          <img 
            src="/hoodie-base.jpg" 
            alt="Image par défaut"
            className="default-image"
          />
        )}
        <div className="product-actions">
          <button 
            className="action-btn favorite"
            title="Ajouter aux favoris"
            onClick={(e) => e.stopPropagation()}
          >
            <FaHeart />
          </button>
          {product?.estPersonnalise && (
            <button 
              className="action-btn customize"
              onClick={(e) => {
                e.stopPropagation();
                onCustomize(product);
              }}
              title="Personnaliser"
            >
              <FaPalette />
            </button>
          )}
        </div>
      </div>
      
      <div className="product-info">
        <h3 className="product-name">{product?.nom || 'Produit sans nom'}</h3>
        <p className="product-brand">{formatBrand(product?.marque)}</p>
        
        <div className="product-price">
          {product?.prixReduit && product.prixReduit < product.prix ? (
            <div className="price-container">
              <span className="original-price">{product.prix} TND</span>
              <span className="sale-price">{product.prixReduit} TND</span>
            </div>
          ) : (
            <span className="price">{product?.prix || 0} TND</span>
          )}
        </div>
      </div>
      
    </div>
  );
};

// Composant carte produit pour le sport (design moderne)
const SportProductCard = ({ product, onAddToCart }) => {
  const handleAddToCart = () => {
    onAddToCart(product, null, null);
  };

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300">
      <div className="relative">
        <img
          src={product?.images && product.images.length > 0 ? (product.images[0]?.url || product.images[0]) : '/hoodie-base.jpg'}
          alt={product?.nom || 'Produit sport'}
          className="w-full h-64 object-cover"
        />
        <div className="absolute top-2 left-2 bg-red-500 text-white px-2 py-1 rounded text-xs font-bold">
          {formatBrand(product?.marque)}
        </div>
        <div className="absolute top-2 right-2 bg-black/50 text-white p-2 rounded-full">
          <FaHeart className="text-sm" />
        </div>
      </div>
      
      <div className="p-4">
        <h3 className="font-bold text-lg mb-2 line-clamp-2">{product?.nom || 'Produit sans nom'}</h3>
        <p className="text-gray-600 text-sm mb-3 line-clamp-2">{product?.description || 'Description non disponible'}</p>
        
        <div className="flex items-center justify-between mb-3">
          <span className="text-2xl font-black text-red-500">{product?.prix || 0} TND</span>
          <div className="flex items-center">
            <FaStar className="text-yellow-400 text-sm" />
            <span className="text-sm text-gray-600 ml-1">4.5</span>
          </div>
        </div>

        <div className="flex gap-2 mb-4">
          <button className="flex-1 bg-gray-100 text-gray-700 py-2 px-3 rounded text-sm hover:bg-gray-200 transition-colors">
            <FaEye className="inline mr-1" />
            Voir
          </button>
          <button
            onClick={handleAddToCart}
            className="flex-1 bg-red-500 text-white py-2 px-3 rounded text-sm hover:bg-red-600 transition-colors flex items-center justify-center"
          >
            <FaShoppingCart className="mr-1" />
            Ajouter
          </button>
        </div>

        <div className="text-xs text-gray-500">
          <p>Stock: {product?.stock || 'Disponible'}</p>
          {product?.tailles && (
            <p>Tailles: {Array.isArray(product.tailles) ? product.tailles.map(t => t.taille || t).join(', ') : 'Disponible'}</p>
          )}
        </div>
      </div>
    </div>
  );
};

const GenderPage = () => {
  const { genre } = useParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState('dateAjout');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showCustomizer, setShowCustomizer] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedSportCategory, setSelectedSportCategory] = useState('all');

  const { addToCart } = useCart();
  const { user, isAuthenticated } = useAuth();

  // Configuration des genres
  const genreConfig = {
    femme: {
      title: '👩 Collection Femme',
      description: 'Découvrez notre collection exclusive pour femme',
      emoji: '👩',
      color: '#FF6B9D'
    },
    homme: {
      title: '👨 Collection Homme',
      description: 'Style et confort pour l\'homme moderne',
      emoji: '👨',
      color: '#4A90E2'
    },
    enfant: {
      title: '👶 Collection Enfant',
      description: 'Vêtements tendance pour les petits',
      emoji: '👶',
      color: '#7ED321'
    },
    sport: {
      title: 'SPORT',
      description: 'Découvrez notre collection complète d\'équipements et vêtements de sport pour tous vos besoins',
      emoji: '🏃',
      color: '#DC2626'
    }
  };

  // Catégories sport
  const sportCategories = [
    { id: 'all', name: 'Tous les sports', icon: '🏃‍♂️' },
    { id: 'football', name: 'Football', icon: '⚽' },
    { id: 'basketball', name: 'Basketball', icon: '🏀' },
    { id: 'tennis', name: 'Tennis', icon: '🎾' },
    { id: 'running', name: 'Course à pied', icon: '👟' },
    { id: 'fitness', name: 'Fitness', icon: '💪' },
    { id: 'swimming', name: 'Natation', icon: '🏊‍♂️' },
    { id: 'cycling', name: 'Cyclisme', icon: '🚴‍♂️' }
  ];

  const currentGenre = genreConfig[genre] || genreConfig.femme;

  useEffect(() => {
    fetchProducts();
  }, [genre, sortBy, searchTerm, currentPage]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = {
        page: currentPage,
        limit: 12,
        tri: sortBy,
        genre: genre
      };
      
      if (searchTerm) params.recherche = searchTerm;

      const response = await api.get('/api/products', { 
        params,
        timeout: 5000
      });
      
      const data = response.data;
      const produits = data.produits || [];
      
      // Filtrer les produits pour ne garder que ceux qui sont disponibles
      const produitsDisponibles = produits.filter(product => {
        if (product.tailles && Array.isArray(product.tailles)) {
          return product.tailles.some(taille => taille.stock > 0);
        }
        return true; // Si pas de tailles définies, on garde le produit
      });
      
      setProducts(produitsDisponibles);
      setTotalPages(Math.ceil((data.total || 0) / 12));
      
    } catch (error) {
      console.error('Erreur lors du chargement des produits:', error);
      toast.error('Erreur lors du chargement des produits');
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSortChange = (value) => {
    setSortBy(value);
    setCurrentPage(1);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(1);
    fetchProducts();
  };

  const handleAddToCart = async (product, selectedSize, selectedColor) => {
    console.log('handleAddToCart appelé avec:', { 
      product: product?.nom,
      selectedSize,
      selectedColor
    });
    
    try {
      const result = await addToCart(product._id, 1, selectedSize, selectedColor);
      if (result.success) {
        toast.success(`${product.nom} ajouté au panier !`);
      }
    } catch (error) {
      console.error('Erreur lors de l\'ajout au panier:', error);
      toast.error('Erreur lors de l\'ajout au panier');
    }
  };

  const handleCustomize = (product) => {
    setSelectedProduct(product);
    setShowCustomizer(true);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  if (loading) {
    return (
      <div className="products-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement des produits...</p>
        </div>
      </div>
    );
  }

  return (
    <DebugWrapper name="GenderPage">
      <div className="products-page">
        <div className="products-header">
          <Link to="/" className="back-to-home-link">
            <FaArrowLeft />
            Retour à l'accueil
          </Link>
          <h1>{currentGenre.title}</h1>
          <p>{currentGenre.description}</p>
        </div>

        <div className="products-container">
          {/* Filtres et recherche */}
          <div className="filters-sidebar">
            <div className="filters-header">
              <h3>Filtres</h3>
            </div>

            <form onSubmit={handleSearch} className="search-form">
              <div className="search-input">
                <FaSearch />
                <input
                  type="text"
                  placeholder="Rechercher dans cette collection..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </form>

            <div className="filter-section">
              <h4>Genre</h4>
              <select value={genre} disabled>
                <option value={genre}>{currentGenre.emoji} {currentGenre.title.split(' ')[1]}</option>
              </select>
            </div>

            <div className="filter-section">
              <h4>Catégorie</h4>
              <select>
                <option value="">Toutes les catégories</option>
                <option value="hoodie">Hoodie</option>
                <option value="pull">Pull</option>
              </select>
            </div>

            <div className="filter-section">
              <h4>Marque</h4>
              <select>
                <option value="">Toutes les marques</option>
                <option value="AYNEXT">AYNEXT</option>
              </select>
            </div>

            <div className="filter-section">
              <h4>Prix</h4>
              <div className="price-inputs">
                <input
                  type="number"
                  placeholder="Min"
                />
                <span>-</span>
                <input
                  type="number"
                  placeholder="Max"
                />
              </div>
            </div>

            <div className="filter-section">
              <h4>Taille</h4>
              <select>
                <option value="">Toutes les tailles</option>
                <option value="XS">XS</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
                <option value="XXL">XXL</option>
              </select>
            </div>

            <div className="filter-section">
              <h4>Couleur</h4>
              <select>
                <option value="">Toutes les couleurs</option>
                <option value="noir">Noir</option>
                <option value="blanc">Blanc</option>
                <option value="rouge">Rouge</option>
                <option value="bleu">Bleu</option>
                <option value="vert">Vert</option>
                <option value="jaune">Jaune</option>
                <option value="rose">Rose</option>
                <option value="gris">Gris</option>
              </select>
            </div>
          </div>

          {/* Produits */}
          <div className="products-main">
            <div className="products-toolbar">
              <div className="sort-controls">
                <FaSort />
                <select
                  value={sortBy}
                  onChange={(e) => handleSortChange(e.target.value)}
                >
                  <option value="name">Nom A-Z</option>
                  <option value="-name">Nom Z-A</option>
                  <option value="prix">Prix croissant</option>
                  <option value="-prix">Prix décroissant</option>
                  <option value="createdAt">Plus récents</option>
                  <option value="-createdAt">Plus anciens</option>
                </select>
              </div>

              <div className="results-info">
                {products?.length || 0} produit(s) disponible(s)
                <small style={{ display: 'block', color: '#666', fontSize: '0.9em' }}>
                  Seuls les produits en stock sont affichés
                </small>
              </div>
            </div>

            <div className="products-grid">
              {products.length > 0 ? (
                products.map(product => (
                  <ProductCard
                    key={product._id}
                    product={product}
                    onAddToCart={handleAddToCart}
                    onCustomize={handleCustomize}
                  />
                ))
              ) : (
                <div className="no-products">
                  <div className="no-products-content">
                    <h3>Aucun produit trouvé</h3>
                    <p>Il n'y a actuellement aucun produit dans cette collection.</p>
                    <Link to="/" className="back-to-home-btn">
                      Retour à l'accueil
                    </Link>
                  </div>
                </div>
              )}
            </div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="pagination">
                <button 
                  onClick={() => handlePageChange(currentPage - 1)}
                  disabled={currentPage === 1}
                  className="pagination-btn"
                >
                  Précédent
                </button>
                
                {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                  <button
                    key={page}
                    onClick={() => handlePageChange(page)}
                    className={`pagination-btn ${currentPage === page ? 'active' : ''}`}
                  >
                    {page}
                  </button>
                ))}
                
                <button 
                  onClick={() => handlePageChange(currentPage + 1)}
                  disabled={currentPage === totalPages}
                  className="pagination-btn"
                >
                  Suivant
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modal de personnalisation */}
      {showCustomizer && selectedProduct && (
        <div className="customizer-modal">
          <div className="customizer-content">
            <button 
              className="close-customizer"
              onClick={() => setShowCustomizer(false)}
            >
              ×
            </button>
            <SimpleHoodieDesigner 
              product={selectedProduct}
              onClose={() => setShowCustomizer(false)}
            />
          </div>
        </div>
      )}
    </DebugWrapper>
  );
};

export default GenderPage;
