import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaPlus, FaEdit, FaTrash, FaEye, FaUsers, FaBox, FaShoppingCart, FaChartLine, FaCog, FaSignOutAlt } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import { formatBrand } from '../utils/formatUtils';
import '../styles/Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({});
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [usersPagination, setUsersPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    pages: 0
  });
  const [usersSearch, setUsersSearch] = useState('');
  const [usersRoleFilter, setUsersRoleFilter] = useState('');

  const { user, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (user && user.role === 'admin') {
      fetchDashboardData();
    }
  }, [user]);

  const handleLogout = () => {
    if (window.confirm('Êtes-vous sûr de vouloir vous déconnecter ?')) {
      logout();
      navigate('/');
      toast.success('Déconnexion réussie');
    }
  };

  // Charger les utilisateurs quand on clique sur l'onglet Utilisateurs
  useEffect(() => {
    if (activeTab === 'users' && user && user.role === 'admin') {
      fetchUsers(1, 20, '', '');
    }
  }, [activeTab, user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Charger les statistiques
      const statsResponse = await fetch('/api/admin/stats', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setStats(statsData);
      }

      // Charger les produits
      const productsResponse = await fetch('/api/products?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (productsResponse.ok) {
        const productsData = await productsResponse.json();
        setProducts(productsData.products || []);
      }

      // Charger les commandes récentes
      const ordersResponse = await fetch('/api/orders?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (ordersResponse.ok) {
        const ordersData = await ordersResponse.json();
        setOrders(ordersData.orders);
      }

      // Charger les utilisateurs récents pour le dashboard
      await fetchUsers(1, 10);

    } catch (error) {
      toast.error('Erreur lors du chargement des données');
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async (page = 1, limit = 20, search = '', role = '') => {
    try {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: limit.toString()
      });
      
      if (search) params.append('recherche', search);
      if (role) params.append('role', role);

      const response = await fetch(`/api/users/admin/tous?${params}`, {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        setUsers(data.utilisateurs);
        setUsersPagination(data.pagination);
      } else {
        toast.error('Erreur lors du chargement des utilisateurs');
      }
    } catch (error) {
      toast.error('Erreur lors du chargement des utilisateurs');
    }
  };


  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-page">
        <div className="admin-container">
          <div className="access-denied">
            <h1>Accès refusé</h1>
            <p>Vous n'avez pas les permissions nécessaires pour accéder à cette page.</p>
            <Link to="/" className="btn-primary">
              Retour à l'accueil
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement du tableau de bord...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-layout">
        {/* Sidebar */}
        <div className="admin-sidebar">
          <div className="sidebar-header">
            <h2>Administration</h2>
            <p>SHADOWHOOD</p>
          </div>
          
          <nav className="sidebar-nav">
            <button
              className={`sidebar-item ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={() => setActiveTab('dashboard')}
            >
              <FaChartLine className="sidebar-icon" />
              <span>Tableau de bord</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'products' ? 'active' : ''}`}
              onClick={() => setActiveTab('products')}
            >
              <FaBox className="sidebar-icon" />
              <span>Produits</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'orders' ? 'active' : ''}`}
              onClick={() => setActiveTab('orders')}
            >
              <FaShoppingCart className="sidebar-icon" />
              <span>Commandes</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'users' ? 'active' : ''}`}
              onClick={() => setActiveTab('users')}
            >
              <FaUsers className="sidebar-icon" />
              <span>Utilisateurs</span>
            </button>
            
            <button
              className={`sidebar-item ${activeTab === 'settings' ? 'active' : ''}`}
              onClick={() => setActiveTab('settings')}
            >
              <FaCog className="sidebar-icon" />
              <span>Paramètres</span>
            </button>
          </nav>
          
          {/* Bouton de déconnexion */}
          <div className="sidebar-footer">
            <button
              className="sidebar-item logout-btn"
              onClick={handleLogout}
            >
              <FaSignOutAlt className="sidebar-icon" />
              <span>Déconnexion</span>
            </button>
          </div>
        </div>

        {/* Contenu principal */}
        <div className="admin-main">
          <div className="admin-content">
          {activeTab === 'dashboard' && (
            <DashboardTab stats={stats} />
          )}
          
          {activeTab === 'products' && (
            <ProductsTab 
              products={products} 
              onProductAdded={(newProduct) => {
                setProducts(prev => [...(prev || []), newProduct]);
              }}
            />
          )}
          
          {activeTab === 'orders' && (
            <OrdersTab orders={orders} />
          )}
          
          {activeTab === 'users' && (
            <UsersTab 
              users={users}
              pagination={usersPagination}
            />
          )}
          
          {activeTab === 'settings' && (
            <SettingsTab />
          )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Tableau de bord
const DashboardTab = ({ stats }) => {
  return (
    <div className="dashboard-tab">
      <div className="simple-stats">
        <h2>Statistiques de la boutique</h2>
        
        <div className="stats-grid">
          <div className="stat-card">
            <FaUsers className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalUsers || 0}</h3>
              <p>Utilisateurs</p>
            </div>
          </div>

          <div className="stat-card">
            <FaBox className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalProducts || 0}</h3>
              <p>Produits</p>
            </div>
          </div>

          <div className="stat-card">
            <FaShoppingCart className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalOrders || 0}</h3>
              <p>Commandes</p>
            </div>
          </div>

          <div className="stat-card">
            <FaChartLine className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.chiffreAffaires || 0}€</h3>
              <p>Chiffre d'affaires</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Produits
const ProductsTab = ({ products, onProductAdded }) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(false);

  return (
    <div className="products-tab">
      <div className="tab-header">
        <h2>Gestion des produits</h2>
        <div className="header-actions">
          <p>Total: {products?.length || 0} produits</p>
          <button 
            className="btn-primary"
            onClick={() => setShowAddForm(true)}
          >
            <FaPlus />
            Ajouter un produit
          </button>
        </div>
      </div>

      {showAddForm && (
        <AddProductForm 
          onClose={() => setShowAddForm(false)}
          onProductAdded={(newProduct) => {
            onProductAdded(newProduct);
            setShowAddForm(false);
          }}
        />
      )}

      <div className="products-grid">
        {(products || []).map(product => {
          const totalStock = product.tailles ? 
            product.tailles.reduce((total, taille) => total + (taille.stock || 0), 0) 
            : 0;
          const mainImage = product.images && product.images.length > 0 ? product.images[0].url : null;
          
          return (
            <div key={product._id} className="product-card">
              <div className="product-image">
                {mainImage ? (
                  <img 
                    src={mainImage} 
                    alt={product.images[0].alt || product.nom}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                ) : null}
                <div className="no-image" style={{ display: mainImage ? 'none' : 'flex' }}>
                  <FaBox />
                  <span>Aucune image</span>
                </div>
                <div className="product-badges">
                  {product.estNouveau && <span className="badge new">Nouveau</span>}
                  {product.estEnPromotion && <span className="badge promo">Promo</span>}
                  {product.estPopulaire && <span className="badge popular">Populaire</span>}
                </div>
              </div>
              
              <div className="product-info">
                <h3 className="product-name">{product.nom}</h3>
                <p className="product-brand">{formatBrand(product.marque)}</p>
                <p className="product-category">{product.categorie}</p>
                
                <div className="product-pricing">
                  <span className="current-price">{product.prix}€</span>
                  {product.prixReduit && product.prixReduit < product.prix && (
                    <span className="original-price">{product.prixReduit}€</span>
                  )}
                </div>
                
                <div className="product-stock">
                  <span className={`stock-indicator ${totalStock > 0 ? 'in-stock' : 'out-of-stock'}`}>
                    {totalStock > 0 ? `${totalStock} en stock` : 'Rupture de stock'}
                  </span>
                </div>
                
                {product.tailles && product.tailles.length > 0 && (
                  <div className="product-sizes">
                    <span className="sizes-label">Tailles disponibles:</span>
                    <div className="sizes-list">
                      {product.tailles
                        .filter(taille => taille.stock > 0)
                        .map(taille => (
                          <span key={taille.nom} className="size-tag">
                            {taille.nom} ({taille.stock})
                          </span>
                        ))
                      }
                    </div>
                  </div>
                )}
                
                {product.couleurs && product.couleurs.length > 0 && (
                  <div className="product-colors">
                    <span className="colors-label">Couleurs:</span>
                    <div className="colors-list">
                      {product.couleurs.slice(0, 5).map((couleur, index) => (
                        <div 
                          key={index}
                          className="color-dot" 
                          style={{ backgroundColor: couleur.code }}
                          title={couleur.nom}
                        />
                      ))}
                      {product.couleurs.length > 5 && (
                        <span className="more-colors">+{product.couleurs.length - 5}</span>
                      )}
                    </div>
                  </div>
                )}
              </div>
              
              <div className="product-actions">
                <button className="btn-icon edit" title="Modifier le produit">
                  <FaEdit />
                </button>
                <button className="btn-icon view" title="Voir les détails">
                  <FaEye />
                </button>
                <button className="btn-icon delete" title="Supprimer le produit">
                  <FaTrash />
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

// Onglet Commandes
const OrdersTab = ({ orders }) => {
  return (
    <div className="orders-tab">
      <div className="tab-header">
        <h2>Gestion des commandes</h2>
        <p>Total: {orders?.length || 0} commandes</p>
      </div>

      <div className="orders-table">
        <table>
          <thead>
            <tr>
              <th>N° Commande</th>
              <th>Client</th>
              <th>Total</th>
              <th>Statut</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {(orders || []).map(order => (
              <tr key={order._id}>
                <td>#{order._id?.slice(-6) || 'N/A'}</td>
                <td>{order.utilisateur?.nom} {order.utilisateur?.prenom}</td>
                <td>{order.total}€</td>
                <td>
                  <span className={`status-badge ${order.statut}`}>
                    {order.statut}
                  </span>
                </td>
                <td>
                  <div className="action-buttons">
                    <button className="btn-icon" title="Voir détails">
                      <FaEye />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Onglet Utilisateurs
const UsersTab = ({ users, pagination }) => {
  return (
    <div className="users-tab">
      <div className="tab-header">
        <h2>Gestion des utilisateurs</h2>
        <p>Total: {pagination?.total || 0} utilisateurs</p>
      </div>

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>Nom</th>
              <th>Email</th>
              <th>Rôle</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {(users || []).map(user => (
              <tr key={user._id}>
                <td>{user.nom} {user.prenom}</td>
                <td>{user.email}</td>
                <td>
                  <span className={`role-badge ${user.role}`}>
                    {user.role === 'admin' ? 'Administrateur' : 'Client'}
                  </span>
                </td>
                <td>
                  <div className="action-buttons">
                    <button className="btn-icon" title="Voir détails">
                      <FaEye />
                    </button>
                    <button className="btn-icon delete" title="Supprimer">
                      <FaTrash />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Onglet Paramètres
const SettingsTab = () => {
  return (
    <div className="settings-tab">
      <div className="tab-header">
        <h2>Paramètres de la boutique</h2>
      </div>

      <div className="settings-content">
        <div className="setting-group">
          <h3>Informations générales</h3>
          <p>Configuration des informations de base de votre boutique</p>
        </div>

        <div className="setting-group">
          <h3>Livraison</h3>
          <p>Gestion des frais de livraison et zones de livraison</p>
        </div>

        <div className="setting-group">
          <h3>Paiement</h3>
          <p>Configuration des méthodes de paiement acceptées</p>
        </div>

        <div className="setting-group">
          <h3>Notifications</h3>
          <p>Gestion des emails et notifications automatiques</p>
        </div>
      </div>
    </div>
  );
};


// Composant formulaire d'ajout de produit
const AddProductForm = ({ onClose, onProductAdded }) => {
  const [formData, setFormData] = useState({
    nom: '',
    description: '',
    prix: '',
    prixReduit: '',
    categorie: '',
    sousCategorie: '',
    marque: '',
    materiau: '',
    entretien: '',
    estEnPromotion: false,
    estNouveau: false,
    estPopulaire: false,
    tags: []
  });

  const [tailles, setTailles] = useState([
    { nom: 'XS', stock: 0 },
    { nom: 'S', stock: 0 },
    { nom: 'M', stock: 0 },
    { nom: 'L', stock: 0 },
    { nom: 'XL', stock: 0 },
    { nom: 'XXL', stock: 0 }
  ]);

  const [couleurs, setCouleurs] = useState([
    { nom: 'Noir', code: '#000000' },
    { nom: 'Blanc', code: '#FFFFFF' },
    { nom: 'Rouge', code: '#FF0000' },
    { nom: 'Bleu', code: '#0000FF' },
    { nom: 'Vert', code: '#00FF00' }
  ]);

  const [images, setImages] = useState([]);
  const [imageFiles, setImageFiles] = useState([]);
  const [uploadingImages, setUploadingImages] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const categories = [
    'hauts', 'bas', 'robes', 'vestes', 'chaussures', 'accessoires'
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    
    // Effacer l'erreur du champ modifié
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleTailleChange = (index, field, value) => {
    const newTailles = [...tailles];
    newTailles[index] = { ...newTailles[index], [field]: value };
    setTailles(newTailles);
  };

  const handleCouleurChange = (index, field, value) => {
    const newCouleurs = [...couleurs];
    newCouleurs[index] = { ...newCouleurs[index], [field]: value };
    setCouleurs(newCouleurs);
  };

  const addCouleur = () => {
    setCouleurs([...couleurs, { nom: '', code: '#000000' }]);
  };

  const removeCouleur = (index) => {
    setCouleurs(couleurs.filter((_, i) => i !== index));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => {
      const isValidType = file.type.startsWith('image/');
      const isValidSize = file.size <= 5 * 1024 * 1024; // 5MB max
      return isValidType && isValidSize;
    });

    if (validFiles.length !== files.length) {
      setErrors(prev => ({ 
        ...prev, 
        images: 'Seules les images de moins de 5MB sont acceptées' 
      }));
    }

    setImageFiles(prev => [...prev, ...validFiles]);
    
    // Créer des URLs de prévisualisation
    validFiles.forEach(file => {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImages(prev => [...prev, {
          file,
          preview: e.target.result,
          name: file.name
        }]);
      };
      reader.readAsDataURL(file);
    });
  };

  const removeImage = (index) => {
    setImages(prev => prev.filter((_, i) => i !== index));
    setImageFiles(prev => prev.filter((_, i) => i !== index));
  };

  const uploadImages = async () => {
    if (imageFiles.length === 0) return [];

    setUploadingImages(true);
    const uploadedImages = [];

    try {
      // Upload toutes les images en une seule fois
      const formData = new FormData();
      imageFiles.forEach(file => {
        formData.append('images', file);
      });

      const response = await fetch('/api/upload/product-images', {
        method: 'POST',
        headers: {
          'x-auth-token': localStorage.getItem('token')
        },
        body: formData
      });

      if (response.ok) {
        const result = await response.json();
        uploadedImages.push(...result.images.map(img => img.url));
      } else {
        const error = await response.json();
        throw new Error(error.message || 'Erreur lors du chargement des images');
      }
    } catch (error) {
      console.error('Erreur upload images:', error);
      setErrors(prev => ({ 
        ...prev, 
        images: error.message || 'Erreur lors du chargement des images' 
      }));
    } finally {
      setUploadingImages(false);
    }

    return uploadedImages;
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.nom.trim()) newErrors.nom = 'Le nom est requis';
    if (!formData.description.trim()) newErrors.description = 'La description est requise';
    if (!formData.prix || formData.prix <= 0) newErrors.prix = 'Le prix doit être supérieur à 0';
    if (!formData.categorie) newErrors.categorie = 'La catégorie est requise';
    if (!formData.marque.trim()) newErrors.marque = 'La marque est requise';

    // Vérifier qu'au moins une taille a du stock
    const hasStock = tailles.some(taille => taille.stock > 0);
    if (!hasStock) newErrors.stock = 'Au moins une taille doit avoir du stock';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    
    try {
      // Upload des images d'abord
      const uploadedImages = await uploadImages();
      
      const productData = {
        ...formData,
        prix: parseFloat(formData.prix),
        prixReduit: formData.prixReduit ? parseFloat(formData.prixReduit) : null,
        tailles: tailles.filter(taille => taille.stock > 0), // Seulement les tailles avec stock
        couleurs: couleurs.filter(couleur => couleur.nom.trim() !== ''), // Seulement les couleurs nommées
        images: uploadedImages.map(url => ({
          url: url,
          alt: formData.nom || 'Image produit'
        })) // Convertir les URLs en objets avec url et alt
      };

      const response = await fetch('/api/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify(productData)
      });

      if (response.ok) {
        const newProduct = await response.json();
        onProductAdded(newProduct);
        // Reset form
        setFormData({
          nom: '',
          description: '',
          prix: '',
          prixReduit: '',
          categorie: '',
          sousCategorie: '',
          marque: '',
          materiau: '',
          entretien: '',
          estEnPromotion: false,
          estNouveau: false,
          estPopulaire: false,
          tags: []
        });
        setTailles(tailles.map(t => ({ ...t, stock: 0 })));
        setCouleurs([
          { nom: 'Noir', code: '#000000' },
          { nom: 'Blanc', code: '#FFFFFF' },
          { nom: 'Rouge', code: '#FF0000' },
          { nom: 'Bleu', code: '#0000FF' },
          { nom: 'Vert', code: '#00FF00' }
        ]);
        setImages([]);
        setImageFiles([]);
      } else {
        const error = await response.json();
        setErrors({ submit: error.message || 'Erreur lors de l\'ajout du produit' });
      }
    } catch (error) {
      setErrors({ submit: 'Erreur de connexion' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-form">
      <div className="form-header">
        <h3>Ajouter un nouveau produit</h3>
        <button onClick={onClose} className="close-btn">×</button>
      </div>

      <form onSubmit={handleSubmit} className="product-form">
        <div className="form-section">
          <h4>Informations générales</h4>
          
          <div className="form-row">
            <div className="form-group">
              <label>Nom du produit *</label>
              <input
                type="text"
                name="nom"
                value={formData.nom}
                onChange={handleChange}
                className={errors.nom ? 'error' : ''}
                placeholder="Ex: T-shirt Premium"
              />
              {errors.nom && <span className="error-message">{errors.nom}</span>}
            </div>

            <div className="form-group">
              <label>Marque *</label>
              <input
                type="text"
                name="marque"
                value={formData.marque}
                onChange={handleChange}
                className={errors.marque ? 'error' : ''}
                placeholder="Ex: Nike, Adidas..."
              />
              {errors.marque && <span className="error-message">{errors.marque}</span>}
            </div>
          </div>

          <div className="form-group">
            <label>Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className={errors.description ? 'error' : ''}
              placeholder="Description détaillée du produit..."
              rows="3"
            />
            {errors.description && <span className="error-message">{errors.description}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Prix (€) *</label>
              <input
                type="number"
                name="prix"
                value={formData.prix}
                onChange={handleChange}
                className={errors.prix ? 'error' : ''}
                placeholder="29.99"
                step="0.01"
                min="0"
              />
              {errors.prix && <span className="error-message">{errors.prix}</span>}
            </div>

            <div className="form-group">
              <label>Prix réduit (€)</label>
              <input
                type="number"
                name="prixReduit"
                value={formData.prixReduit}
                onChange={handleChange}
                placeholder="19.99"
                step="0.01"
                min="0"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Catégorie *</label>
              <select
                name="categorie"
                value={formData.categorie}
                onChange={handleChange}
                className={errors.categorie ? 'error' : ''}
              >
                <option value="">Sélectionner une catégorie</option>
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
              {errors.categorie && <span className="error-message">{errors.categorie}</span>}
            </div>

            <div className="form-group">
              <label>Sous-catégorie</label>
              <input
                type="text"
                name="sousCategorie"
                value={formData.sousCategorie}
                onChange={handleChange}
                placeholder="Ex: T-shirts, Jeans..."
              />
            </div>
          </div>
        </div>

        <div className="form-section">
          <h4>Tailles et stock</h4>
          <div className="tailles-grid">
            {tailles.map((taille, index) => (
              <div key={index} className="taille-item">
                <label>{taille.nom}</label>
                <input
                  type="number"
                  value={taille.stock}
                  onChange={(e) => handleTailleChange(index, 'stock', parseInt(e.target.value) || 0)}
                  min="0"
                  placeholder="0"
                />
              </div>
            ))}
          </div>
          {errors.stock && <span className="error-message">{errors.stock}</span>}
        </div>

        <div className="form-section">
          <h4>Couleurs disponibles</h4>
          <div className="couleurs-list">
            {couleurs.map((couleur, index) => (
              <div key={index} className="couleur-item">
                <input
                  type="text"
                  value={couleur.nom}
                  onChange={(e) => handleCouleurChange(index, 'nom', e.target.value)}
                  placeholder="Nom de la couleur"
                />
                <input
                  type="color"
                  value={couleur.code}
                  onChange={(e) => handleCouleurChange(index, 'code', e.target.value)}
                />
                <button
                  type="button"
                  onClick={() => removeCouleur(index)}
                  className="remove-btn"
                >
                  ×
                </button>
              </div>
            ))}
            <button type="button" onClick={addCouleur} className="add-couleur-btn">
              + Ajouter une couleur
            </button>
          </div>
        </div>

        <div className="form-section">
          <h4>Images du produit</h4>
          
          <div className="image-upload-area">
            <div className="upload-zone">
              <input
                type="file"
                id="image-upload"
                multiple
                accept="image/*"
                onChange={handleImageUpload}
                style={{ display: 'none' }}
              />
              <label htmlFor="image-upload" className="upload-label">
                <div className="upload-icon">📷</div>
                <p>Cliquez pour ajouter des images</p>
                <small>Formats acceptés: JPG, PNG, GIF (max 5MB)</small>
              </label>
            </div>

            {images.length > 0 && (
              <div className="image-preview-grid">
                {images.map((image, index) => (
                  <div key={index} className="image-preview-item">
                    <img src={image.preview} alt={`Preview ${index + 1}`} />
                    <div className="image-overlay">
                      <span className="image-name">{image.name}</span>
                      <button
                        type="button"
                        onClick={() => removeImage(index)}
                        className="remove-image-btn"
                        title="Supprimer l'image"
                      >
                        ×
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}

            {errors.images && (
              <span className="error-message">{errors.images}</span>
            )}

            {uploadingImages && (
              <div className="upload-progress">
                <p>Chargement des images en cours...</p>
              </div>
            )}
          </div>
        </div>

        <div className="form-section">
          <h4>Détails supplémentaires</h4>
          
          <div className="form-row">
            <div className="form-group">
              <label>Matériau</label>
              <input
                type="text"
                name="materiau"
                value={formData.materiau}
                onChange={handleChange}
                placeholder="Ex: 100% Coton"
              />
            </div>

            <div className="form-group">
              <label>Entretien</label>
              <input
                type="text"
                name="entretien"
                value={formData.entretien}
                onChange={handleChange}
                placeholder="Ex: Lavage à 30°C"
              />
            </div>
          </div>

          <div className="checkboxes">
            <label className="checkbox-label">
              <input
                type="checkbox"
                name="estEnPromotion"
                checked={formData.estEnPromotion}
                onChange={handleChange}
              />
              En promotion
            </label>

            <label className="checkbox-label">
              <input
                type="checkbox"
                name="estNouveau"
                checked={formData.estNouveau}
                onChange={handleChange}
              />
              Nouveau produit
            </label>

            <label className="checkbox-label">
              <input
                type="checkbox"
                name="estPopulaire"
                checked={formData.estPopulaire}
                onChange={handleChange}
              />
              Produit populaire
            </label>
          </div>
        </div>

        {errors.submit && (
          <div className="error-message submit-error">{errors.submit}</div>
        )}

        <div className="form-actions">
          <button type="button" onClick={onClose} className="btn-secondary">
            Annuler
          </button>
          <button type="submit" className="btn-primary" disabled={loading}>
            {loading ? 'Ajout en cours...' : 'Ajouter le produit'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default Admin;
