import { useState } from "react";
import { removeBackground, isApiKeyConfigured } from "../config/removeBg";

export default function HoodieCustomizer() {
  const [color, setColor] = useState("#ff0000"); // couleur par défaut (rouge)

  // Couleurs prédéfinies
  const predefinedColors = [
    { name: "Noir", value: "#000000", emoji: "⚫" },
    { name: "Jaune", value: "#ffff00", emoji: "🟡" },
    { name: "Rouge", value: "#ff0000", emoji: "🔴" },
    { name: "Orange", value: "#ffa500", emoji: "🟠" },
    { name: "Bleu", value: "#0000ff", emoji: "🔵" },
    { name: "Blanc", value: "#ffffff", emoji: "⚪" },
    { name: "Vert", value: "#00ff00", emoji: "🟢" },
    { name: "Rose", value: "#ff69b4", emoji: "🩷" }
  ];
  const [logo, setLogo] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [logoPosition, setLogoPosition] = useState("chest"); // position par défaut
  const [logoSize, setLogoSize] = useState(80); // taille par défaut

  // Positions disponibles pour le logo
  const logoPositions = [
    { id: "chest", name: "Poitrine", icon: "👕" },
    { id: "back", name: "Dos", icon: "🔙" },
    { id: "sleeve", name: "Manche", icon: "👋" },
    { id: "hood", name: "Capuche", icon: "🎩" },
    { id: "pocket", name: "Poche", icon: "👝" }
  ];

  // Styles de positionnement pour chaque emplacement
  const getLogoStyle = (position) => {
    const baseStyle = {
      position: "absolute",
      width: `${logoSize}px`,
      transform: "translate(-50%, -50%)",
      pointerEvents: "none",
      filter: "drop-shadow(0 2px 4px rgba(0,0,0,0.3))",
    };

    switch (position) {
      case "chest":
        return { ...baseStyle, top: "40%", left: "50%" };
      case "back":
        return { ...baseStyle, top: "45%", left: "50%" }; // Position optimisée pour le dos
      case "sleeve":
        return { ...baseStyle, top: "60%", left: "25%" };
      case "hood":
        return { ...baseStyle, top: "15%", left: "50%" };
      case "pocket":
        return { ...baseStyle, top: "50%", left: "50%" };
      default:
        return { ...baseStyle, top: "40%", left: "50%" };
    }
  };

  // Fonction pour définir la taille recommandée selon la position
  const getRecommendedSize = (pos) => {
    switch (pos) {
      case "sleeve": return 60;
      case "hood": return 70;
      case "pocket": return 60;
      default: return 80;
    }
  };

  // Mettre à jour la taille quand la position change
  const handlePositionChange = (newPosition) => {
    setLogoPosition(newPosition);
    setLogoSize(getRecommendedSize(newPosition));
  };

  // Upload du logo avec suppression d'arrière-plan
  const handleLogoUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setIsProcessing(true);
      
      try {
        if (isApiKeyConfigured()) {
          // Avec suppression d'arrière-plan
          const logoUrl = await removeBackground(file);
          setLogo(logoUrl);
        } else {
          // Sans suppression d'arrière-plan (fallback)
          const reader = new FileReader();
          reader.onload = (event) => {
            setLogo(event.target.result);
          };
          reader.readAsDataURL(file);
        }
      } catch (error) {
        console.error('Erreur lors du traitement du logo:', error);
        // Fallback: utiliser l'image originale
        const reader = new FileReader();
        reader.onload = (event) => {
          setLogo(event.target.result);
        };
        reader.readAsDataURL(file);
      } finally {
        setIsProcessing(false);
      }
    }
  };

  return (
    <div style={{ 
      textAlign: "center", 
      marginTop: "20px",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      borderRadius: "20px",
      padding: "30px",
      boxShadow: "0 20px 40px rgba(0,0,0,0.1)",
      maxWidth: "800px",
      margin: "20px auto",
      color: "white"
    }}>
      <div style={{
        background: "rgba(255,255,255,0.1)",
        backdropFilter: "blur(10px)",
        borderRadius: "15px",
        padding: "20px",
        border: "1px solid rgba(255,255,255,0.2)"
      }}>
        <h2 style={{ 
          margin: "0 0 20px 0", 
          fontSize: "28px", 
          fontWeight: "bold",
          background: "linear-gradient(45deg, #fff, #f0f0f0)",
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          textShadow: "0 2px 4px rgba(0,0,0,0.3)"
        }}>
          🎨 Personnalise ton hoodie
        </h2>
      
        <div style={{ display: "flex", gap: "10px", justifyContent: "center", marginBottom: "20px", flexWrap: "wrap" }}>
          {logoPosition === "back" && (
            <div style={{ 
              padding: "8px 16px", 
              background: "rgba(33, 150, 243, 0.2)", 
              border: "1px solid rgba(33, 150, 243, 0.5)",
              borderRadius: "20px",
              fontSize: "14px",
              color: "#81d4fa",
              display: "flex",
              alignItems: "center",
              gap: "8px",
              backdropFilter: "blur(10px)"
            }}>
              <span>🔙</span>
              <span>Vue de dos</span>
            </div>
          )}
          
          {color === "#000000" && (
            <div style={{ 
              padding: "8px 16px", 
              background: "rgba(0, 0, 0, 0.3)", 
              border: "1px solid rgba(255, 255, 255, 0.3)",
              borderRadius: "20px",
              fontSize: "14px",
              color: "white",
              display: "flex",
              alignItems: "center",
              gap: "8px",
              backdropFilter: "blur(10px)"
            }}>
              <span>⚫</span>
              <span>Capuche noire {logoPosition === "back" ? "(dos)" : "(face)"}</span>
            </div>
          )}
        </div>

        <div style={{ 
          position: "relative", 
          display: "inline-block",
          background: "rgba(255,255,255,0.1)",
          borderRadius: "20px",
          padding: "20px",
          backdropFilter: "blur(10px)",
          border: "1px solid rgba(255,255,255,0.2)",
          boxShadow: "0 10px 30px rgba(0,0,0,0.2)"
        }}>
          {/* Hoodie de base - change selon la position et la couleur */}
          <img
            src={
              color === "#000000" 
                ? (logoPosition === "back" ? "/hoodie-dos-noir.png" : "/hoodie-noir.jpg")
                : (logoPosition === "back" ? "/hoodie-dos.jpg" : "/hoodie-blanc.jpg")
            }
            alt="Hoodie"
            style={{ 
              maxWidth: "400px", 
              display: "block",
              borderRadius: "15px",
              boxShadow: "0 8px 25px rgba(0,0,0,0.3)"
            }}
          />
        

        {/* Calque couleur - seulement si ce n'est pas noir (on a déjà l'image noire) */}
        {color !== "#000000" && (
          <div
            style={{
              position: "absolute",
              top: 0,
              left: 0,
              width: "100%",
              height: "100%",
              backgroundColor: color,
              mixBlendMode: "multiply",
              opacity: 1,
              pointerEvents: "none",
            }}
          ></div>
        )}

        {/* Logo */}
        {logo && (
          <img
            src={logo}
            alt="Logo"
            style={getLogoStyle(logoPosition)}
          />
        )}

        {/* Indicateur de position (quand pas de logo) */}
        {!logo && (
          <div
            style={{
              ...getLogoStyle(logoPosition),
              border: "2px dashed #007bff",
              borderRadius: "50%",
              background: "rgba(0, 123, 255, 0.1)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: "20px",
              color: "#007bff",
            }}
          >
            📍
          </div>
        )}

        {/* Indicateur de traitement */}
        {isProcessing && (
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              background: "rgba(0,0,0,0.8)",
              color: "white",
              padding: "10px 20px",
              borderRadius: "5px",
              fontSize: "14px",
            }}
          >
            🔄 Traitement du logo...
          </div>
        )}
      </div>

        </div>

        {/* Contrôles */}
        <div style={{ marginTop: "30px" }}>
          <div style={{ 
            marginBottom: "25px",
            background: "rgba(255,255,255,0.1)",
            borderRadius: "15px",
            padding: "20px",
            backdropFilter: "blur(10px)",
            border: "1px solid rgba(255,255,255,0.2)"
          }}>
            <label style={{ 
              display: "block", 
              marginBottom: "15px", 
              fontWeight: "bold",
              fontSize: "18px",
              color: "white",
              textShadow: "0 2px 4px rgba(0,0,0,0.3)"
            }}>
              🎨 Choisir couleur
            </label>
          <div style={{ 
            display: "flex", 
            flexWrap: "wrap", 
            gap: "8px", 
            justifyContent: "center",
            maxWidth: "500px",
            margin: "0 auto"
          }}>
            {predefinedColors.map((colorOption) => (
              <button
                key={colorOption.value}
                onClick={() => setColor(colorOption.value)}
                style={{
                  padding: "12px 18px",
                  border: color === colorOption.value ? "3px solid rgba(255,255,255,0.8)" : "2px solid rgba(255,255,255,0.3)",
                  borderRadius: "30px",
                  background: colorOption.value,
                  color: colorOption.value === "#ffffff" || colorOption.value === "#ffff00" ? "#333" : "white",
                  cursor: "pointer",
                  fontSize: "14px",
                  display: "flex",
                  alignItems: "center",
                  gap: "10px",
                  transition: "all 0.3s ease",
                  fontWeight: color === colorOption.value ? "bold" : "normal",
                  boxShadow: color === colorOption.value 
                    ? "0 8px 20px rgba(0,0,0,0.4), 0 0 0 4px rgba(255,255,255,0.2)" 
                    : "0 4px 15px rgba(0,0,0,0.2)",
                  minWidth: "110px",
                  justifyContent: "center",
                  backdropFilter: "blur(10px)",
                  transform: color === colorOption.value ? "scale(1.05)" : "scale(1)",
                  position: "relative",
                  overflow: "hidden"
                }}
              >
                <span style={{ fontSize: "18px", zIndex: 2, position: "relative" }}>{colorOption.emoji}</span>
                <span style={{ zIndex: 2, position: "relative" }}>{colorOption.name}</span>
                {color === colorOption.value && (
                  <div style={{
                    position: "absolute",
                    top: 0,
                    left: 0,
                    right: 0,
                    bottom: 0,
                    background: "linear-gradient(45deg, rgba(255,255,255,0.1), rgba(255,255,255,0.05))",
                    borderRadius: "30px"
                  }} />
                )}
              </button>
            ))}
          </div>
            <div style={{ 
              marginTop: "15px", 
              fontSize: "14px", 
              color: "rgba(255,255,255,0.9)",
              textAlign: "center",
              background: "rgba(255,255,255,0.1)",
              padding: "10px 20px",
              borderRadius: "20px",
              backdropFilter: "blur(10px)",
              border: "1px solid rgba(255,255,255,0.2)"
            }}>
              <span style={{ marginRight: "10px" }}>🎯</span>
              Couleur sélectionnée : <strong style={{ 
                color: color,
                textShadow: "0 2px 4px rgba(0,0,0,0.5)",
                fontSize: "16px"
              }}>{predefinedColors.find(c => c.value === color)?.name}</strong>
            </div>
        </div>

        <div style={{ 
          marginBottom: "25px",
          background: "rgba(255,255,255,0.1)",
          borderRadius: "15px",
          padding: "20px",
          backdropFilter: "blur(10px)",
          border: "1px solid rgba(255,255,255,0.2)"
        }}>
          <label style={{ 
            display: "block", 
            marginBottom: "15px", 
            fontWeight: "bold",
            fontSize: "18px",
            color: "white",
            textShadow: "0 2px 4px rgba(0,0,0,0.3)"
          }}>
            📷 Ajouter logo
          </label>
          <div style={{ 
            background: "rgba(255,255,255,0.1)",
            borderRadius: "10px",
            padding: "15px",
            border: "2px dashed rgba(255,255,255,0.3)"
          }}>
            <input
              type="file"
              accept="image/*"
              onChange={handleLogoUpload}
              disabled={isProcessing}
              style={{ 
                width: "100%",
                padding: "10px",
                borderRadius: "8px",
                border: "none",
                background: "rgba(255,255,255,0.2)",
                color: "white",
                fontSize: "14px"
              }}
            />
          </div>
        </div>

        {logo && (
          <div style={{ 
            marginTop: "25px",
            background: "rgba(255,255,255,0.1)",
            borderRadius: "15px",
            padding: "20px",
            backdropFilter: "blur(10px)",
            border: "1px solid rgba(255,255,255,0.2)"
          }}>
            <label style={{ 
              display: "block", 
              marginBottom: "15px", 
              fontWeight: "bold",
              fontSize: "18px",
              color: "white",
              textShadow: "0 2px 4px rgba(0,0,0,0.3)"
            }}>
              📍 Position du logo
            </label>
            <div style={{ 
              display: "flex", 
              flexWrap: "wrap", 
              gap: "8px", 
              justifyContent: "center",
              maxWidth: "400px",
              margin: "0 auto"
            }}>
              {logoPositions.map((pos) => (
                <button
                  key={pos.id}
                  onClick={() => handlePositionChange(pos.id)}
                  style={{
                    padding: "10px 16px",
                    border: logoPosition === pos.id ? "2px solid rgba(255,255,255,0.8)" : "1px solid rgba(255,255,255,0.3)",
                    borderRadius: "25px",
                    background: logoPosition === pos.id ? "rgba(255,255,255,0.2)" : "rgba(255,255,255,0.1)",
                    color: "white",
                    cursor: "pointer",
                    fontSize: "14px",
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                    transition: "all 0.3s ease",
                    fontWeight: logoPosition === pos.id ? "bold" : "normal",
                    boxShadow: logoPosition === pos.id 
                      ? "0 6px 15px rgba(0,0,0,0.3), 0 0 0 2px rgba(255,255,255,0.2)" 
                      : "0 3px 10px rgba(0,0,0,0.2)",
                    backdropFilter: "blur(10px)",
                    transform: logoPosition === pos.id ? "scale(1.05)" : "scale(1)",
                    minWidth: "100px",
                    justifyContent: "center"
                  }}
                >
                  <span style={{ fontSize: "16px" }}>{pos.icon}</span>
                  <span>{pos.name}</span>
                </button>
              ))}
            </div>
          </div>
        )}

        {logo && (
          <div style={{ 
            marginTop: "25px",
            background: "rgba(255,255,255,0.1)",
            borderRadius: "15px",
            padding: "20px",
            backdropFilter: "blur(10px)",
            border: "1px solid rgba(255,255,255,0.2)"
          }}>
            <label style={{ 
              display: "block", 
              marginBottom: "15px", 
              fontWeight: "bold",
              fontSize: "18px",
              color: "white",
              textShadow: "0 2px 4px rgba(0,0,0,0.3)"
            }}>
              📏 Taille du logo
            </label>
            <div style={{ 
              display: "flex", 
              alignItems: "center", 
              justifyContent: "center", 
              gap: "15px",
              background: "rgba(255,255,255,0.1)",
              padding: "20px",
              borderRadius: "15px",
              border: "1px solid rgba(255,255,255,0.2)",
              backdropFilter: "blur(10px)"
            }}>
              <span style={{ 
                fontSize: "14px", 
                color: "rgba(255,255,255,0.8)",
                fontWeight: "500"
              }}>Petit</span>
              <input
                type="range"
                min="40"
                max="120"
                value={logoSize}
                onChange={(e) => setLogoSize(parseInt(e.target.value))}
                style={{ 
                  width: "200px",
                  height: "8px",
                  background: "rgba(255,255,255,0.2)",
                  outline: "none",
                  borderRadius: "10px",
                  appearance: "none",
                  cursor: "pointer"
                }}
              />
              <span style={{ 
                fontSize: "14px", 
                color: "rgba(255,255,255,0.8)",
                fontWeight: "500"
              }}>Grand</span>
              <span style={{ 
                fontSize: "16px", 
                background: "linear-gradient(45deg, #667eea, #764ba2)", 
                color: "white",
                padding: "8px 16px", 
                borderRadius: "20px",
                minWidth: "60px",
                textAlign: "center",
                fontWeight: "bold",
                boxShadow: "0 4px 15px rgba(0,0,0,0.3)",
                border: "1px solid rgba(255,255,255,0.3)"
              }}>
                {logoSize}px
              </span>
            </div>
            <div style={{ 
              marginTop: "15px", 
              fontSize: "14px", 
              color: "rgba(255,255,255,0.8)",
              textAlign: "center",
              background: "rgba(255,255,255,0.1)",
              padding: "10px 20px",
              borderRadius: "15px",
              backdropFilter: "blur(10px)",
              border: "1px solid rgba(255,255,255,0.2)"
            }}>
              <span style={{ marginRight: "10px" }}>💡</span>
              Taille recommandée pour <strong>{logoPositions.find(p => p.id === logoPosition)?.name}</strong>: <strong style={{ color: "#81d4fa" }}>{getRecommendedSize(logoPosition)}px</strong>
              {logoSize !== getRecommendedSize(logoPosition) && (
                <button
                  onClick={() => setLogoSize(getRecommendedSize(logoPosition))}
                  style={{
                    marginLeft: "15px",
                    background: "linear-gradient(45deg, #28a745, #20c997)",
                    color: "white",
                    border: "none",
                    padding: "6px 12px",
                    borderRadius: "15px",
                    fontSize: "12px",
                    cursor: "pointer",
                    fontWeight: "bold",
                    boxShadow: "0 3px 10px rgba(0,0,0,0.3)",
                    transition: "all 0.3s ease"
                  }}
                >
                  🔄 Réinitialiser
                </button>
              )}
            </div>
          </div>
        )}
        
        {!isApiKeyConfigured() && (
          <div style={{ 
            marginTop: "25px",
            background: "rgba(255, 193, 7, 0.2)", 
            border: "1px solid rgba(255, 193, 7, 0.5)",
            borderRadius: "15px",
            padding: "15px",
            fontSize: "14px",
            color: "#ffc107",
            backdropFilter: "blur(10px)",
            textAlign: "center"
          }}>
            ⚠️ <strong>Conseil :</strong> Pour supprimer automatiquement l'arrière-plan, 
            utilisez des logos PNG transparents ou configurez l'API Remove.bg.
          </div>
        )}
        
        {logo && (
          <div style={{ 
            marginTop: "25px",
            textAlign: "center"
          }}>
            <button
              onClick={() => setLogo(null)}
              style={{
                background: "linear-gradient(45deg, #dc3545, #c82333)",
                color: "white",
                border: "none",
                padding: "12px 24px",
                borderRadius: "25px",
                cursor: "pointer",
                fontSize: "14px",
                fontWeight: "bold",
                boxShadow: "0 4px 15px rgba(220, 53, 69, 0.4)",
                transition: "all 0.3s ease",
                display: "flex",
                alignItems: "center",
                gap: "8px",
                margin: "0 auto"
              }}
            >
              🗑️ Supprimer le logo
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
