import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../config/axios';

const OrdersContext = createContext();

export const useOrders = () => {
  const context = useContext(OrdersContext);
  if (!context) {
    throw new Error('useOrders must be used within an OrdersProvider');
  }
  return context;
};

export const OrdersProvider = ({ children }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fonction pour rÃ©cupÃ©rer les commandes de l'utilisateur
  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const token = localStorage.getItem('token');
      if (!token) {
        console.log('ðŸ”‘ Pas de token d\'authentification');
        setOrders([]);
        return;
      }

      console.log('ðŸ“¤ RÃ©cupÃ©ration des commandes...');
      const response = await api.get('/api/orders', {
        headers: {
          'x-auth-token': token
        }
      });

      console.log('ðŸ“¥ RÃ©ponse des commandes:', response.data);
      
      // Le serveur retourne directement le tableau des commandes
      const orders = Array.isArray(response.data) ? response.data : [];
      setOrders(orders);
      
      console.log(`âœ… ${orders.length} commande(s) chargÃ©e(s)`);
    } catch (error) {
      console.error('âŒ Erreur lors du chargement des commandes:', error);
      setError('Erreur lors du chargement des commandes');
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  // Fonction pour ajouter une nouvelle commande
  const addOrder = (newOrder) => {
    setOrders(prevOrders => [newOrder, ...prevOrders]);
  };

  // Fonction pour mettre Ã  jour le statut d'une commande
  const updateOrderStatus = (orderId, newStatus) => {
    setOrders(prevOrders => 
      prevOrders.map(order => 
        order._id === orderId 
          ? { ...order, statut: newStatus }
          : order
      )
    );
  };

  // Fonction pour obtenir le nombre de commandes
  const getOrdersCount = () => {
    return orders.length;
  };

  // Fonction pour obtenir les commandes par statut
  const getOrdersByStatus = (status) => {
    return orders.filter(order => order.statut === status);
  };

  // Charger les commandes au montage du composant
  useEffect(() => {
    fetchOrders();
  }, []);

  const value = {
    orders,
    loading,
    error,
    fetchOrders,
    addOrder,
    updateOrderStatus,
    getOrdersCount,
    getOrdersByStatus
  };

  return (
    <OrdersContext.Provider value={value}>
      {children}
    </OrdersContext.Provider>
  );
};

export default OrdersContext;
