import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { FaSearch, FaFilter, FaSort, FaHeart, FaShoppingCart, FaPalette, FaArrowLeft } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';
import { formatBrand, debugObjects } from '../utils/formatUtils';
import DebugWrapper from '../components/DebugWrapper';
import SimpleHoodieDesigner from '../components/SimpleHoodieDesigner';
import api from '../config/axios';
import '../styles/Products.css';

// Composant carte produit (réutilisé de Products.js)
const ProductCard = ({ product, onAddToCart, onCustomize }) => {
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [showQuickView, setShowQuickView] = useState(false);

  // Débogage des données du produit
  React.useEffect(() => {
    debugObjects(product, 'ProductCard - product');
    if (product?.marque) {
      debugObjects(product.marque, 'ProductCard - product.marque');
    }
  }, [product]);

  const availableSizes = product?.tailles ? product.tailles.filter(taille => 
    taille.stock > 0
  ).map(taille => taille.nom) : ['S', 'M', 'L', 'XL'];

  const availableColors = product?.couleurs ? product.couleurs.map(couleur => couleur.nom) : ['Noir', 'Blanc', 'Rouge', 'Bleu'];

  const handleAddToCart = () => {
    const size = selectedSize || 'M';
    const color = selectedColor || 'Noir';
    
    console.log('handleAddToCart appelé avec:', { 
      product: product?.nom, 
      size, 
      color, 
      selectedSize, 
      selectedColor 
    });
    
    onAddToCart(product, size, color);
  };

  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-shadow duration-300 group">
      <div className="relative overflow-hidden">
        {product?.image ? (
          <img 
            src={product.image} 
            alt={product.nom} 
            className="w-full h-64 object-cover group-hover:scale-105 transition-transform duration-300"
          />
        ) : (
          <div className="w-full h-64 bg-gray-200 flex items-center justify-center">
            <span className="text-gray-500">Image non disponible</span>
          </div>
        )}
        
        {/* Overlay avec boutons */}
        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all duration-300 flex items-center justify-center opacity-0 group-hover:opacity-100">
          <div className="flex gap-2">
            <button 
              className="bg-white text-gray-800 p-3 rounded-full hover:bg-gray-100 transition-colors duration-300"
              onClick={() => setShowQuickView(true)}
            >
              <FaSearch />
            </button>
            <button 
              className="bg-white text-gray-800 p-3 rounded-full hover:bg-gray-100 transition-colors duration-300"
              onClick={() => toast.info('Fonctionnalité à venir')}
            >
              <FaHeart />
            </button>
          </div>
        </div>
      </div>
      
      <div className="p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-2 line-clamp-2">
          {product?.nom || 'Produit sans nom'}
        </h3>
        <p className="text-sm text-gray-600 mb-3">{formatBrand(product?.marque)}</p>
        
        <p className="text-sm text-gray-500 mb-4 line-clamp-3">
          {product?.description ? 
            (product.description.length > 100 ? 
              product.description.substring(0, 100) + '...' : 
              product.description
            ) : 
            'Aucune description disponible'
          }
        </p>
        
        <div className="space-y-3 mb-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Taille:</label>
            <select 
              value={selectedSize} 
              onChange={(e) => setSelectedSize(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-red-500 focus:border-transparent"
            >
              <option value="">Choisir</option>
              {availableSizes.map(size => (
                <option key={size} value={size}>{size}</option>
              ))}
            </select>
          </div>
          
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Couleur:</label>
            <select 
              value={selectedColor} 
              onChange={(e) => setSelectedColor(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-red-500 focus:border-transparent"
            >
              <option value="">Choisir</option>
              {availableColors.map(color => (
                <option key={color} value={color}>{color}</option>
              ))}
            </select>
          </div>
        </div>
        
        <div className="mb-4">
          {product?.prixReduit && product.prixReduit < product.prix ? (
            <div className="flex items-center gap-2">
              <span className="text-lg font-bold text-red-500">{product.prixReduit} TND</span>
              <span className="text-sm text-gray-500 line-through">{product.prix} TND</span>
            </div>
          ) : (
            <span className="text-lg font-bold text-gray-900">{product?.prix || 0} TND</span>
          )}
        </div>
        
        <div className="space-y-2">
          <button 
            className="w-full bg-black text-white py-3 px-4 rounded-lg hover:bg-gray-800 transition-colors duration-300 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
            onClick={handleAddToCart}
            disabled={!selectedSize || !selectedColor}
          >
            <FaShoppingCart />
            Ajouter au panier
          </button>

          <Link 
            to={`/product/${product?._id}`}
            className="block w-full text-center border border-gray-300 text-gray-700 py-3 px-4 rounded-lg hover:bg-gray-50 transition-colors duration-300"
          >
            Voir les détails
          </Link>
        </div>
      </div>
      
      {showQuickView && (
        <div className="quick-view-modal">
          <div className="quick-view-content">
            <button 
              className="close-btn"
              onClick={() => setShowQuickView(false)}
            >
              ×
            </button>
            <div className="quick-view-image">
              {product?.image ? (
                <img src={product.image} alt={product.nom} />
              ) : (
                <div className="no-image">Image non disponible</div>
              )}
            </div>
            <div className="quick-view-info">
              <h3>{product?.nom}</h3>
              <p className="brand">{formatBrand(product?.marque)}</p>
              <p className="description">{product?.description}</p>
              <div className="price">
                {product?.prixReduit && product.prixReduit < product.prix ? (
                  <div className="price-container">
                    <span className="original-price">{product.prix} TND</span>
                    <span className="sale-price">{product.prixReduit} TND</span>
                  </div>
                ) : (
                  <span>{product?.prix || 0} TND</span>
                )}
              </div>
              <div className="actions">
                <button 
                  className="add-to-cart-btn"
                  onClick={handleAddToCart}
                  disabled={!selectedSize || !selectedColor}
                >
                  <FaShoppingCart />
                  Ajouter au panier
                </button>

                <Link 
                  to={`/product/${product?._id}`}
                  className="view-details-btn"
                >
                  Voir les détails
                </Link>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const GenderPage = () => {
  const { genre } = useParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState('dateAjout');
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showCustomizer, setShowCustomizer] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const { addToCart } = useCart();
  const { user, isAuthenticated } = useAuth();

  // Configuration des genres
  const genreConfig = {
    femme: {
      title: '👩 Collection Femme',
      description: 'Découvrez notre collection exclusive pour femme',
      emoji: '👩',
      color: '#FF6B9D'
    },
    homme: {
      title: '👨 Collection Homme',
      description: 'Style et confort pour l\'homme moderne',
      emoji: '👨',
      color: '#4A90E2'
    },
    enfant: {
      title: '👶 Collection Enfant',
      description: 'Vêtements tendance pour les petits',
      emoji: '👶',
      color: '#7ED321'
    },
    unisex: {
      title: '👥 Collection Unisex',
      description: 'Des pièces pour tous, sans distinction',
      emoji: '👥',
      color: '#9013FE'
    }
  };

  const currentGenre = genreConfig[genre] || genreConfig.femme;

  useEffect(() => {
    fetchProducts();
  }, [genre, sortBy, searchTerm, currentPage]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const params = {
        page: currentPage,
        limit: 12,
        tri: sortBy,
        genre: genre
      };
      
      if (searchTerm) params.recherche = searchTerm;

      const response = await api.get('/api/products', { 
        params,
        timeout: 5000
      });
      
      const data = response.data;
      const produits = data.produits || [];
      
      // Filtrer les produits pour ne garder que ceux qui sont disponibles
      const produitsDisponibles = produits.filter(product => {
        if (product.tailles && Array.isArray(product.tailles)) {
          return product.tailles.some(taille => taille.stock > 0);
        }
        return true; // Si pas de tailles définies, on garde le produit
      });
      
      setProducts(produitsDisponibles);
      setTotalPages(Math.ceil((data.total || 0) / 12));
      
    } catch (error) {
      console.error('Erreur lors du chargement des produits:', error);
      toast.error('Erreur lors du chargement des produits');
      setProducts([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSortChange = (value) => {
    setSortBy(value);
    setCurrentPage(1);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    setCurrentPage(1);
    fetchProducts();
  };

  const handleAddToCart = async (product, selectedSize, selectedColor) => {
    console.log('handleAddToCart appelé avec:', { 
      product: product?.nom,
      selectedSize,
      selectedColor
    });
    
    try {
      await addToCart(product, selectedSize, selectedColor);
      toast.success(`${product.nom} ajouté au panier !`);
    } catch (error) {
      console.error('Erreur lors de l\'ajout au panier:', error);
      toast.error('Erreur lors de l\'ajout au panier');
    }
  };

  const handleCustomize = (product) => {
    setSelectedProduct(product);
    setShowCustomizer(true);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  if (loading) {
    return (
      <div className="products-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement des produits...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <DebugWrapper>
        {/* Hero Section avec le même style que l'accueil */}
        <section className="relative bg-white py-24">
          <div className="container mx-auto px-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-20 items-center">
              {/* Left Content */}
              <div>
                <Link to="/" className="inline-flex items-center gap-2 text-gray-600 hover:text-gray-900 mb-8 transition-colors duration-300">
                  <FaArrowLeft />
                  Retour à l'accueil
                </Link>
                
                <h1 className="text-6xl md:text-7xl font-black text-black mb-8 leading-tight">
                  {currentGenre.title.toUpperCase()}
                </h1>
                <div className="w-24 h-1 bg-red-500 mb-6"></div>
                <p className="text-2xl text-gray-700 mb-10 font-light">
                  {currentGenre.description}
                </p>
                
                {/* Bouton de personnalisation */}
                <div className="mb-8">
                  <button
                    onClick={() => setShowCustomizer(true)}
                    className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-8 py-6 font-bold text-center hover:from-purple-600 hover:to-pink-600 transition-all duration-300 shadow-lg hover:shadow-2xl transform hover:scale-105 border-2 border-purple-500 hover:border-pink-500 w-full"
                  >
                    <FaPalette className="inline mr-2" />
                    PERSONNALISER UN HOODIE
                  </button>
                </div>
              </div>

              {/* Right Content - Placeholder pour image ou contenu visuel */}
              <div className="relative">
                <div className="bg-gray-100 rounded-lg p-8 text-center">
                  <div className="text-6xl mb-4">{currentGenre.emoji}</div>
                  <h3 className="text-xl font-semibold text-gray-700 mb-2">Collection {currentGenre.title.split(' ')[1]}</h3>
                  <p className="text-gray-500">Découvrez nos produits exclusifs</p>
                </div>
                
                {/* Éléments décoratifs comme sur l'accueil */}
                <div className="absolute top-0 left-0 w-full h-full">
                  <div className="absolute top-8 left-20 w-2 h-2 bg-red-500 rounded-full opacity-80 animate-ping"></div>
                  <div className="absolute top-16 right-32 w-1 h-1 bg-gray-400 rounded-full opacity-60 animate-pulse"></div>
                  <div className="absolute bottom-12 left-1/4 w-1.5 h-1.5 bg-red-400 rounded-full opacity-70 animate-bounce"></div>
                  <div className="absolute top-24 right-1/3 w-1 h-1 bg-gray-400 rounded-full opacity-50 animate-ping"></div>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Section des produits */}
        <section className="py-16 bg-gray-50">
          <div className="container mx-auto px-6">

            {/* Barre d'outils moderne */}
            <div className="mb-12">
              <div className="flex flex-col md:flex-row gap-6 items-center justify-between">
                <form onSubmit={handleSearch} className="flex-1 max-w-md">
                  <div className="relative">
                    <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                    <input
                      type="text"
                      placeholder="Rechercher dans cette collection..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-red-500 focus:border-transparent"
                    />
                  </div>
                </form>

                <div className="flex items-center gap-2">
                  <FaSort className="text-gray-400" />
                  <select 
                    value={sortBy} 
                    onChange={(e) => handleSortChange(e.target.value)}
                    className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-red-500 focus:border-transparent"
                  >
                    <option value="dateAjout">Plus récents</option>
                    <option value="prix">Prix croissant</option>
                    <option value="-prix">Prix décroissant</option>
                    <option value="nom">Nom A-Z</option>
                    <option value="-nom">Nom Z-A</option>
                  </select>
                </div>
              </div>
            </div>

            {/* Résultats */}
            <div className="mb-8 text-center">
              <p className="text-lg text-gray-600">
                {products.length} produit{products.length > 1 ? 's' : ''} trouvé{products.length > 1 ? 's' : ''}
              </p>
            </div>

            {/* Grille des produits */}
            {products.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
                {products.map(product => (
                  <ProductCard
                    key={product._id}
                    product={product}
                    onAddToCart={handleAddToCart}
                    onCustomize={handleCustomize}
                  />
                ))}
              </div>
            ) : (
              <div className="text-center py-16">
                <div className="text-6xl mb-4">{currentGenre.emoji}</div>
                <h3 className="text-2xl font-bold text-gray-700 mb-4">Aucun produit trouvé</h3>
                <p className="text-gray-500 mb-8">Il n'y a actuellement aucun produit dans cette collection.</p>
                <Link 
                  to="/" 
                  className="inline-block bg-black text-white px-8 py-3 rounded-lg hover:bg-gray-800 transition-colors duration-300"
                >
                  Retour à l'accueil
                </Link>
              </div>
            )}

            {/* Pagination moderne */}
            {totalPages > 1 && (
              <div className="mt-16 flex justify-center">
                <div className="flex items-center gap-2">
                  <button 
                    onClick={() => handlePageChange(currentPage - 1)}
                    disabled={currentPage === 1}
                    className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-300"
                  >
                    Précédent
                  </button>
                  
                  {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
                    <button
                      key={page}
                      onClick={() => handlePageChange(page)}
                      className={`px-4 py-2 border rounded-lg transition-colors duration-300 ${
                        currentPage === page 
                          ? 'bg-red-500 text-white border-red-500' 
                          : 'border-gray-300 hover:bg-gray-50'
                      }`}
                    >
                      {page}
                    </button>
                  ))}
                  
                  <button 
                    onClick={() => handlePageChange(currentPage + 1)}
                    disabled={currentPage === totalPages}
                    className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-300"
                  >
                    Suivant
                  </button>
                </div>
              </div>
            )}
          </div>
        </section>
      </DebugWrapper>

      {/* Modal de personnalisation */}
      {showCustomizer && selectedProduct && (
        <div className="customizer-modal">
          <div className="customizer-content">
            <button 
              className="close-customizer"
              onClick={() => setShowCustomizer(false)}
            >
              ×
            </button>
            <SimpleHoodieDesigner 
              product={selectedProduct}
              onClose={() => setShowCustomizer(false)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default GenderPage;
