import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaEye, FaEyeSlash, FaEnvelope, FaLock, FaUser, FaPhone, FaMapMarkerAlt, FaGoogle, FaFacebook } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    telephone: '',
    motDePasse: '',
    confirmerMotDePasse: '',
    adresse: {
      rue: '',
      ville: '',
      codePostal: '',
      pays: 'France'
    }
  });
  
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [acceptTerms, setAcceptTerms] = useState(false);

  const { register } = useAuth();
  const navigate = useNavigate();

  // Gérer le scroll du body
  useEffect(() => {
    document.body.classList.add('auth-page-active');
    return () => {
      document.body.classList.remove('auth-page-active');
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
    
    // Effacer l'erreur du champ modifié
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // Validation des champs obligatoires
    if (!formData.nom.trim()) {
      newErrors.nom = 'Le nom est requis';
    } else if (formData.nom.trim().length < 2) {
      newErrors.nom = 'Le nom doit contenir au moins 2 caractères';
    }

    if (!formData.prenom.trim()) {
      newErrors.prenom = 'Le prénom est requis';
    } else if (formData.prenom.trim().length < 2) {
      newErrors.prenom = 'Le prénom doit contenir au moins 2 caractères';
    }

    if (!formData.email) {
      newErrors.email = 'L\'email est requis';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'L\'email n\'est pas valide';
    }

    if (!formData.telephone) {
      newErrors.telephone = 'Le téléphone est requis';
    } else if (!/^(\+33|0)[1-9](\d{8})$/.test(formData.telephone.replace(/\s/g, ''))) {
      newErrors.telephone = 'Le numéro de téléphone n\'est pas valide';
    }

    if (!formData.motDePasse) {
      newErrors.motDePasse = 'Le mot de passe est requis';
    } else if (formData.motDePasse.length < 8) {
      newErrors.motDePasse = 'Le mot de passe doit contenir au moins 8 caractères';
    } else if (!/(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(formData.motDePasse)) {
      newErrors.motDePasse = 'Le mot de passe doit contenir au moins une minuscule, une majuscule et un chiffre';
    }

    if (!formData.confirmerMotDePasse) {
      newErrors.confirmerMotDePasse = 'La confirmation du mot de passe est requise';
    } else if (formData.motDePasse !== formData.confirmerMotDePasse) {
      newErrors.confirmerMotDePasse = 'Les mots de passe ne correspondent pas';
    }

    // Validation de l'adresse
    if (!formData.adresse.rue.trim()) {
      newErrors['adresse.rue'] = 'La rue est requise';
    }

    if (!formData.adresse.ville.trim()) {
      newErrors['adresse.ville'] = 'La ville est requise';
    }

    if (!formData.adresse.codePostal) {
      newErrors['adresse.codePostal'] = 'Le code postal est requis';
    } else if (!/^\d{5}$/.test(formData.adresse.codePostal)) {
      newErrors['adresse.codePostal'] = 'Le code postal doit contenir 5 chiffres';
    }

    if (!acceptTerms) {
      newErrors.terms = 'Vous devez accepter les conditions d\'utilisation';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    
    try {
      // Préparer les données pour l'API
      const userData = {
        nom: formData.nom.trim(),
        prenom: formData.prenom.trim(),
        email: formData.email.toLowerCase(),
        telephone: formData.telephone,
        motDePasse: formData.motDePasse,
        adresse: formData.adresse
      };

      await register(userData);
      toast.success('Compte créé avec succès ! Vous pouvez maintenant vous connecter.');
      navigate('/login');
    } catch (error) {
      if (error.response?.data?.message) {
        toast.error(error.response.data.message);
      } else {
        toast.error('Erreur lors de la création du compte');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleSocialRegister = (provider) => {
    toast.info(`Inscription avec ${provider} bientôt disponible !`);
  };

  const getFieldError = (fieldName) => {
    return errors[fieldName] || '';
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-header">
          <h1>Créer un compte</h1>
          <p>Rejoignez notre communauté et profitez de nos offres exclusives</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form register-form">
          {/* Informations personnelles */}
          <div className="form-section">
            <h3>Informations personnelles</h3>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="prenom">Prénom *</label>
                <div className="input-wrapper">
                  <FaUser className="input-icon" />
                  <input
                    type="text"
                    id="prenom"
                    name="prenom"
                    value={formData.prenom}
                    onChange={handleChange}
                    placeholder="Votre prénom"
                    className={getFieldError('prenom') ? 'error' : ''}
                    autoComplete="given-name"
                  />
                </div>
                {getFieldError('prenom') && <span className="error-message">{getFieldError('prenom')}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="nom">Nom *</label>
                <div className="input-wrapper">
                  <FaUser className="input-icon" />
                  <input
                    type="text"
                    id="nom"
                    name="nom"
                    value={formData.nom}
                    onChange={handleChange}
                    placeholder="Votre nom"
                    className={getFieldError('nom') ? 'error' : ''}
                    autoComplete="family-name"
                  />
                </div>
                {getFieldError('nom') && <span className="error-message">{getFieldError('nom')}</span>}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="email">Email *</label>
              <div className="input-wrapper">
                <FaEnvelope className="input-icon" />
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="votre@email.com"
                  className={getFieldError('email') ? 'error' : ''}
                  autoComplete="email"
                />
              </div>
              {getFieldError('email') && <span className="error-message">{getFieldError('email')}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="telephone">Téléphone *</label>
              <div className="input-wrapper">
                <FaPhone className="input-icon" />
                <input
                  type="tel"
                  id="telephone"
                  name="telephone"
                  value={formData.telephone}
                  onChange={handleChange}
                  placeholder="06 12 34 56 78"
                  className={getFieldError('telephone') ? 'error' : ''}
                  autoComplete="tel"
                />
              </div>
              {getFieldError('telephone') && <span className="error-message">{getFieldError('telephone')}</span>}
            </div>
          </div>

          {/* Adresse */}
          <div className="form-section">
            <h3>Adresse de livraison</h3>
            
            <div className="form-group">
              <label htmlFor="adresse.rue">Rue *</label>
              <div className="input-wrapper">
                <FaMapMarkerAlt className="input-icon" />
                <input
                  type="text"
                  id="adresse.rue"
                  name="adresse.rue"
                  value={formData.adresse.rue}
                  onChange={handleChange}
                  placeholder="123 Rue de la Paix"
                  className={getFieldError('adresse.rue') ? 'error' : ''}
                  autoComplete="street-address"
                />
              </div>
              {getFieldError('adresse.rue') && <span className="error-message">{getFieldError('adresse.rue')}</span>}
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="adresse.codePostal">Code postal *</label>
                <div className="input-wrapper">
                  <FaMapMarkerAlt className="input-icon" />
                  <input
                    type="text"
                    id="adresse.codePostal"
                    name="adresse.codePostal"
                    value={formData.adresse.codePostal}
                    onChange={handleChange}
                    placeholder="75001"
                    className={getFieldError('adresse.codePostal') ? 'error' : ''}
                    autoComplete="postal-code"
                  />
                </div>
                {getFieldError('adresse.codePostal') && <span className="error-message">{getFieldError('adresse.codePostal')}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="adresse.ville">Ville *</label>
                <div className="input-wrapper">
                  <FaMapMarkerAlt className="input-icon" />
                  <input
                    type="text"
                    id="adresse.ville"
                    name="adresse.ville"
                    value={formData.adresse.ville}
                    onChange={handleChange}
                    placeholder="Paris"
                    className={getFieldError('adresse.ville') ? 'error' : ''}
                    autoComplete="address-level2"
                  />
                </div>
                {getFieldError('adresse.ville') && <span className="error-message">{getFieldError('adresse.ville')}</span>}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="adresse.pays">Pays</label>
              <div className="input-wrapper">
                <FaMapMarkerAlt className="input-icon" />
                <select
                  id="adresse.pays"
                  name="adresse.pays"
                  value={formData.adresse.pays}
                  onChange={handleChange}
                >
                  <option value="France">France</option>
                  <option value="Belgique">Belgique</option>
                  <option value="Suisse">Suisse</option>
                  <option value="Canada">Canada</option>
                  <option value="Autre">Autre</option>
                </select>
              </div>
            </div>
          </div>

          {/* Sécurité */}
          <div className="form-section">
            <h3>Sécurité</h3>
            
            <div className="form-group">
              <label htmlFor="motDePasse">Mot de passe *</label>
              <div className="input-wrapper">
                <FaLock className="input-icon" />
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="motDePasse"
                  name="motDePasse"
                  value={formData.motDePasse}
                  onChange={handleChange}
                  placeholder="Votre mot de passe"
                  className={getFieldError('motDePasse') ? 'error' : ''}
                  autoComplete="new-password"
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? <FaEyeSlash /> : <FaEye />}
                </button>
              </div>
              {getFieldError('motDePasse') && <span className="error-message">{getFieldError('motDePasse')}</span>}
              <div className="password-requirements">
                <small>Le mot de passe doit contenir au moins 8 caractères, une minuscule, une majuscule et un chiffre.</small>
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="confirmerMotDePasse">Confirmer le mot de passe *</label>
              <div className="input-wrapper">
                <FaLock className="input-icon" />
                <input
                  type={showConfirmPassword ? 'text' : 'password'}
                  id="confirmerMotDePasse"
                  name="confirmerMotDePasse"
                  value={formData.confirmerMotDePasse}
                  onChange={handleChange}
                  placeholder="Confirmez votre mot de passe"
                  className={getFieldError('confirmerMotDePasse') ? 'error' : ''}
                  autoComplete="new-password"
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? <FaEyeSlash /> : <FaEye />}
                </button>
              </div>
              {getFieldError('confirmerMotDePasse') && <span className="error-message">{getFieldError('confirmerMotDePasse')}</span>}
            </div>
          </div>

          {/* Conditions */}
          <div className="form-section">
            <label className="checkbox-wrapper terms-checkbox">
              <input 
                type="checkbox" 
                id="terms" 
                checked={acceptTerms}
                onChange={(e) => setAcceptTerms(e.target.checked)}
              />
              <span className="checkmark"></span>
              J'accepte les{' '}
              <Link to="/terms" className="link" target="_blank">
                conditions d'utilisation
              </Link>
              {' '}et la{' '}
              <Link to="/privacy" className="link" target="_blank">
                politique de confidentialité
              </Link>
            </label>
            {getFieldError('terms') && <span className="error-message">{getFieldError('terms')}</span>}
          </div>

          <button
            type="submit"
            className="submit-btn primary"
            disabled={loading}
          >
            {loading ? 'Création du compte...' : 'Créer mon compte'}
          </button>
        </form>

        <div className="divider">
          <span>ou</span>
        </div>

        <div className="social-login">
          <button
            type="button"
            className="social-btn google"
            onClick={() => handleSocialRegister('Google')}
          >
            <FaGoogle />
            Continuer avec Google
          </button>
          
          <button
            type="button"
            className="social-btn facebook"
            onClick={() => handleSocialRegister('Facebook')}
          >
            <FaFacebook />
            Continuer avec Facebook
          </button>
        </div>

        <div className="auth-footer">
          <p>
            Déjà un compte ?{' '}
            <Link to="/login" className="link">
              Se connecter
            </Link>
          </p>
        </div>
      </div>

      <div className="auth-illustration">
        <div className="illustration-content">
          <h2>Rejoignez notre communauté</h2>
          <p>Créez votre compte et bénéficiez de nombreux avantages exclusifs.</p>
          <div className="features">
            <div className="feature">
              <div className="feature-icon">🎁</div>
              <span>Offres exclusives</span>
            </div>
            <div className="feature">
              <div className="feature-icon">🚚</div>
              <span>Livraison rapide</span>
            </div>
            <div className="feature">
              <div className="feature-icon">💎</div>
              <span>Programme fidélité</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
