import { useState } from "react";

export default function HoodieCustomizer() {
  const [color, setColor] = useState("#ffffff"); // couleur par défaut
  const [logo, setLogo] = useState(null);

  // Fonction pour calculer la rotation de teinte
  const getHueRotation = (hexColor) => {
    if (hexColor === "#ffffff") return 0;
    
    // Convertir hex en RGB
    const hex = hexColor.replace('#', '');
    const r = parseInt(hex.substr(0, 2), 16);
    const g = parseInt(hex.substr(2, 2), 16);
    const b = parseInt(hex.substr(4, 2), 16);
    
    // Calculer la teinte
    const max = Math.max(r, g, b);
    const min = Math.min(r, g, b);
    let hue = 0;
    
    if (max !== min) {
      const d = max - min;
      if (max === r) hue = ((g - b) / d) % 6;
      else if (max === g) hue = (b - r) / d + 2;
      else hue = (r - g) / d + 4;
    }
    
    return Math.round(hue * 60);
  };

  // Charger logo
  const handleLogoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => setLogo(event.target.result);
      reader.readAsDataURL(file);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Personnalise ton hoodie</h2>

      {/* Zone de preview */}
      <div style={{ position: "relative", display: "inline-block" }}>
        {/* Hoodie de base */}
        <img
          src="/hoodie-blanc.jpg" // Votre image de capuche blanche
          alt="Hoodie"
          style={{
            maxWidth: "400px",
            filter: `drop-shadow(0 0 5px #000) hue-rotate(${getHueRotation(color)}deg) saturate(1.2) brightness(1.1)`,
          }}
        />

        {/* Logo par-dessus */}
        {logo && (
          <img
            src={logo}
            alt="Logo"
            style={{
              position: "absolute",
              top: "40%",
              left: "50%",
              width: "100px",
              transform: "translate(-50%, -50%)",
              pointerEvents: "none",
            }}
          />
        )}
      </div>

      {/* Contrôles */}
      <div style={{ marginTop: "20px" }}>
        <label>
          Choisir couleur :
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            style={{ marginLeft: "10px" }}
          />
        </label>

        <br /><br />

        <label>
          Ajouter logo :
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            style={{ marginLeft: "10px" }}
          />
        </label>
      </div>
    </div>
  );
}
