import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useOrders } from '../contexts/OrdersContext';
import { FaBox, FaClock, FaCheck, FaTruck, FaTimes, FaEye, FaSearch, FaFilter, FaShoppingBag, FaCalendarAlt, FaMapMarkerAlt, FaCreditCard } from 'react-icons/fa';
import '../styles/OrdersPage.css';

const Orders = () => {
  const { orders, loading, error, fetchOrders, getOrdersCount } = useOrders();
  const [loadingTimeout, setLoadingTimeout] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  // Le chargement initial est gÃ©rÃ© par OrdersContext

  // Timeout de sÃ©curitÃ© sÃ©parÃ©
  useEffect(() => {
    if (loading) {
      console.log('â±ï¸ DÃ©marrage du timeout de sÃ©curitÃ©');
      const timeout = setTimeout(() => {
        console.log('âš ï¸ Timeout de chargement dÃ©tectÃ©');
        setLoadingTimeout(true);
      }, 10000); // 10 secondes

      return () => {
        console.log('ðŸ§¹ Nettoyage du timeout');
        clearTimeout(timeout);
      };
    } else {
      // Reset le timeout si le chargement se termine
      setLoadingTimeout(false);
    }
  }, [loading]);

  // Debug des donnÃ©es
  useEffect(() => {
    console.log('ðŸ” Orders - Ã‰tat des commandes:', {
      orders,
      loading,
      error,
      count: getOrdersCount(),
      ordersLength: orders?.length,
      ordersType: typeof orders,
      ordersIsArray: Array.isArray(orders)
    });
  }, [orders, loading, error, getOrdersCount]);

  const getStatusIcon = (status) => {
    const statusIcons = {
      'en_attente': <FaClock className="text-yellow-500" />,
      'confirmee': <FaCheck className="text-blue-500" />,
      'en_preparation': <FaBox className="text-orange-500" />,
      'expediee': <FaTruck className="text-purple-500" />,
      'livree': <FaCheck className="text-green-500" />,
      'annulee': <FaTimes className="text-red-500" />
    };
    return statusIcons[status] || <FaClock className="text-gray-500" />;
  };

  const getStatusLabel = (status) => {
    const statusLabels = {
      'en_attente': 'En attente',
      'confirmee': 'ConfirmÃ©e',
      'en_preparation': 'En prÃ©paration',
      'expediee': 'ExpÃ©diÃ©e',
      'livree': 'LivrÃ©e',
      'annulee': 'AnnulÃ©e'
    };
    return statusLabels[status] || status;
  };

  const getStatusColor = (status) => {
    const statusColors = {
      'en_attente': 'bg-yellow-100 text-yellow-800',
      'confirmee': 'bg-blue-100 text-blue-800',
      'en_preparation': 'bg-orange-100 text-orange-800',
      'expediee': 'bg-purple-100 text-purple-800',
      'livree': 'bg-green-100 text-green-800',
      'annulee': 'bg-red-100 text-red-800'
    };
    return statusColors[status] || 'bg-gray-100 text-gray-800';
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Fonctions de filtrage et recherche
  const filteredOrders = orders.filter(order => {
    const matchesSearch = searchTerm === '' || 
      order._id.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.articles?.some(article => 
        article.nom.toLowerCase().includes(searchTerm.toLowerCase())
      );
    
    const matchesStatus = statusFilter === 'all' || order.statut === statusFilter;
    
    return matchesSearch && matchesStatus;
  });

  const getStatusInfo = (status) => {
    const statusConfig = {
      'en_attente': { 
        label: 'En attente', 
        icon: <FaClock />, 
        className: 'status-pending' 
      },
      'confirmee': { 
        label: 'ConfirmÃ©e', 
        icon: <FaCheck />, 
        className: 'status-confirmed' 
      },
      'en_preparation': { 
        label: 'En prÃ©paration', 
        icon: <FaBox />, 
        className: 'status-preparing' 
      },
      'expediee': { 
        label: 'ExpÃ©diÃ©e', 
        icon: <FaTruck />, 
        className: 'status-shipped' 
      },
      'livree': { 
        label: 'LivrÃ©e', 
        icon: <FaCheck />, 
        className: 'status-delivered' 
      },
      'annulee': { 
        label: 'AnnulÃ©e', 
        icon: <FaTimes />, 
        className: 'status-cancelled' 
      }
    };
    return statusConfig[status] || { 
      label: status, 
      icon: <FaClock />, 
      className: 'status-pending' 
    };
  };

  if (loading && !loadingTimeout) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            <div className="ml-4">
              <p className="text-gray-600">Chargement de vos commandes...</p>
              <p className="text-sm text-gray-500 mt-2">Cela peut prendre quelques secondes</p>
              <button
                onClick={() => {
                  console.log('ðŸ”„ Forcer l\'arrÃªt du chargement');
                  setLoadingTimeout(true);
                }}
                className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                ArrÃªter le chargement
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Si timeout de chargement
  if (loadingTimeout && loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
            <div className="flex">
              <div className="ml-3 flex-1">
                <h3 className="text-sm font-medium text-yellow-800">Chargement lent dÃ©tectÃ©</h3>
                <div className="mt-2 text-sm text-yellow-700">
                  Le chargement prend plus de temps que prÃ©vu. Cela peut Ãªtre dÃ» Ã  :
                  <ul className="mt-2 list-disc list-inside">
                    <li>ProblÃ¨me de connexion au serveur</li>
                    <li>Token d'authentification expirÃ©</li>
                    <li>Serveur surchargÃ©</li>
                  </ul>
                </div>
                <div className="mt-4 space-x-3">
                  <button
                    onClick={() => {
                      setLoadingTimeout(false);
                      fetchOrders();
                    }}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-yellow-600 hover:bg-yellow-700"
                  >
                    ðŸ”„ RÃ©essayer
                  </button>
                  <button
                    onClick={() => {
                      localStorage.removeItem('token');
                      window.location.href = '/login';
                    }}
                    className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                  >
                    ðŸ”‘ Se reconnecter
                  </button>
                  <button
                    onClick={() => {
                      console.log('ðŸ” DIAGNOSTIC COMPLET:');
                      console.log('Token:', localStorage.getItem('token') ? 'PrÃ©sent' : 'Absent');
                      console.log('User:', localStorage.getItem('user'));
                      console.log('Timestamp:', new Date().toISOString());
                      
                      // Test direct du serveur
                      fetch('http://localhost:5000/api/orders', {
                        headers: {
                          'x-auth-token': localStorage.getItem('token') || 'no-token'
                        }
                      })
                      .then(response => {
                        console.log('ðŸ“Š Test serveur:', response.status, response.statusText);
                        return response.text();
                      })
                      .then(data => {
                        console.log('ðŸ“„ RÃ©ponse serveur:', data.substring(0, 200));
                      })
                      .catch(error => {
                        console.log('âŒ Erreur test serveur:', error.message);
                      });
                    }}
                    className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                  >
                    ðŸ” Diagnostic
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-red-50 border border-red-200 rounded-md p-4">
            <div className="flex">
              <FaTimes className="h-5 w-5 text-red-400" />
              <div className="ml-3 flex-1">
                <h3 className="text-sm font-medium text-red-800">Erreur</h3>
                <div className="mt-2 text-sm text-red-700">{error}</div>
                <div className="mt-4">
                  <button
                    onClick={() => {
                      console.log('ðŸ”„ Rechargement forcÃ©...');
                      fetchOrders();
                    }}
