import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaPlus, FaEdit, FaTrash, FaEye, FaUsers, FaBox, FaShoppingCart, FaChartLine, FaCog, FaSignOutAlt, FaTimes } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import { useSocket } from '../contexts/SocketContext';
import { formatBrand } from '../utils/formatUtils';
import api from '../config/axios';
import '../styles/Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({});
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [usersPagination, setUsersPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    pages: 0
  });
  const [usersSearch, setUsersSearch] = useState('');
  const [usersRoleFilter, setUsersRoleFilter] = useState('');

  const { user, logout } = useAuth();
  const { socket, isConnected, joinAdminRoom, leaveAdminRoom } = useSocket();
  const navigate = useNavigate();

  // Fonction pour mettre Ã  jour les statistiques basÃ©es sur le nombre de produits
  const updateStatsFromProducts = (newProductCount) => {
    setStats(prevStats => ({
      ...prevStats,
      totalProducts: newProductCount
    }));
    console.log('ðŸ“Š Statistiques mises Ã  jour localement - Produits:', newProductCount);
  };

  useEffect(() => {
    if (user && user.role === 'admin') {
      fetchDashboardData();
      // Rejoindre la room admin pour les mises Ã  jour en temps rÃ©el
      joinAdminRoom();
    }
  }, [user, joinAdminRoom]);

  // Quitter la room admin quand le composant est dÃ©montÃ©
  useEffect(() => {
    return () => {
      leaveAdminRoom();
    };
  }, [leaveAdminRoom]);

  // Ã‰couter les Ã©vÃ©nements WebSocket
  useEffect(() => {
    if (!socket || !isConnected) return;

    // Ã‰vÃ©nement quand un produit est supprimÃ©
    socket.on('product-deleted', (data) => {
      console.log('Produit supprimÃ© via WebSocket:', data);
      setProducts(prev => {
        const newProducts = prev.filter(p => p._id !== data.productId);
        // Mettre Ã  jour les statistiques avec le nouveau nombre
        updateStatsFromProducts(newProducts.length);
        return newProducts;
      });
      toast.info(`Produit "${data.productName}" supprimÃ© par ${data.deletedBy}`);
    });

    // Ã‰vÃ©nement quand un produit est ajoutÃ©
    socket.on('product-added', (data) => {
      console.log('Produit ajoutÃ© via WebSocket:', data);
      setProducts(prev => {
        const newProducts = [data.product, ...prev];
        // Mettre Ã  jour les statistiques avec le nouveau nombre
        updateStatsFromProducts(newProducts.length);
        return newProducts;
      });
      toast.info(`Nouveau produit "${data.product.nom}" ajoutÃ© par ${data.addedBy}`);
    });

    // Ã‰vÃ©nement quand un produit est mis Ã  jour
    socket.on('product-updated', (data) => {
      console.log('Produit mis Ã  jour via WebSocket:', data);
      setProducts(prev => prev.map(p => p._id === data.product._id ? data.product : p));
      // Pas besoin de mettre Ã  jour le nombre pour une mise Ã  jour
      toast.info(`Produit "${data.product.nom}" mis Ã  jour par ${data.updatedBy}`);
    });

    // Ã‰vÃ©nement pour les statistiques mises Ã  jour en temps rÃ©el
    socket.on('stats-updated', (data) => {
      console.log('ðŸ“Š Statistiques reÃ§ues via WebSocket:', data);
      console.log('ðŸ“¦ Nombre de produits reÃ§u:', data.totalProducts);
      setStats(data);
      toast.info(`Statistiques mises Ã  jour: ${data.totalProducts} produits`, { autoClose: 2000 });
    });

    // Cleanup
    return () => {
      socket.off('product-deleted');
      socket.off('product-added');
      socket.off('product-updated');
      socket.off('stats-updated');
    };
  }, [socket, isConnected]);

  const handleLogout = () => {
    if (window.confirm('ÃŠtes-vous sÃ»r de vouloir vous dÃ©connecter ?')) {
      // Quitter la room admin avant de se dÃ©connecter
      leaveAdminRoom();
      logout();
      navigate('/');
      toast.success('DÃ©connexion rÃ©ussie');
    }
  };

  // Charger les utilisateurs quand on clique sur l'onglet Utilisateurs
  useEffect(() => {
    if (activeTab === 'users' && user && user.role === 'admin') {
      fetchUsers(1, 20, '', '');
    }
  }, [activeTab, user]);

  // Charger toutes les commandes quand on clique sur l'onglet Commandes
  useEffect(() => {
    if (activeTab === 'orders' && user && user.role === 'admin') {
      fetchAllOrders();
    }
  }, [activeTab, user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      // Charger les statistiques
      const statsResponse = await fetch('/api/admin/stats', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        console.log('ðŸ“Š Statistiques initiales chargÃ©es:', statsData);
        console.log('ðŸ“¦ Nombre de produits initial:', statsData.totalProducts);
        setStats(statsData);
      }

      // Charger les produits
      console.log('Chargement des produits...');
      try {
        const productsResponse = await api.get('/api/products?limit=100'); // Charger plus de produits pour avoir le vrai total
        console.log('DonnÃ©es produits reÃ§ues:', productsResponse.data);
        const loadedProducts = productsResponse.data.produits || productsResponse.data.products || [];
        console.log('Nombre de produits chargÃ©s:', loadedProducts.length);
        setProducts(loadedProducts);

        // Synchroniser les statistiques avec le nombre rÃ©el de produits
        if (loadedProducts.length > 0) {
          setStats(prevStats => ({
            ...prevStats,
            totalProducts: loadedProducts.length
          }));
          console.log('ðŸ“Š Statistiques synchronisÃ©es - Produits:', loadedProducts.length);
        }
      } catch (error) {
        console.error('Erreur lors du chargement des produits:', error);
      }

      // Charger les commandes rÃ©centes (toutes les commandes pour l'admin)
      try {
        const ordersResponse = await api.get('/api/orders/admin/toutes?limit=10');
        setOrders(ordersResponse.data.commandes || ordersResponse.data.orders || []);
      } catch (error) {
        console.error('Erreur lors du chargement des commandes:', error);
      }

      // Charger les utilisateurs rÃ©cents pour le dashboard
      await fetchUsers(1, 10);

    } catch (error) {
      toast.error('Erreur lors du chargement des donnÃ©es');
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async (page = 1, limit = 20, search = '', role = '') => {
    try {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: limit.toString()
      });

      if (search) params.append('recherche', search);
      if (role) params.append('role', role);

      const response = await fetch(`/api/users/admin/tous?${params}`, {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUsers(data.utilisateurs);
        setUsersPagination(data.pagination);
      } else {
        toast.error('Erreur lors du chargement des utilisateurs');
      }
    } catch (error) {
      toast.error('Erreur lors du chargement des utilisateurs');
    }
  };

  const fetchAllOrders = async () => {
    try {
      setLoading(true);
      const response = await api.get('/api/orders/admin/toutes');
      setOrders(response.data.commandes || response.data.orders || []);
    } catch (error) {
      console.error('Erreur lors du chargement des commandes:', error);
      toast.error('Erreur lors du chargement des commandes');
    } finally {
      setLoading(false);
    }
  };


  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-page">
        <div className="admin-container">
          <div className="access-denied">
            <h1>AccÃ¨s refusÃ©</h1>
            <p>Vous n'avez pas les permissions nÃ©cessaires pour accÃ©der Ã  cette page.</p>
            <Link to="/" className="btn-primary">
              Retour Ã  l'accueil
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement du tableau de bord...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-layout">
        {/* Sidebar */}
        <div className="admin-sidebar">
          <div className="sidebar-header">
            <h2>Administration</h2>
            <p>SHADOWHOOD</p>
            <div className="connection-status">
              <span className={`status-indicator ${isConnected ? 'connected' : 'disconnected'}`}>
                {isConnected ? 'ðŸŸ¢ Temps rÃ©el' : 'ðŸ”´ Hors ligne'}
              </span>
            </div>
          </div>

          <nav className="sidebar-nav">
            <button
              className={`sidebar-item ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={() => setActiveTab('dashboard')}
            >
              <FaChartLine className="sidebar-icon" />
              <span>Tableau de bord</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'products' ? 'active' : ''}`}
              onClick={() => setActiveTab('products')}
            >
              <FaBox className="sidebar-icon" />
              <span>Produits</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'orders' ? 'active' : ''}`}
              onClick={() => setActiveTab('orders')}
            >
              <FaShoppingCart className="sidebar-icon" />
              <span>Commandes</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'users' ? 'active' : ''}`}
              onClick={() => setActiveTab('users')}
            >
              <FaUsers className="sidebar-icon" />
              <span>Utilisateurs</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'settings' ? 'active' : ''}`}
              onClick={() => setActiveTab('settings')}
            >
              <FaCog className="sidebar-icon" />
              <span>ParamÃ¨tres</span>
            </button>
          </nav>

          {/* Bouton de dÃ©connexion */}
          <div className="sidebar-footer">
            <button
              className="sidebar-item logout-btn"
              onClick={handleLogout}
            >
              <FaSignOutAlt className="sidebar-icon" />
              <span>DÃ©connexion</span>
            </button>
          </div>
        </div>

        {/* Contenu principal */}
        <div className="admin-main">
          <div className="admin-content">
          {activeTab === 'dashboard' && (
            <DashboardTab stats={stats} onRefresh={fetchDashboardData} />
          )}

          {activeTab === 'products' && (
            <ProductsTab 
              products={products} 
              onProductAdded={(newProduct) => {
                setProducts(prev => [...(prev || []), newProduct]);
              }}
            />
          )}

          {activeTab === 'orders' && (
            <OrdersTab orders={orders} />
          )}

          {activeTab === 'users' && (
            <UsersTab 
              users={users}
              pagination={usersPagination}
            />
          )}

          {activeTab === 'settings' && (
            <SettingsTab />
          )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Tableau de bord
const DashboardTab = ({ stats, onRefresh }) => {
  return (
    <div className="dashboard-tab">
      <div className="simple-stats">
        <div className="stats-header">
          <h2>Statistiques de la boutique</h2>
          <div className="stats-controls">
            <div className="realtime-indicator">
              <div className="pulse-dot"></div>
              <span>Mise Ã  jour en temps rÃ©el</span>
            </div>
            <button 
              className="refresh-stats-btn"
              onClick={onRefresh}
              title="RafraÃ®chir les statistiques"
            >
              <FaChartLine />
            </button>
          </div>
        </div>

        <div className="stats-grid">
          <div className="stat-card">
            <FaUsers className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalUsers || 0}</h3>
              <p>Utilisateurs</p>
            </div>
          </div>

          <div className="stat-card">
            <FaBox className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalProducts || 0}</h3>
              <p>Produits</p>
            </div>
          </div>

          <div className="stat-card">
            <FaShoppingCart className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalOrders || 0}</h3>
              <p>Commandes</p>
            </div>
          </div>

          <div className="stat-card">
            <FaChartLine className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.chiffreAffaires || 0}â‚¬</h3>
              <p>Chiffre d'affaires</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Produits
const ProductsTab = ({ products, onProductAdded }) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showProductDetails, setShowProductDetails] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [productsList, setProductsList] = useState(products || []);
  const { user } = useAuth();

  // Log pour dÃ©bogage
  console.log('ProductsTab - Produits reÃ§us:', products);
  console.log('ProductsTab - Nombre de produits:', products?.length || 0);

  // Mettre Ã  jour la liste des produits quand les props changent
  useEffect(() => {
    setProductsList(products || []);
  }, [products]);

  // Fonction pour supprimer un produit
