// Composant EditProductForm
const EditProductForm = ({ product, onClose, onProductUpdated }) => {
  const [formData, setFormData] = useState({
    nom: product.nom || '',
    description: product.description || '',
    prix: product.prix || 0,
    prixReduit: product.prixReduit || '',
    categorie: product.categorie || '',
    marque: product.marque || '',
    materiau: product.materiau || '',
    entretien: product.entretien || '',
    tailles: product.tailles ? product.tailles.map(taille => ({
      nom: taille.nom || taille.taille || '',
      stock: taille.stock || 0
    })) : [],
    couleurs: product.couleurs ? product.couleurs.map(couleur => ({
      nom: couleur.nom || '',
      code: couleur.code || ''
    })) : []
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Effacer l'erreur pour ce champ
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSizeChange = (index, field, value) => {
    const newTailles = [...formData.tailles];
    newTailles[index] = {
      ...newTailles[index],
      [field]: value
    };
    setFormData(prev => ({
      ...prev,
      tailles: newTailles
    }));
  };

  const addSize = () => {
    console.log('Ajout d\'une nouvelle taille...');
    console.log('Tailles actuelles:', formData.tailles);

    setFormData(prev => {
      const newTailles = [...prev.tailles, { nom: '', stock: 0 }];
      console.log('Nouvelles tailles:', newTailles);
      return {
        ...prev,
        tailles: newTailles
      };
    });
  };

  const removeSize = (index) => {
    setFormData(prev => ({
      ...prev,
      tailles: prev.tailles.filter((_, i) => i !== index)
    }));
  };

  const handleColorChange = (index, field, value) => {
    const newCouleurs = [...formData.couleurs];
    newCouleurs[index] = {
      ...newCouleurs[index],
      [field]: value
    };
    setFormData(prev => ({
      ...prev,
      couleurs: newCouleurs
    }));
  };

  const addColor = () => {
    setFormData(prev => ({
      ...prev,
      couleurs: [...prev.couleurs, { nom: '', code: '' }]
    }));
  };

  const removeColor = (index) => {
    setFormData(prev => ({
      ...prev,
      couleurs: prev.couleurs.filter((_, i) => i !== index)
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.nom.trim()) newErrors.nom = 'Le nom est requis';
    if (!formData.description.trim()) newErrors.description = 'La description est requise';
    if (!formData.prix || formData.prix <= 0) newErrors.prix = 'Le prix doit Ãªtre positif';
    if (!formData.categorie) newErrors.categorie = 'La catÃ©gorie est requise';
    if (!formData.marque.trim()) newErrors.marque = 'La marque est requise';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      // PrÃ©parer les donnÃ©es pour l'API
      const submitData = {
        ...formData,
        prix: parseFloat(formData.prix),
        prixReduit: formData.prixReduit ? parseFloat(formData.prixReduit) : null
      };

      console.log('Mise Ã  jour du produit:', product._id, submitData);

      const response = await api.put(`/api/products/${product._id}`, submitData);

      if (response.status === 200) {
        console.log('Produit mis Ã  jour avec succÃ¨s:', response.data);
        onProductUpdated(response.data);
      } else {
        throw new Error('Erreur lors de la mise Ã  jour');
      }
    } catch (error) {
      console.error('Erreur lors de la mise Ã  jour du produit:', error);
      toast.error('Erreur lors de la mise Ã  jour du produit');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content large-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Modifier le produit</h3>
          <button className="close-btn" onClick={onClose}>
            <FaTimes />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="product-form">
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="nom">Nom du produit *</label>
              <input
                type="text"
                id="nom"
                name="nom"
                value={formData.nom}
                onChange={handleChange}
                className={errors.nom ? 'error' : ''}
                required
              />
              {errors.nom && <span className="error-message">{errors.nom}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="marque">Marque *</label>
              <input
                type="text"
                id="marque"
                name="marque"
                value={formData.marque}
                onChange={handleChange}
                className={errors.marque ? 'error' : ''}
                required
              />
              {errors.marque && <span className="error-message">{errors.marque}</span>}
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="prix">Prix (â‚¬) *</label>
              <input
                type="number"
                id="prix"
                name="prix"
                value={formData.prix}
                onChange={handleChange}
                min="0"
                step="0.01"
                className={errors.prix ? 'error' : ''}
                required
              />
              {errors.prix && <span className="error-message">{errors.prix}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="prixReduit">Prix rÃ©duit (â‚¬)</label>
              <input
                type="number"
                id="prixReduit"
                name="prixReduit"
                value={formData.prixReduit}
                onChange={handleChange}
                min="0"
                step="0.01"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="categorie">CatÃ©gorie *</label>
              <select
                id="categorie"
                name="categorie"
                value={formData.categorie}
                onChange={handleChange}
                className={errors.categorie ? 'error' : ''}
                required
              >
                <option value="">SÃ©lectionner une catÃ©gorie</option>
                <option value="hauts">Hauts</option>
                <option value="bas">Bas</option>
                <option value="robes">Robes</option>
                <option value="vestes">Vestes</option>
                <option value="chaussures">Chaussures</option>
                <option value="accessoires">Accessoires</option>
              </select>
              {errors.categorie && <span className="error-message">{errors.categorie}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="materiau">MatiÃ¨re</label>
              <input
                type="text"
                id="materiau"
                name="materiau"
                value={formData.materiau}
                onChange={handleChange}
                placeholder="Ex: Coton, Polyester..."
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="description">Description *</label>
            <textarea
              id="description"
              name="description"
              value={formData.description}
              onChange={handleChange}
              rows="4"
              className={errors.description ? 'error' : ''}
              required
            />
            {errors.description && <span className="error-message">{errors.description}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="entretien">Instructions d'entretien</label>
            <input
              type="text"
              id="entretien"
              name="entretien"
              value={formData.entretien}
              onChange={handleChange}
              placeholder="Ex: Lavage machine 30Â°C..."
            />
          </div>

          {/* Gestion des tailles */}
          <div className="form-group">
            <label>Tailles disponibles ({formData.tailles.length})</label>

            {/* Liste des tailles actuelles */}
            {formData.tailles.map((taille, index) => (
              <div key={`taille-${index}-${taille.nom || 'new'}`} className="size-color-item">
                <span className="size-label">{taille.nom || 'Nouvelle taille'}</span>
                <input
                  type="number"
                  placeholder="Stock"
                  value={taille.stock || 0}
                  onChange={(e) => handleSizeChange(index, 'stock', parseInt(e.target.value) || 0)}
                  min="0"
                  className="stock-input"
                />
                <button type="button" onClick={() => removeSize(index)} className="remove-btn">
                  <FaTimes />
                </button>
              </div>
            ))}

            {/* SÃ©lecteur de nouvelles tailles */}
            <div className="size-selector">
              <label>Ajouter une taille :</label>
              <select 
                id="newSizeSelect" 
                className="size-select"
                onChange={(e) => {
                  if (e.target.value) {
                    addSizeFromSelect(e.target.value);
                    e.target.value = ''; // Reset le select
                  }
                }}
              >
                <option value="">Choisir une taille...</option>
                <option value="XXS">XXS</option>
                <option value="XS">XS</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
                <option value="XXL">XXL</option>
                <option value="XXXL">XXXL</option>
                <option value="28">28</option>
                <option value="30">30</option>
                <option value="32">32</option>
                <option value="34">34</option>
                <option value="36">36</option>
                <option value="38">38</option>
                <option value="40">40</option>
                <option value="42">42</option>
                <option value="44">44</option>
                <option value="46">46</option>
                <option value="48">48</option>
                <option value="50">50</option>
                <option value="52">52</option>
              </select>
            </div>
          </div>

          {/* Gestion des couleurs */}
          <div className="form-group">
            <label>Couleurs disponibles</label>
            {formData.couleurs.map((couleur, index) => (
              <div key={index} className="size-color-item">
                <input
                  type="text"
                  placeholder="Nom de la couleur"
                  value={couleur.nom}
                  onChange={(e) => handleColorChange(index, 'nom', e.target.value)}
                />
                <input
                  type="text"
                  placeholder="Code couleur (ex: #000000)"
                  value={couleur.code}
                  onChange={(e) => handleColorChange(index, 'code', e.target.value)}
                />
                <button type="button" onClick={() => removeColor(index)} className="remove-btn">
                  <FaTimes />
                </button>
              </div>
            ))}
            <button type="button" onClick={addColor} className="add-btn">
              <FaPlus /> Ajouter une couleur
            </button>
          </div>

          <div className="modal-footer">
            <button type="button" className="btn-secondary" onClick={onClose}>
              Annuler
            </button>
            <button type="submit" className="btn-primary" disabled={loading}>
              {loading ? 'Modification...' : 'Modifier le produit'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Admin;
