import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaCog, FaTimes } from 'react-icons/fa';

const AdminSetupButton = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [adminExists, setAdminExists] = useState(true);

  useEffect(() => {
    checkAdminExists();
  }, []);

  const checkAdminExists = async () => {
    try {
      const response = await fetch('/api/admin/check');
      const data = await response.json();
      setAdminExists(data.exists);
      setIsVisible(!data.exists); // Afficher seulement si aucun admin n'existe
    } catch (error) {
      console.error('Erreur lors de la vérification admin:', error);
    }
  };

  const handleClose = () => {
    setIsVisible(false);
  };

  if (!isVisible || adminExists) {
    return null;
  }

  return (
    <div className="fixed bottom-6 right-6 z-50">
      <div className="bg-orange-500 text-white p-4 rounded-lg shadow-lg max-w-sm animate-pulse">
        <div className="flex items-start justify-between mb-2">
          <div className="flex items-center gap-2">
            <FaCog className="text-lg" />
            <h3 className="font-semibold">Configuration Requise</h3>
          </div>
          <button
            onClick={handleClose}
            className="text-white hover:text-gray-200 transition-colors"
          >
            <FaTimes className="text-sm" />
          </button>
        </div>
        <p className="text-sm mb-3 text-orange-100">
          Créez votre compte administrateur pour gérer votre boutique.
        </p>
        <Link
          to="/admin/setup"
          className="inline-flex items-center gap-2 bg-white text-orange-500 px-3 py-2 rounded text-sm font-medium hover:bg-orange-50 transition-colors"
        >
          <FaCog />
          Configurer Admin
        </Link>
      </div>
    </div>
  );
};

export default AdminSetupButton;
