import React, { useState } from 'react';

const OrdersTest = () => {
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  const testOrdersAPI = async () => {
    setLoading(true);
    setResult('Test en cours...\n');
    
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setResult('‚ùå Pas de token trouv√© - Veuillez vous connecter');
        return;
      }
      
      setResult(prev => prev + `üîë Token trouv√©: ${token.substring(0, 20)}...\n`);
      
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 5000);
      
      const response = await fetch('http://localhost:5000/api/orders', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': token
        },
        signal: controller.signal
      });
      
      clearTimeout(timeoutId);
      
      setResult(prev => prev + `üìä Statut: ${response.status}\n`);
      
      if (response.ok) {
        const data = await response.json();
        setResult(prev => prev + `‚úÖ ${data.length} commande(s) trouv√©e(s)\n`);
        setResult(prev => prev + `üìÑ Donn√©es: ${JSON.stringify(data, null, 2)}\n`);
      } else {
        const errorData = await response.text();
        setResult(prev => prev + `‚ùå Erreur: ${errorData}\n`);
      }
      
    } catch (error) {
      if (error.name === 'AbortError') {
        setResult(prev => prev + '‚è±Ô∏è Timeout (5 secondes)\n');
      } else {
        setResult(prev => prev + `‚ùå Erreur: ${error.message}\n`);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px', border: '1px solid #ccc', margin: '20px' }}>
      <h3>üß™ Test des Commandes</h3>
      <button 
        onClick={testOrdersAPI} 
        disabled={loading}
        style={{ 
          padding: '10px 20px', 
          backgroundColor: loading ? '#ccc' : '#007bff', 
          color: 'white', 
          border: 'none', 
          borderRadius: '4px',
          cursor: loading ? 'not-allowed' : 'pointer'
        }}
      >
        {loading ? 'Test en cours...' : 'Tester l\'API'}
      </button>
      
      <div style={{ 
        marginTop: '20px', 
        padding: '10px', 
        backgroundColor: '#f8f9fa', 
        border: '1px solid #dee2e6',
        borderRadius: '4px',
        fontFamily: 'monospace',
        whiteSpace: 'pre-wrap'
      }}>
        {result}
      </div>
    </div>
  );
};

export default OrdersTest;
