import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useOrders } from '../contexts/OrdersContext';
import { FaBox, FaClock, FaCheck, FaTruck, FaTimes, FaEye, FaSearch, FaFilter, FaShoppingBag, FaCalendarAlt, FaMapMarkerAlt, FaCreditCard } from 'react-icons/fa';
import '../styles/OrdersPage.css';

const Orders = () => {
  const { orders, loading, error, fetchOrders, getOrdersCount } = useOrders();
  const [loadingTimeout, setLoadingTimeout] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  // Le chargement initial est g√©r√© par OrdersContext

  // Timeout de s√©curit√© s√©par√©
  useEffect(() => {
    if (loading) {
      console.log('‚è±Ô∏è D√©marrage du timeout de s√©curit√©');
      const timeout = setTimeout(() => {
        console.log('‚ö†Ô∏è Timeout de chargement d√©tect√©');
        setLoadingTimeout(true);
      }, 10000); // 10 secondes

      return () => {
        console.log('üßπ Nettoyage du timeout');
        clearTimeout(timeout);
      };
    } else {
      // Reset le timeout si le chargement se termine
      setLoadingTimeout(false);
    }
  }, [loading]);

  // Debug des donn√©es
  useEffect(() => {
    console.log('üîç Orders - √âtat des commandes:', {
      orders,
      loading,
      error,
      count: getOrdersCount(),
      ordersLength: orders?.length,
      ordersType: typeof orders,
      ordersIsArray: Array.isArray(orders)
    });
  }, [orders, loading, error, getOrdersCount]);

  const getStatusIcon = (status) => {
    const statusIcons = {
      'en_attente': <FaClock className="text-yellow-500" />,
      'confirmee': <FaCheck className="text-blue-500" />,
      'en_preparation': <FaBox className="text-orange-500" />,
      'expediee': <FaTruck className="text-purple-500" />,
      'livree': <FaCheck className="text-green-500" />,
      'annulee': <FaTimes className="text-red-500" />
    };
    return statusIcons[status] || <FaClock className="text-gray-500" />;
  };

  const getStatusLabel = (status) => {
    const statusLabels = {
      'en_attente': 'En attente',
      'confirmee': 'Confirm√©e',
      'en_preparation': 'En pr√©paration',
      'expediee': 'Exp√©di√©e',
      'livree': 'Livr√©e',
      'annulee': 'Annul√©e'
    };
    return statusLabels[status] || status;
  };

  const getStatusColor = (status) => {
    const statusColors = {
      'en_attente': 'bg-yellow-100 text-yellow-800',
      'confirmee': 'bg-blue-100 text-blue-800',
      'en_preparation': 'bg-orange-100 text-orange-800',
      'expediee': 'bg-purple-100 text-purple-800',
      'livree': 'bg-green-100 text-green-800',
      'annulee': 'bg-red-100 text-red-800'
    };
    return statusColors[status] || 'bg-gray-100 text-gray-800';
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleDateString('fr-FR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Fonctions de filtrage et recherche
  const filteredOrders = orders.filter(order => {
    const matchesSearch = searchTerm === '' || 
      order._id.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.articles?.some(article => 
        article.nom.toLowerCase().includes(searchTerm.toLowerCase())
      );
    
    const matchesStatus = statusFilter === 'all' || order.statut === statusFilter;
    
    return matchesSearch && matchesStatus;
  });

  const getStatusInfo = (status) => {
    const statusConfig = {
      'en_attente': { 
        label: 'En attente', 
        icon: <FaClock />, 
        className: 'status-pending' 
      },
      'confirmee': { 
        label: 'Confirm√©e', 
        icon: <FaCheck />, 
        className: 'status-confirmed' 
      },
      'en_preparation': { 
        label: 'En pr√©paration', 
        icon: <FaBox />, 
        className: 'status-preparing' 
      },
      'expediee': { 
        label: 'Exp√©di√©e', 
        icon: <FaTruck />, 
        className: 'status-shipped' 
      },
      'livree': { 
        label: 'Livr√©e', 
        icon: <FaCheck />, 
        className: 'status-delivered' 
      },
      'annulee': { 
        label: 'Annul√©e', 
        icon: <FaTimes />, 
        className: 'status-cancelled' 
      }
    };
    return statusConfig[status] || { 
      label: status, 
      icon: <FaClock />, 
      className: 'status-pending' 
    };
  };

  if (loading && !loadingTimeout) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            <div className="ml-4">
              <p className="text-gray-600">Chargement de vos commandes...</p>
              <p className="text-sm text-gray-500 mt-2">Cela peut prendre quelques secondes</p>
              <button
                onClick={() => {
                  console.log('üîÑ Forcer l\'arr√™t du chargement');
                  setLoadingTimeout(true);
                }}
                className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Arr√™ter le chargement
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Si timeout de chargement
  if (loadingTimeout && loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
            <div className="flex">
              <div className="ml-3 flex-1">
                <h3 className="text-sm font-medium text-yellow-800">Chargement lent d√©tect√©</h3>
                <div className="mt-2 text-sm text-yellow-700">
                  Le chargement prend plus de temps que pr√©vu. Cela peut √™tre d√ª √† :
                  <ul className="mt-2 list-disc list-inside">
                    <li>Probl√®me de connexion au serveur</li>
                    <li>Token d'authentification expir√©</li>
                    <li>Serveur surcharg√©</li>
                  </ul>
                </div>
                <div className="mt-4 space-x-3">
                  <button
                    onClick={() => {
                      setLoadingTimeout(false);
                      fetchOrders();
                    }}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-yellow-600 hover:bg-yellow-700"
                  >
                    üîÑ R√©essayer
                  </button>
                  <button
                    onClick={() => {
                      localStorage.removeItem('token');
                      window.location.href = '/login';
                    }}
                    className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                  >
                    üîë Se reconnecter
                  </button>
                  <button
                    onClick={() => {
                      console.log('üîç DIAGNOSTIC COMPLET:');
                      console.log('Token:', localStorage.getItem('token') ? 'Pr√©sent' : 'Absent');
                      console.log('User:', localStorage.getItem('user'));
                      console.log('Timestamp:', new Date().toISOString());
                      
                      // Test direct du serveur
                      fetch('http://localhost:5000/api/orders', {
                        headers: {
                          'x-auth-token': localStorage.getItem('token') || 'no-token'
                        }
                      })
                      .then(response => {
                        console.log('üìä Test serveur:', response.status, response.statusText);
                        return response.text();
                      })
                      .then(data => {
                        console.log('üìÑ R√©ponse serveur:', data.substring(0, 200));
                      })
                      .catch(error => {
                        console.log('‚ùå Erreur test serveur:', error.message);
                      });
                    }}
                    className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
                  >
                    üîç Diagnostic
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-red-50 border border-red-200 rounded-md p-4">
            <div className="flex">
              <FaTimes className="h-5 w-5 text-red-400" />
              <div className="ml-3 flex-1">
                <h3 className="text-sm font-medium text-red-800">Erreur</h3>
                <div className="mt-2 text-sm text-red-700">{error}</div>
                <div className="mt-4">
                  <button
                    onClick={() => {
                      console.log('üîÑ Rechargement forc√©...');
                      fetchOrders();
                    }}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-red-600 hover:bg-red-700"
                  >
                    üîÑ R√©essayer
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!loading && !error && (!orders || orders.length === 0)) {
    console.log('üîç Affichage de la page vide - orders:', orders, 'length:', orders?.length);
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-white rounded-lg shadow p-8 text-center">
            <FaBox className="mx-auto h-12 w-12 text-gray-400 mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Aucune commande</h3>
            <p className="text-gray-500 mb-6">Vous n'avez pas encore pass√© de commande</p>
            <button
              onClick={() => {
                console.log('üîÑ Rechargement manuel des commandes');
                fetchOrders();
              }}
              className="mr-4 inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
            >
              üîÑ Recharger
            </button>
            <Link
              to="/products"
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
            >
              D√©couvrir nos produits
            </Link>
          </div>
        </div>
      </div>
    );
  }

  console.log('üéØ RENDU PRINCIPAL - Affichage des commandes:', {
    ordersLength: orders?.length,
    loading,
    error,
    count: getOrdersCount()
  });

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* En-t√™te */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Mes Commandes</h1>
          <p className="mt-2 text-gray-600">
            Vous avez {getOrdersCount()} commande{getOrdersCount() > 1 ? 's' : ''}
          </p>
        </div>

        {/* Liste des commandes */}
        <div className="space-y-6">
          {orders.map((order) => (
            <div key={order._id} className="bg-white rounded-lg shadow-md overflow-hidden">
              {/* En-t√™te de la commande */}
              <div className="px-6 py-4 border-b border-gray-200">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-2">
                      {getStatusIcon(order.statut)}
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(order.statut)}`}>
                        {getStatusLabel(order.statut)}
                      </span>
                    </div>
                    <div className="text-sm text-gray-500">
                      Commande #{order._id?.slice(-8)}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-lg font-semibold text-gray-900">
                      {order.total}‚Ç¨
                    </div>
                    <div className="text-sm text-gray-500">
                      {formatDate(order.dateCreation)}
                    </div>
                  </div>
                </div>
              </div>

              {/* Corps de la commande */}
              <div className="px-6 py-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {/* Articles */}
                  <div>
                    <h4 className="text-sm font-medium text-gray-900 mb-3">Articles</h4>
                    <div className="space-y-2">
                      {order.articles?.slice(0, 3).map((article, index) => (
                        <div key={index} className="flex items-center justify-between text-sm">
                          <div className="flex items-center space-x-2">
                            {article.type === 'custom_hoodie' && (
                              <span className="text-green-500">üé®</span>
                            )}
                            <span className="text-gray-900">{article.nom}</span>
                            {article.quantite > 1 && (
                              <span className="text-gray-500">(x{article.quantite})</span>
                            )}
                          </div>
                          <span className="text-gray-600">{article.prixTotal}‚Ç¨</span>
                        </div>
                      ))}
                      {order.articles?.length > 3 && (
                        <div className="text-sm text-gray-500 italic">
                          +{order.articles.length - 3} autres articles
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Adresse de livraison */}
                  <div>
                    <h4 className="text-sm font-medium text-gray-900 mb-3">Adresse de livraison</h4>
                    <div className="text-sm text-gray-600">
                      <div>{order.adresseLivraison?.nom} {order.adresseLivraison?.prenom}</div>
                      <div>{order.adresseLivraison?.rue}</div>
                      <div>{order.adresseLivraison?.codePostal} {order.adresseLivraison?.ville}</div>
                      <div>{order.adresseLivraison?.pays}</div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Pied de la commande */}
              <div className="px-6 py-4 bg-gray-50 border-t border-gray-200">
                <div className="flex items-center justify-between">
                  <div className="text-sm text-gray-500">
                    M√©thode de paiement: <span className="font-medium">{order.methodePaiement}</span>
                  </div>
                  <button className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50">
                    <FaEye className="mr-2 h-4 w-4" />
                    Voir d√©tails
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orders;
