import { useState } from "react";
import { removeBackground, isApiKeyConfigured } from "../config/removeBg";

export default function HoodieCustomizer() {
  const [color, setColor] = useState("#ff0000"); // couleur par d√©faut (rouge)
  const [logo, setLogo] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [logoPosition, setLogoPosition] = useState("chest"); // position par d√©faut

  // Positions disponibles pour le logo
  const logoPositions = [
    { id: "chest", name: "Poitrine", icon: "üëï" },
    { id: "back", name: "Dos", icon: "üîô" },
    { id: "sleeve", name: "Manche", icon: "üëã" },
    { id: "hood", name: "Capuche", icon: "üé©" },
    { id: "pocket", name: "Poche", icon: "üëù" }
  ];

  // Styles de positionnement pour chaque emplacement
  const getLogoStyle = (position) => {
    const baseStyle = {
      position: "absolute",
      width: "80px",
      transform: "translate(-50%, -50%)",
      pointerEvents: "none",
      filter: "drop-shadow(0 2px 4px rgba(0,0,0,0.3))",
    };

    switch (position) {
      case "chest":
        return { ...baseStyle, top: "40%", left: "50%" };
      case "back":
        return { ...baseStyle, top: "35%", left: "50%" };
      case "sleeve":
        return { ...baseStyle, top: "60%", left: "25%", width: "60px" };
      case "hood":
        return { ...baseStyle, top: "15%", left: "50%", width: "70px" };
      case "pocket":
        return { ...baseStyle, top: "50%", left: "50%", width: "60px" };
      default:
        return { ...baseStyle, top: "40%", left: "50%" };
    }
  };

  // Upload du logo avec suppression d'arri√®re-plan
  const handleLogoUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setIsProcessing(true);
      
      try {
        if (isApiKeyConfigured()) {
          // Avec suppression d'arri√®re-plan
          const logoUrl = await removeBackground(file);
          setLogo(logoUrl);
        } else {
          // Sans suppression d'arri√®re-plan (fallback)
          const reader = new FileReader();
          reader.onload = (event) => {
            setLogo(event.target.result);
          };
          reader.readAsDataURL(file);
        }
      } catch (error) {
        console.error('Erreur lors du traitement du logo:', error);
        // Fallback: utiliser l'image originale
        const reader = new FileReader();
        reader.onload = (event) => {
          setLogo(event.target.result);
        };
        reader.readAsDataURL(file);
      } finally {
        setIsProcessing(false);
      }
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Personnalise ton hoodie</h2>

      <div style={{ position: "relative", display: "inline-block" }}>
        {/* Hoodie de base */}
        <img
          src="/hoodie-blanc.jpg"
          alt="Hoodie"
          style={{ maxWidth: "400px", display: "block" }}
        />

        {/* Calque couleur */}
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: color,
            mixBlendMode: color === "#000000" ? "overlay" : "multiply",
            opacity: color === "#000000" ? 0.8 : 1, // ajuste pour le noir
            pointerEvents: "none",
          }}
        ></div>

        {/* Logo */}
        {logo && (
          <img
            src={logo}
            alt="Logo"
            style={getLogoStyle(logoPosition)}
          />
        )}

        {/* Indicateur de traitement */}
        {isProcessing && (
          <div
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              transform: "translate(-50%, -50%)",
              background: "rgba(0,0,0,0.8)",
              color: "white",
              padding: "10px 20px",
              borderRadius: "5px",
              fontSize: "14px",
            }}
          >
            üîÑ Traitement du logo...
          </div>
        )}
      </div>

      {/* Contr√¥les */}
      <div style={{ marginTop: "20px" }}>
        <label>
          Choisir couleur :
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            style={{ marginLeft: "10px" }}
          />
        </label>

        <br /><br />

        <label>
          Ajouter logo :
          <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            style={{ marginLeft: "10px" }}
            disabled={isProcessing}
          />
        </label>

        {logo && (
          <div style={{ marginTop: "15px" }}>
            <label style={{ display: "block", marginBottom: "10px", fontWeight: "bold" }}>
              üìç Position du logo :
            </label>
            <div style={{ 
              display: "flex", 
              flexWrap: "wrap", 
              gap: "8px", 
              justifyContent: "center",
              maxWidth: "400px",
              margin: "0 auto"
            }}>
              {logoPositions.map((pos) => (
                <button
                  key={pos.id}
                  onClick={() => setLogoPosition(pos.id)}
                  style={{
                    padding: "8px 12px",
                    border: logoPosition === pos.id ? "2px solid #007bff" : "1px solid #ddd",
                    borderRadius: "20px",
                    background: logoPosition === pos.id ? "#e3f2fd" : "white",
                    cursor: "pointer",
                    fontSize: "12px",
                    display: "flex",
                    alignItems: "center",
                    gap: "5px",
                    transition: "all 0.2s ease",
                  }}
                >
                  <span>{pos.icon}</span>
                  <span>{pos.name}</span>
                </button>
              ))}
            </div>
          </div>
        )}
        
        {!isApiKeyConfigured() && (
          <div style={{ 
            marginTop: "10px", 
            padding: "10px", 
            background: "#fff3cd", 
            border: "1px solid #ffeaa7",
            borderRadius: "5px",
            fontSize: "12px",
            color: "#856404"
          }}>
            ‚ö†Ô∏è <strong>Conseil :</strong> Pour supprimer automatiquement l'arri√®re-plan, 
            utilisez des logos PNG transparents ou configurez l'API Remove.bg.
          </div>
        )}
        
        {logo && (
          <div style={{ marginTop: "10px" }}>
            <button
              onClick={() => setLogo(null)}
              style={{
                background: "#dc3545",
                color: "white",
                border: "none",
                padding: "5px 10px",
                borderRadius: "3px",
                cursor: "pointer",
                fontSize: "12px",
              }}
            >
              Supprimer le logo
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
