import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaPlus, FaEdit, FaTrash, FaEye, FaUsers, FaBox, FaShoppingCart, FaChartLine, FaCog } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({});
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);

  const { user } = useAuth();

  useEffect(() => {
    if (user && user.role === 'admin') {
      fetchDashboardData();
    }
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Charger les statistiques
      const statsResponse = await fetch('/api/admin/stats', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setStats(statsData);
      }

      // Charger les produits
      const productsResponse = await fetch('/api/products?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (productsResponse.ok) {
        const productsData = await productsResponse.json();
        setProducts(productsData.products);
      }

      // Charger les commandes récentes
      const ordersResponse = await fetch('/api/orders?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (ordersResponse.ok) {
        const ordersData = await ordersResponse.json();
        setOrders(ordersData.orders);
      }

      // Charger les utilisateurs récents
      const usersResponse = await fetch('/api/users?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });
      
      if (usersResponse.ok) {
        const usersData = await usersResponse.json();
        setUsers(usersData.users);
      }

    } catch (error) {
      toast.error('Erreur lors du chargement des données');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteProduct = async (productId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer ce produit ?')) {
      try {
        const response = await fetch(`/api/products/${productId}`, {
          method: 'DELETE',
          headers: {
            'x-auth-token': localStorage.getItem('token')
          }
        });

        if (response.ok) {
          toast.success('Produit supprimé avec succès');
          fetchDashboardData(); // Recharger les données
        } else {
          toast.error('Erreur lors de la suppression');
        }
      } catch (error) {
        toast.error('Erreur de connexion');
      }
    }
  };

  const handleUpdateOrderStatus = async (orderId, newStatus) => {
    try {
      const response = await fetch(`/api/orders/${orderId}/status`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ statut: newStatus })
      });

      if (response.ok) {
        toast.success('Statut de la commande mis à jour');
        fetchDashboardData(); // Recharger les données
      } else {
        toast.error('Erreur lors de la mise à jour');
      }
    } catch (error) {
      toast.error('Erreur de connexion');
    }
  };

  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-page">
        <div className="admin-container">
          <div className="access-denied">
            <h1>Accès refusé</h1>
            <p>Vous n'avez pas les permissions nécessaires pour accéder à cette page.</p>
            <Link to="/" className="btn-primary">
              Retour à l'accueil
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement du tableau de bord...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-container">
        <div className="admin-header">
          <h1>Tableau de bord administrateur</h1>
          <p>Gérez votre boutique en ligne</p>
        </div>

        {/* Navigation des onglets */}
        <div className="admin-tabs">
          <button
            className={`tab ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            <FaChartLine />
            Tableau de bord
          </button>
          <button
            className={`tab ${activeTab === 'products' ? 'active' : ''}`}
            onClick={() => setActiveTab('products')}
          >
            <FaBox />
            Produits
          </button>
          <button
            className={`tab ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            <FaShoppingCart />
            Commandes
          </button>
          <button
            className={`tab ${activeTab === 'users' ? 'active' : ''}`}
            onClick={() => setActiveTab('users')}
          >
            <FaUsers />
            Utilisateurs
          </button>
          <button
            className={`tab ${activeTab === 'settings' ? 'active' : ''}`}
            onClick={() => setActiveTab('settings')}
          >
            <FaCog />
            Paramètres
          </button>
        </div>

        {/* Contenu des onglets */}
        <div className="admin-content">
          {activeTab === 'dashboard' && (
            <DashboardTab 
              stats={stats} 
              products={products} 
              orders={orders} 
              users={users}
              onDeleteProduct={handleDeleteProduct}
              onUpdateOrderStatus={handleUpdateOrderStatus}
            />
          )}
          
          {activeTab === 'products' && (
            <ProductsTab 
              products={products}
              onDeleteProduct={handleDeleteProduct}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'orders' && (
            <OrdersTab 
              orders={orders}
              onUpdateOrderStatus={handleUpdateOrderStatus}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'users' && (
            <UsersTab 
              users={users}
              onRefresh={fetchDashboardData}
            />
          )}
          
          {activeTab === 'settings' && (
            <SettingsTab />
          )}
        </div>
      </div>
    </div>
  );
};

// Onglet Tableau de bord
const DashboardTab = ({ stats, products, orders, users, onDeleteProduct, onUpdateOrderStatus }) => {
  return (
    <div className="dashboard-tab">
      {/* Statistiques */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">
            <FaUsers />
          </div>
          <div className="stat-content">
            <h3>{stats.totalUsers || 0}</h3>
            <p>Utilisateurs</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <FaBox />
          </div>
          <div className="stat-content">
            <h3>{stats.totalProducts || 0}</h3>
            <p>Produits</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <FaShoppingCart />
          </div>
          <div className="stat-content">
            <h3>{stats.totalOrders || 0}</h3>
            <p>Commandes</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <FaChartLine />
          </div>
          <div className="stat-content">
            <h3>{stats.totalRevenue || 0}€</h3>
            <p>Chiffre d'affaires</p>
          </div>
        </div>
      </div>

      <div className="dashboard-sections">
        {/* Produits récents */}
        <div className="dashboard-section">
          <div className="section-header">
            <h3>Produits récents</h3>
            <Link to="/admin/products" className="btn-secondary">
              Voir tous
            </Link>
          </div>
          <div className="recent-items">
            {products.slice(0, 5).map(product => (
              <div key={product._id} className="recent-item">
                <img 
                  src={product.images[0] || '/placeholder-product.jpg'} 
                  alt={product.nom}
                  onError={(e) => {
                    e.target.src = '/placeholder-product.jpg';
                  }}
                />
                <div className="item-info">
                  <h4>{product.nom}</h4>
                  <p>{product.marque} - {product.prix}€</p>
                </div>
                <div className="item-actions">
                  <Link to={`/admin/products/${product._id}`} className="btn-icon">
                    <FaEdit />
                  </Link>
                  <button 
                    onClick={() => onDeleteProduct(product._id)}
                    className="btn-icon delete"
                  >
                    <FaTrash />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Commandes récentes */}
        <div className="dashboard-section">
          <div className="section-header">
            <h3>Commandes récentes</h3>
            <Link to="/admin/orders" className="btn-secondary">
              Voir toutes
            </Link>
          </div>
          <div className="recent-items">
            {orders.slice(0, 5).map(order => (
              <div key={order._id} className="recent-item">
                <div className="item-info">
                  <h4>Commande #{order._id.slice(-6)}</h4>
                  <p>{order.utilisateur?.nom} {order.utilisateur?.prenom}</p>
                  <p className="order-status">{order.statut}</p>
                </div>
                <div className="item-actions">
                  <select
                    value={order.statut}
                    onChange={(e) => onUpdateOrderStatus(order._id, e.target.value)}
                    className="status-select"
                  >
                    <option value="en_attente">En attente</option>
                    <option value="confirmee">Confirmée</option>
                    <option value="en_preparation">En préparation</option>
                    <option value="expediee">Expédiée</option>
                    <option value="livree">Livrée</option>
                    <option value="annulee">Annulée</option>
                  </select>
                  <Link to={`/admin/orders/${order._id}`} className="btn-icon">
                    <FaEye />
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Produits
const ProductsTab = ({ products, onDeleteProduct, onRefresh }) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  return (
    <div className="products-tab">
      <div className="tab-header">
        <h2>Gestion des produits</h2>
        <button 
          onClick={() => setShowAddForm(true)}
          className="btn-primary"
        >
          <FaPlus />
          Ajouter un produit
        </button>
      </div>

      <div className="products-table">
        <table>
          <thead>
            <tr>
              <th>Image</th>
              <th>Nom</th>
              <th>Marque</th>
              <th>Catégorie</th>
              <th>Prix</th>
              <th>Stock</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product._id}>
                <td>
                  <img 
                    src={product.images[0] || '/placeholder-product.jpg'} 
                    alt={product.nom}
                    className="product-thumbnail"
                    onError={(e) => {
                      e.target.src = '/placeholder-product.jpg';
                    }}
                  />
                </td>
                <td>{product.nom}</td>
                <td>{product.marque}</td>
                <td>{product.categorie}</td>
                <td>{product.prix}€</td>
                <td>
                  {Object.values(product.tailles || {}).reduce((sum, stock) => sum + stock, 0)}
                </td>
                <td>
                  <div className="action-buttons">
                    <button 
                      onClick={() => setEditingProduct(product)}
                      className="btn-icon edit"
                      title="Modifier"
                    >
                      <FaEdit />
                    </button>
                    <button 
                      onClick={() => onDeleteProduct(product._id)}
                      className="btn-icon delete"
                      title="Supprimer"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Formulaire d'ajout/modification */}
      {(showAddForm || editingProduct) && (
        <ProductForm
          product={editingProduct}
          onClose={() => {
            setShowAddForm(false);
            setEditingProduct(null);
          }}
          onSuccess={() => {
            setShowAddForm(false);
            setEditingProduct(null);
            onRefresh();
          }}
        />
      )}
    </div>
  );
};

// Onglet Commandes
const OrdersTab = ({ orders, onUpdateOrderStatus, onRefresh }) => {
  return (
    <div className="orders-tab">
      <div className="tab-header">
        <h2>Gestion des commandes</h2>
      </div>

      <div className="orders-table">
        <table>
          <thead>
            <tr>
              <th>N° Commande</th>
              <th>Client</th>
              <th>Date</th>
              <th>Total</th>
              <th>Statut</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map(order => (
              <tr key={order._id}>
                <td>#{order._id.slice(-6)}</td>
                <td>{order.utilisateur?.nom} {order.utilisateur?.prenom}</td>
                <td>{new Date(order.createdAt).toLocaleDateString('fr-FR')}</td>
                <td>{order.total}€</td>
                <td>
                  <select
                    value={order.statut}
                    onChange={(e) => onUpdateOrderStatus(order._id, e.target.value)}
                    className="status-select"
                  >
                    <option value="en_attente">En attente</option>
                    <option value="confirmee">Confirmée</option>
                    <option value="en_preparation">En préparation</option>
                    <option value="expediee">Expédiée</option>
                    <option value="livree">Livrée</option>
                    <option value="annulee">Annulée</option>
                  </select>
                </td>
                <td>
                  <Link to={`/admin/orders/${order._id}`} className="btn-icon">
                    <FaEye />
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Onglet Utilisateurs
const UsersTab = ({ users, onRefresh }) => {
  return (
    <div className="users-tab">
      <div className="tab-header">
        <h2>Gestion des utilisateurs</h2>
      </div>

      <div className="users-table">
        <table>
          <thead>
            <tr>
              <th>Nom</th>
              <th>Email</th>
              <th>Téléphone</th>
              <th>Rôle</th>
              <th>Date d'inscription</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr key={user._id}>
                <td>{user.nom} {user.prenom}</td>
                <td>{user.email}</td>
                <td>{user.telephone}</td>
                <td>
                  <span className={`role-badge ${user.role}`}>
                    {user.role === 'admin' ? 'Administrateur' : 'Client'}
                  </span>
                </td>
                <td>{new Date(user.createdAt).toLocaleDateString('fr-FR')}</td>
                <td>
                  <Link to={`/admin/users/${user._id}`} className="btn-icon">
                    <FaEye />
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Onglet Paramètres
const SettingsTab = () => {
  return (
    <div className="settings-tab">
      <div className="tab-header">
        <h2>Paramètres de la boutique</h2>
      </div>

      <div className="settings-content">
        <div className="setting-group">
          <h3>Informations générales</h3>
          <p>Configuration des informations de base de votre boutique</p>
          <button className="btn-secondary">Configurer</button>
        </div>

        <div className="setting-group">
          <h3>Livraison</h3>
          <p>Gestion des frais de livraison et zones de livraison</p>
          <button className="btn-secondary">Configurer</button>
        </div>

        <div className="setting-group">
          <h3>Paiement</h3>
          <p>Configuration des méthodes de paiement acceptées</p>
          <button className="btn-secondary">Configurer</button>
        </div>

        <div className="setting-group">
          <h3>Notifications</h3>
          <p>Gestion des emails et notifications automatiques</p>
          <button className="btn-secondary">Configurer</button>
        </div>
      </div>
    </div>
  );
};

// Formulaire de produit (simplifié)
const ProductForm = ({ product, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    nom: product?.nom || '',
    description: product?.description || '',
    prix: product?.prix || '',
    categorie: product?.categorie || '',
    marque: product?.marque || '',
    images: product?.images || [],
    tailles: product?.tailles || { XS: 0, S: 0, M: 0, L: 0, XL: 0, XXL: 0 },
    couleurs: product?.couleurs || []
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Logique de soumission simplifiée
    toast.success(product ? 'Produit modifié' : 'Produit ajouté');
    onSuccess();
  };

  return (
    <div className="product-form-overlay">
      <div className="product-form-modal">
        <div className="form-header">
          <h3>{product ? 'Modifier le produit' : 'Ajouter un produit'}</h3>
          <button onClick={onClose} className="close-btn">×</button>
        </div>

        <form onSubmit={handleSubmit} className="product-form">
          <div className="form-row">
            <div className="form-group">
              <label>Nom du produit</label>
              <input
                type="text"
                value={formData.nom}
                onChange={(e) => setFormData({...formData, nom: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Prix</label>
              <input
                type="number"
                step="0.01"
                value={formData.prix}
                onChange={(e) => setFormData({...formData, prix: e.target.value})}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
              rows="3"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Catégorie</label>
              <select
                value={formData.categorie}
                onChange={(e) => setFormData({...formData, categorie: e.target.value})}
                required
              >
                <option value="">Sélectionner une catégorie</option>
                <option value="vetements">Vêtements</option>
                <option value="chaussures">Chaussures</option>
                <option value="accessoires">Accessoires</option>
              </select>
            </div>
            <div className="form-group">
              <label>Marque</label>
              <input
                type="text"
                value={formData.marque}
                onChange={(e) => setFormData({...formData, marque: e.target.value})}
                required
              />
            </div>
          </div>

          <div className="form-actions">
            <button type="button" onClick={onClose} className="btn-secondary">
              Annuler
            </button>
            <button type="submit" className="btn-primary">
              {product ? 'Modifier' : 'Ajouter'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Admin;
