import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaPlus, FaEdit, FaTrash, FaEye, FaUsers, FaBox, FaShoppingCart, FaChartLine, FaCog, FaSignOutAlt } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { useAuth } from '../contexts/AuthContext';
import { formatBrand } from '../utils/formatUtils';
import '../styles/Admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({});
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [usersPagination, setUsersPagination] = useState({
    page: 1,
    limit: 20,
    total: 0,
    pages: 0
  });
  const [usersSearch, setUsersSearch] = useState('');
  const [usersRoleFilter, setUsersRoleFilter] = useState('');

  const { user, logout } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (user && user.role === 'admin') {
      fetchDashboardData();
    }
  }, [user]);

  const handleLogout = () => {
    if (window.confirm('ÃŠtes-vous sÃ»r de vouloir vous dÃ©connecter ?')) {
      logout();
      navigate('/');
      toast.success('DÃ©connexion rÃ©ussie');
    }
  };

  // Charger les utilisateurs quand on clique sur l'onglet Utilisateurs
  useEffect(() => {
    if (activeTab === 'users' && user && user.role === 'admin') {
      fetchUsers(1, 20, '', '');
    }
  }, [activeTab, user]);

  // Charger toutes les commandes quand on clique sur l'onglet Commandes
  useEffect(() => {
    if (activeTab === 'orders' && user && user.role === 'admin') {
      fetchAllOrders();
    }
  }, [activeTab, user]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      // Charger les statistiques
      const statsResponse = await fetch('/api/admin/stats', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (statsResponse.ok) {
        const statsData = await statsResponse.json();
        setStats(statsData);
      }

      // Charger les produits
      const productsResponse = await fetch('/api/products?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (productsResponse.ok) {
        const productsData = await productsResponse.json();
        setProducts(productsData.products || []);
      }

      // Charger les commandes rÃ©centes (toutes les commandes pour l'admin)
      const ordersResponse = await fetch('/api/orders/admin/toutes?limit=10', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (ordersResponse.ok) {
        const ordersData = await ordersResponse.json();
        setOrders(ordersData.commandes || ordersData.orders || []);
      }

      // Charger les utilisateurs rÃ©cents pour le dashboard
      await fetchUsers(1, 10);

    } catch (error) {
      toast.error('Erreur lors du chargement des donnÃ©es');
    } finally {
      setLoading(false);
    }
  };

  const fetchUsers = async (page = 1, limit = 20, search = '', role = '') => {
    try {
      const params = new URLSearchParams({
        page: page.toString(),
        limit: limit.toString()
      });

      if (search) params.append('recherche', search);
      if (role) params.append('role', role);

      const response = await fetch(`/api/users/admin/tous?${params}`, {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUsers(data.utilisateurs);
        setUsersPagination(data.pagination);
      } else {
        toast.error('Erreur lors du chargement des utilisateurs');
      }
    } catch (error) {
      toast.error('Erreur lors du chargement des utilisateurs');
    }
  };

  const fetchAllOrders = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/orders/admin/toutes', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (response.ok) {
        const data = await response.json();
        setOrders(data.commandes || data.orders || []);
      } else {
        toast.error('Erreur lors du chargement des commandes');
      }
    } catch (error) {
      toast.error('Erreur lors du chargement des commandes');
    } finally {
      setLoading(false);
    }
  };


  if (!user || user.role !== 'admin') {
    return (
      <div className="admin-page">
        <div className="admin-container">
          <div className="access-denied">
            <h1>AccÃ¨s refusÃ©</h1>
            <p>Vous n'avez pas les permissions nÃ©cessaires pour accÃ©der Ã  cette page.</p>
            <Link to="/" className="btn-primary">
              Retour Ã  l'accueil
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Chargement du tableau de bord...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-layout">
        {/* Sidebar */}
        <div className="admin-sidebar">
          <div className="sidebar-header">
            <h2>Administration</h2>
            <p>SHADOWHOOD</p>
          </div>

          <nav className="sidebar-nav">
            <button
              className={`sidebar-item ${activeTab === 'dashboard' ? 'active' : ''}`}
              onClick={() => setActiveTab('dashboard')}
            >
              <FaChartLine className="sidebar-icon" />
              <span>Tableau de bord</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'products' ? 'active' : ''}`}
              onClick={() => setActiveTab('products')}
            >
              <FaBox className="sidebar-icon" />
              <span>Produits</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'orders' ? 'active' : ''}`}
              onClick={() => setActiveTab('orders')}
            >
              <FaShoppingCart className="sidebar-icon" />
              <span>Commandes</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'users' ? 'active' : ''}`}
              onClick={() => setActiveTab('users')}
            >
              <FaUsers className="sidebar-icon" />
              <span>Utilisateurs</span>
            </button>

            <button
              className={`sidebar-item ${activeTab === 'settings' ? 'active' : ''}`}
              onClick={() => setActiveTab('settings')}
            >
              <FaCog className="sidebar-icon" />
              <span>ParamÃ¨tres</span>
            </button>
          </nav>

          {/* Bouton de dÃ©connexion */}
          <div className="sidebar-footer">
            <button
              className="sidebar-item logout-btn"
              onClick={handleLogout}
            >
              <FaSignOutAlt className="sidebar-icon" />
              <span>DÃ©connexion</span>
            </button>
          </div>
        </div>

        {/* Contenu principal */}
        <div className="admin-main">
          <div className="admin-content">
          {activeTab === 'dashboard' && (
            <DashboardTab stats={stats} />
          )}

          {activeTab === 'products' && (
            <ProductsTab 
              products={products} 
              onProductAdded={(newProduct) => {
                setProducts(prev => [...(prev || []), newProduct]);
              }}
            />
          )}

          {activeTab === 'orders' && (
            <OrdersTab orders={orders} />
          )}

          {activeTab === 'users' && (
            <UsersTab 
              users={users}
              pagination={usersPagination}
            />
          )}

          {activeTab === 'settings' && (
            <SettingsTab />
          )}
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Tableau de bord
const DashboardTab = ({ stats }) => {
  return (
    <div className="dashboard-tab">
      <div className="simple-stats">
        <h2>Statistiques de la boutique</h2>

        <div className="stats-grid">
          <div className="stat-card">
            <FaUsers className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalUsers || 0}</h3>
              <p>Utilisateurs</p>
            </div>
          </div>

          <div className="stat-card">
            <FaBox className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalProducts || 0}</h3>
              <p>Produits</p>
            </div>
          </div>

          <div className="stat-card">
            <FaShoppingCart className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.totalOrders || 0}</h3>
              <p>Commandes</p>
            </div>
          </div>

          <div className="stat-card">
            <FaChartLine className="stat-icon" />
            <div className="stat-content">
              <h3>{stats.chiffreAffaires || 0}â‚¬</h3>
              <p>Chiffre d'affaires</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Onglet Produits
const ProductsTab = ({ products, onProductAdded }) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [loading, setLoading] = useState(false);

  return (
    <div className="products-tab">
      <div className="tab-header">
        <h2>Gestion des produits</h2>
        <div className="header-actions">
          <p>Total: {products?.length || 0} produits</p>
          <button 
            className="btn-primary"
            onClick={() => setShowAddForm(true)}
          >
            <FaPlus />
            Ajouter un produit
          </button>
        </div>
      </div>

      {showAddForm && (
        <AddProductForm 
          onClose={() => setShowAddForm(false)}
          onProductAdded={(newProduct) => {
            onProductAdded(newProduct);
            setShowAddForm(false);
          }}
        />
      )}

      <div className="products-grid">
        {(products || []).map(product => {
          const totalStock = product.tailles ? 
            product.tailles.reduce((total, taille) => total + (taille.stock || 0), 0) 
            : 0;
          const mainImage = product.images && product.images.length > 0 ? product.images[0].url : null;

          return (
            <div key={product._id} className="product-card">
              <div className="product-image">
                {mainImage ? (
                  <img 
                    src={mainImage} 
                    alt={product.images[0].alt || product.nom}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                ) : null}
                <div className="no-image" style={{ display: mainImage ? 'none' : 'flex' }}>
                  <FaBox />
                  <span>Aucune image</span>
                </div>
                <div className="product-badges">
                  {product.estNouveau && <span className="badge new">Nouveau</span>}
                  {product.estEnPromotion && <span className="badge promo">Promo</span>}
                  {product.estPopulaire && <span className="badge popular">Populaire</span>}
                </div>
              </div>

              <div className="product-info">
                <h3 className="product-name">{product.nom}</h3>
                <p className="product-brand">{formatBrand(product.marque)}</p>
                <p className="product-category">{product.categorie}</p>

                <div className="product-pricing">
                  <span className="current-price">{product.prix}â‚¬</span>
                  {product.prixReduit && product.prixReduit < product.prix && (
                    <span className="original-price">{product.prixReduit}â‚¬</span>
                  )}
                </div>

                <div className="product-stock">
                  <span className={`stock-indicator ${totalStock > 0 ? 'in-stock' : 'out-of-stock'}`}>
