// Composant formulaire d'ajout de produit
const AddProductForm = ({ onClose, onProductAdded }) => {
  const [formData, setFormData] = useState({
    nom: '',
    description: '',
    prix: '',
    prixReduit: '',
    categorie: '',
    sousCategorie: '',
    marque: '',
    materiau: '',
    entretien: '',
    estEnPromotion: false,
    estNouveau: false,
    estPopulaire: false,
    tags: []
  });

  const [tailles, setTailles] = useState([
    { nom: 'XS', stock: 0 },
    { nom: 'S', stock: 0 },
    { nom: 'M', stock: 0 },
    { nom: 'L', stock: 0 },
    { nom: 'XL', stock: 0 },
    { nom: 'XXL', stock: 0 }
  ]);

  const [couleurs, setCouleurs] = useState([
    { nom: 'Noir', code: '#000000' },
    { nom: 'Blanc', code: '#FFFFFF' },
    { nom: 'Rouge', code: '#FF0000' },
    { nom: 'Bleu', code: '#0000FF' },
    { nom: 'Vert', code: '#00FF00' }
  ]);

  const [images, setImages] = useState([]);
  const [imageFiles, setImageFiles] = useState([]);
  const [uploadingImages, setUploadingImages] = useState(false);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const categories = [
    'hauts', 'bas', 'robes', 'vestes', 'chaussures', 'accessoires'
  ];

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    // Effacer l'erreur du champ modifiÃ©
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleTailleChange = (index, field, value) => {
    const newTailles = [...tailles];
    newTailles[index] = { ...newTailles[index], [field]: value };
    setTailles(newTailles);
  };

  const handleCouleurChange = (index, field, value) => {
    const newCouleurs = [...couleurs];
    newCouleurs[index] = { ...newCouleurs[index], [field]: value };
    setCouleurs(newCouleurs);
  };

  const addCouleur = () => {
    setCouleurs([...couleurs, { nom: '', code: '#000000' }]);
  };

  const removeCouleur = (index) => {
    setCouleurs(couleurs.filter((_, i) => i !== index));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => {
      const isValidType = file.type.startsWith('image/');
      const isValidSize = file.size <= 5 * 1024 * 1024; // 5MB max
      return isValidType && isValidSize;
    });

    if (validFiles.length !== files.length) {
      setErrors(prev => ({ 
        ...prev, 
        images: 'Seules les images de moins de 5MB sont acceptÃ©es' 
      }));
    }

    setImageFiles(prev => [...prev, ...validFiles]);

    // CrÃ©er des URLs de prÃ©visualisation
    validFiles.forEach(file => {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImages(prev => [...prev, {
          file,
          preview: e.target.result,
          name: file.name
        }]);
      };
      reader.readAsDataURL(file);
    });
  };

  const removeImage = (index) => {
    setImages(prev => prev.filter((_, i) => i !== index));
    setImageFiles(prev => prev.filter((_, i) => i !== index));
  };

  const uploadImages = async () => {
    if (imageFiles.length === 0) return [];

    setUploadingImages(true);
    const uploadedImages = [];

    try {
      // Upload toutes les images en une seule fois
      const formData = new FormData();
      imageFiles.forEach(file => {
        formData.append('images', file);
      });

      const response = await fetch('/api/upload/product-images', {
        method: 'POST',
        headers: {
          'x-auth-token': localStorage.getItem('token')
        },
        body: formData
      });

      if (response.ok) {
        const result = await response.json();
        uploadedImages.push(...result.images.map(img => img.url));
      } else {
        const error = await response.json();
        throw new Error(error.message || 'Erreur lors du chargement des images');
      }
    } catch (error) {
      console.error('Erreur upload images:', error);
      setErrors(prev => ({ 
        ...prev, 
        images: error.message || 'Erreur lors du chargement des images' 
      }));
    } finally {
      setUploadingImages(false);
    }

    return uploadedImages;
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.nom.trim()) newErrors.nom = 'Le nom est requis';
    if (!formData.description.trim()) newErrors.description = 'La description est requise';
    if (!formData.prix || formData.prix <= 0) newErrors.prix = 'Le prix doit Ãªtre supÃ©rieur Ã  0';
    if (!formData.categorie) newErrors.categorie = 'La catÃ©gorie est requise';
    if (!formData.marque.trim()) newErrors.marque = 'La marque est requise';

    // VÃ©rifier qu'au moins une taille a du stock
    const hasStock = tailles.some(taille => taille.stock > 0);
    if (!hasStock) newErrors.stock = 'Au moins une taille doit avoir du stock';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      // Upload des images d'abord
      const uploadedImages = await uploadImages();

      const productData = {
        ...formData,
        prix: parseFloat(formData.prix),
        prixReduit: formData.prixReduit ? parseFloat(formData.prixReduit) : null,
        tailles: tailles.filter(taille => taille.stock > 0), // Seulement les tailles avec stock
        couleurs: couleurs.filter(couleur => couleur.nom.trim() !== ''), // Seulement les couleurs nommÃ©es
        images: uploadedImages.map(url => ({
          url: url,
          alt: formData.nom || 'Image produit'
        })) // Convertir les URLs en objets avec url et alt
      };

      const response = await fetch('/api/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify(productData)
      });

      if (response.ok) {
        const newProduct = await response.json();
        onProductAdded(newProduct);
        // Reset form
        setFormData({
          nom: '',
          description: '',
          prix: '',
          prixReduit: '',
          categorie: '',
          sousCategorie: '',
          marque: '',
          materiau: '',
          entretien: '',
          estEnPromotion: false,
          estNouveau: false,
          estPopulaire: false,
          tags: []
        });
        setTailles(tailles.map(t => ({ ...t, stock: 0 })));
        setCouleurs([
          { nom: 'Noir', code: '#000000' },
          { nom: 'Blanc', code: '#FFFFFF' },
          { nom: 'Rouge', code: '#FF0000' },
          { nom: 'Bleu', code: '#0000FF' },
          { nom: 'Vert', code: '#00FF00' }
        ]);
        setImages([]);
        setImageFiles([]);
      } else {
        const error = await response.json();
        setErrors({ submit: error.message || 'Erreur lors de l\'ajout du produit' });
      }
    } catch (error) {
      setErrors({ submit: 'Erreur de connexion' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-form">
      <div className="form-header">
        <h3>Ajouter un nouveau produit</h3>
        <button onClick={onClose} className="close-btn">Ã—</button>
      </div>

      <form onSubmit={handleSubmit} className="product-form">
        <div className="form-section">
          <h4>Informations gÃ©nÃ©rales</h4>

          <div className="form-row">
            <div className="form-group">
              <label>Nom du produit *</label>
              <input
                type="text"
                name="nom"
                value={formData.nom}
                onChange={handleChange}
                className={errors.nom ? 'error' : ''}
                placeholder="Ex: T-shirt Premium"
              />
              {errors.nom && <span className="error-message">{errors.nom}</span>}
            </div>

            <div className="form-group">
              <label>Marque *</label>
              <input
                type="text"
                name="marque"
                value={formData.marque}
                onChange={handleChange}
                className={errors.marque ? 'error' : ''}
                placeholder="Ex: Nike, Adidas..."
              />
              {errors.marque && <span className="error-message">{errors.marque}</span>}
            </div>
          </div>

          <div className="form-group">
            <label>Description *</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className={errors.description ? 'error' : ''}
              placeholder="Description dÃ©taillÃ©e du produit..."
              rows="3"
            />
            {errors.description && <span className="error-message">{errors.description}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Prix (â‚¬) *</label>
              <input
                type="number"
                name="prix"
                value={formData.prix}
                onChange={handleChange}
                className={errors.prix ? 'error' : ''}
                placeholder="29.99"
                step="0.01"
                min="0"
              />
              {errors.prix && <span className="error-message">{errors.prix}</span>}
            </div>

            <div className="form-group">
              <label>Prix rÃ©duit (â‚¬)</label>
              <input
                type="number"
                name="prixReduit"
                value={formData.prixReduit}
                onChange={handleChange}
                placeholder="19.99"
                step="0.01"
                min="0"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>CatÃ©gorie *</label>
              <select
                name="categorie"
